float linstep(float a, float b, float x) { return clamp((x-a)/(b-a),0.,1.); }
float smin(float a, float b, float k) { return mix(a,b,linstep(-2.*k,2.*k,a-b))-k*linstep(-2.*k,0.,-abs(a+b)); }
float smax(float a, float b, float r) { return -smin(-a,-b,r); }

float X11=0.;

float distX2(vec2 p)
{
    float W=.8;
    float H=1.;
    float w1=.15;
    float w2=.04;
    float w3=.03;
    float sr=.007;
    float dX11=.015;

    float d=10000.;

    float d1,d2,dh,dThick;
    dh=abs(p.y)-H*.5;

    vec2 Xn;

    Xn=normalize(vec2(H+.05-X11*.03,W+X11*.03));
    float d10=dot(p,Xn);
    d1=abs(d10)-w1*.5;
    d1=smax(d1,dh,sr);

    Xn=normalize(vec2(-(H-.05+X11*.03),W-X11*.03));
    float d20=dot(p,Xn);
    d2=abs(d20+X11*(w2+dX11)*.5*sign(d10))-w2*.5;
    d2=smax(d2,dh,sr);

    d=smin(d1,d2,sr);
    d=smax(d,-d1-w3-X11*w1,sr);
    d=smax(d,-abs(d20)+(dX11*(-1.5+2.*X11)),sr);
    return d;
}

float D=.15;
float distX(vec3 p)
{
    float sr=.007;
    float d=distX2(p.xy);
    float dThick=abs(p.z)-D*.5;
    d=smax(d,dThick,sr);
    return d;
}

float distTw2(vec2 p) {
    float d1,d2,d=1000.;
    d1=length(p)-1.;
    d=min(d,d1);
    d1=length(p-vec2(-.5,-.04))-.8;
    d=max(d,-d1);
    d=max(d,p.y);
    d1=length(p-vec2(.647,.06))-.36;
    d=min(d,d1);

    float dw=1000.;
    d1=length(p-vec2(.01,-.31))-.38;
    d2=length(p-vec2(-.28,-.08))-.366;
    d1=max(d1,-d2);
    dw=min(dw,d1);

    d1=length(p-vec2(-.11,-.08))-.367;
    d2=length(p-vec2(-.3,.235))-.366;
    d1=max(d1,-d2);
    dw=min(dw,d1);

    d1=length(p-vec2(-.105,.17))-.367;
    d2=length(p-vec2(.365,.955))-1.;
    dw=min(dw,d1);
    dw=min(dw,length(p-vec2(.3,0))-.3);
    dw=max(dw,-d2);

    float ds=1000.;
    d1=length(p-vec2(.66,.6))-.53;
    d2=length(p-vec2(.73,1.07))-.8;
    d1=max(d1,-d2);
    d1=max(d1,-(p.x-.85));
    ds=min(ds,d1);
    d1=length(p-vec2(.5,.7))-.82;
    d2=length(p-vec2(.9,1.))-.81;
    d1=max(d1,-d2);
    d1=max(d1,-(p.x-.85));
    ds=min(ds,d1);

    d=min(d,dw);
    d=min(d,ds);

    return d;
}

float distTwX(vec3 p)
{
    p.xy+=vec2(.3,-.3);
    float d=distTw2(p.xy);
    d=smax(d,abs(p.z)-.15,.02);
    if(iTime>2.0) d=smax(d,-distX2((p.xy-vec2(.63,.17))*7.)/7.,.007);
    if(iTime>2.6) d=smax(d,-distX2((p.xy-vec2(.80,.17))*7.)/7.,.007);
    float dz=length((p.xy-vec2(1.02,.2))*vec2(1.8,.65)*.8)-.1;
    dz=smax(dz,dot(p.xy-vec2(1.02,.2),vec2(-.2,.9)),.017);
    dz=smax(dz,-(abs(dot(p.xy-vec2(1.02,.2),vec2(1,0)))+.005-(p.y-.2)*.1),.02);
    float dpy=(p.y/.2-1.);
    dz=smax(dz,abs(p.z+D-.01-dpy*.15-.15*dpy*dpy)-.02,.01);
    if(iTime>3.2) d=min(d,dz);
    return d;
}

float distFloor(vec3 p)
{
    return p.z+.5;
}

float dist(vec3 p)
{
    float d=10000.;
    d=min(d,distTwX(p.xzy/.7)*.7);
    d=min(d,distFloor(p));
    return d;
}

vec3 getGrad(vec3 p, float eps) 
{ 
    vec2 d=vec2(eps*.5,0); 
    return vec3(dist(p+d.xyy)-dist(p-d.xyy),dist(p+d.yxy)-dist(p-d.yxy),dist(p+d.yyx)-dist(p-d.yyx))/eps; 
}

float DOF_Apert=.0;
float DOF_FocalD=5.3;

#define PIH 1.57079632679
#define ROTM(ang) mat2(cos(ang-vec2(0,PIH)),-sin(ang-vec2(0,PIH)))

#define RandTex iChannel1

void getTrafo(inout vec3 pos, inout vec3 dir, vec2 fc)
{
    vec2 sc=(fc-iResolution.xy*.5)/iResolution.x*2.;
    dir=normalize(vec3(0,0,-4.)+vec3(sc,0));
    pos=vec3(0,0,5.5);
    pos+=sin(iTime*100.*vec3(1,1.3,.7))*.1*step(2.0,iTime)*exp(-(iTime-2.0)/.07);
    pos+=sin(iTime*100.*vec3(1.3,.7,1))*.1*step(2.6,iTime)*exp(-(iTime-2.6)/.07);
    pos+=sin(iTime*100.*vec3(.7,1,1.3))*.1*step(3.2,iTime)*exp(-(iTime-3.2)/.07);
    pos+=vec3(cos(iTime*2.*vec2(.7,1))*.01,0);
    pos+=vec3(cos(iTime*3.*vec2(1,.7))*.005,0);
    vec4 r=(texelFetch(RandTex,(ivec2(fc)+iFrame*ivec2(13,61))%256,0)-.5);
    pos.xy+=r.xy*DOF_Apert;
    dir.xy-=r.xy*DOF_Apert/DOF_FocalD;
    float ph = iMouse.x/iResolution.x*10.+.45+cos(iTime*1.)*.05;
    float th = iMouse.y/iResolution.y*10.+1.25;
    pos.yz=ROTM(th)*pos.yz;
    dir.yz=ROTM(th)*dir.yz;
    pos.xy=ROTM(ph)*pos.xy;
    dir.xy=ROTM(ph)*dir.xy;
}

#define PIH 1.57079632679

void march(inout vec3 p, vec3 dir)
{
    for(int i=0;i<80;i++) {float d=dist(p); p+=dir*d; if(d<.001) break; }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 pos,dir;
    getTrafo(pos,dir,fragCoord);

    march(pos,dir);
    vec3 n=normalize(getGrad(pos,.001));
    float ao=1.; float sc=.01; for(int i=0;i<16;i++) { ao*=.88+.12*clamp(dist(pos+n*sc)/sc,0.,1.);  sc*=1.5; if(sc>1.) break; }
    vec3 R=reflect(dir,n);
    float fres=1.-abs(dot(dir,n));
    fres*=fres*fres*fres;
    fres=fres*.8+.2;

    float isFloor=0.; if (dist(pos)==distFloor(pos)) isFloor=1.;    

    fragColor.xyz=vec3(1);
    fragColor.xyz=n*.1+.9;
    fragColor.xyz*=mix(vec3(.2,.7,1),vec3(1),isFloor);
    fragColor.xyz=mix(fragColor.xyz,texture(iChannel0,R).xyz,fres);
    fragColor.xyz*=ao;
    fragColor.w=1.;
}

int roundn(int v, int n)
{
    return int(round(float(v) / pow(10.0f, float(n))));
}

vec3 human_shader(const ivec2 coord)
{
    int x = coord.x;
    int y = coord.y;

    // A
    int u = x - 36;
    int v = 18 - y;
    int h = u * u + v * v;

    int c_r = 0;
    int c_b = 0;

    if (h < 200)
    {
        // B - sphere
        c_r = 420;
        c_b = 520;
        int t = 5000 + 8 * h;
        int p = roundn(t * u, 2);
        int q = roundn(t * v, 2);
        int s = 2 * q;
        int w = roundn(1000 + p - s, 2) + 8;

        if (w > 0)
        {
            c_r = c_r + w * w;
        }

        int o = s + 2200;
        c_r = roundn(c_r * o, 4);
        c_b = roundn(c_b * o, 4);

        if (p > -q)
        {
            w = roundn(p + q, 1);
            c_r = c_r + w;
            c_b = c_b + w;
        }
    }
    else if (v < 0)
    {
        // C - ground
        c_r = 150 + 2 * v;
        c_b = 50;
        int p = h + 8 * v * v;
        int c = 240 * (-v) - p;

        if (c > 1200)
        {
            int o = roundn(6 * c, 1);
            o = c * (1500 - o);
            o = roundn(o, 2) - 8360;
            c_r = roundn(c_r * o, 3);
            c_b = roundn(c_b * o, 3);
        }

        int r = c + u * v;
        int d = 3200 - h - 2 * r;

        if (d > 0)
        {
            c_r = c_r + d;
        }
    }
    else
    {
        // D - sky
        int c = x + 4 * y;
        c_r = 132 + c;
        c_b = 192 + c;
    }

    if (c_r > 255)
    {
        c_r = 255;
    }

    if (c_b > 255)
    {
        c_b = 255;
    }

    int c_g = roundn(7 * c_r + 3 * c_b, 1);

    return vec3(c_r, c_g, c_b) / 255.0f;
}

float saturate(float v)
{
    return clamp(v, 0.0f, 1.0f);
