void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec2 gridSize = vec2(71, 40);
    const vec2 highlightCoord = vec2(49, 20);

    // normalize
    vec2 uv = fragCoord / iResolution.xy;

    // flip y
    uv.y = 1.0 - uv.y;

    // correct aspect
    vec2 ratio = vec2(iResolution) / gridSize;
    vec2 aspectCorrection = ratio / min(ratio.x, ratio.y);
    uv *= aspectCorrection;

    vec2 coord = floor(uv * gridSize);
    vec3 humanShaderCol = human_shader(ivec2(coord));

    vec2 grid = 3.0f * (1.0f - 2.0f * abs(fract(uv * vec2(gridSize)) - 0.5f));
    vec3 gridCol = vec3(min(grid.x, grid.y));

    float highlight = max(1.0f - length(highlightCoord - coord), 0.0f);
    float pulse = max(sin(iTime * 4.0f), 0.0f);
    vec3 highlightCol = vec3(highlight) * pulse;

    vec3 col = highlightCol + min(gridCol, humanShaderCol);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// CC0: Just a simple gradient
//  Created while listening to a twitch stream

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  float aa = 4.0/RESOLUTION.y;
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  const mat2 rot = ROT(1.0*PI/4.0);
  p*=rot;

  vec3 col0 = (1.0+cos(vec3(0.0, 1.0, 2.0)+1.0*(p.y*p.y*p.x-p.x)-0.25*TIME))*0.5;
  vec3 col1 = sqrt(0.5)*col0*col0;

  float n = mod1(p.y, 1.0/128.0);
  float t = smoothstep(0.0, -aa, p.y);
  if (mod(n, 2.0) == 0.0) {
    t = 1.0-t;
  }
  vec3 col  = mix(col0, col1, t);
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

vec2 Rotate(vec2 uv, float angle)
{
    float rad = angle * (3.141594 / 180.0);
    mat2 rot = mat2(cos(rad), sin(rad), -sin(rad), cos(rad));

    return uv * rot;
}

vec3 palletes(float t, vec3 a, vec3 b, vec3 c, vec3 d)
{
    return a + b * cos(6.28316 * (c * t + d));
}

float angles[4] = float[4](50., 120., 240., 0.);
float zooms[4] = float[4](0.85f, 1.2f, 0.6, 1.0f);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    vec3 palls[4];

    palls[0] = palletes(uv.x * uv.y + iTime * 0.2, vec3(0.424,0.784,0.855), 
    vec3(0.098,0.416,0.525), vec3(1.0, 1.0, 1.0),vec3(0.173,0.745,0.886));
    palls[1] = palletes(uv.x * uv.y + iTime * 0.7, vec3(0.938, 0.328, 0.718),
    vec3(0.659, 0.483, 0.328), vec3(0.388, 0.388, 0.296), vec3(2.538, 2.478, 0.168));
    palls[2] = palls[0];
    palls[3] = palls[1];

    float time = iTime * 0.5;
    int i = int(time) % 4;
    int i2 = (i + 1) % 4;

    float t = smoothstep(0.0, 0.3, fract(time));
    float offset = mix(angles[i], angles[i2], t);
    float zoomM = mix(zooms[i], zooms[i2], t);
    uv *= zoomM;

    uv = Rotate(uv, iTime * 5. + offset);
    vec3 tex = vec3(texture(iChannel0, uv).rgb);

    vec3 pal = mix(palls[i], palls[(i + 1) % 4], t);

    vec3 color = tex.r * pal; // symbol
    color += tex.g * abs(uv.x) * vec3(0.110,0.110,0.110); // borders
    color += tex.b * pal * 2.; // moving borders

    fragColor = vec4(vec3(color), 1.0);
}

