#define gridScale 4.0
#define pi 3.14159265359

float N21(vec2 uv)
{
    return fract(sin(uv.x * 18.289 + uv.y * 112.282) * 5102.32);
}

float sphereSDF(vec2 uv)
{
    return length(uv) - 0.5;
}

float boxSDF(vec2 uv, vec2 b) // Thank you Inigo Quilez 
{
    vec2 d = abs(uv)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 gID = floor(uv * gridScale);

    float speed = 0.9;
    float t = iTime * speed;
    int row = int(floor(t)) % int(gridScale);
    float pos = (cos(t * pi) + 1.0) * 0.5;

    float r = N21(vec2(floor(t), 0.0));
    float dir = step(0.5, r);
    float isMoving = 0.0;

    if(dir == 1.0)
    {
        if(int(gID.y) == row)
        {
            uv.x += pos;
            isMoving = 1.0;
        }
    }
    else
    {
        if(int(gID.x) == row)
        {
            uv.y += pos;
            isMoving = 1.0;
        }
    }

    vec2 lUV = fract(uv * gridScale) * 2.0 - 1.0;
    float dist = abs(sphereSDF(lUV));
    dist = smoothstep(0.0, 0.325, dist);
    dist = 0.13 / dist;

    float distBox = boxSDF(lUV, vec2(0.8));
    distBox = step(0.125, distBox);
    float glow = isMoving * distBox * ((cos(t * pi * 2.0 - pi) + 1.0) * 0.5);

    fragColor = vec4(vec3(dist, distBox, glow) ,1.0);
}

