float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 143758.5453);
}

float hermite(float t)
{
  return t * t * (3.0 - 2.0 * t);
}

float noise(vec2 co, float frequency)
{
  vec2 v = vec2(co.x * frequency, co.y * frequency);

  float ix1 = floor(v.x);
  float iy1 = floor(v.y);
  float ix2 = floor(v.x + 1.0);
  float iy2 = floor(v.y + 1.0);

  float fx = hermite(fract(v.x));
  float fy = hermite(fract(v.y));

  float fade1 = mix(rand(vec2(ix1, iy1)), rand(vec2(ix2, iy1)), fx);
  float fade2 = mix(rand(vec2(ix1, iy2)), rand(vec2(ix2, iy2)), fx);

  return mix(fade1, fade2, fy);
}

float pnoise(vec2 co, float freq, int steps, float persistence)
{
  float value = 0.0;
  float ampl = 1.0;
  float sum = 0.0;
  for(int i=0 ; i<steps ; i++)
  {
    sum += ampl;
    value += noise(co, freq) * ampl;
    freq *= 2.0;
    ampl *= persistence;
  }
  return value / sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 v2 = vec2(25.*(sin(iTime/4.)*1.23-cos((uv.y*1.0+sin(iTime/5.))*9.)/3.)/56.,
                   uv.y*0.7+sin(-iTime/2.17)*0.12*(cos(uv.x*55.)*2.+5.)/5.);

    float c = abs(pnoise(v2, 0.6, 6, 13.8)-0.5)*2.5;

    vec3 color=vec3(c,c,c/1.2);

    color += (-0.3 + 0.5*cos(iTime/2.5+uv.xyx+vec3(0,2,4))) / 10.;

    fragColor = vec4(color, 1.);
}

