            if ((st & ga) > 0) curr.y += cstep*mulbase*gp;
            if ((st & ba) > 0) curr.z += cstep*mulbase*bp;

            if (staypatt == 5 || staypatt == 6) curr.z=curr.y=curr.x;	

        } else {
            float stayt = cstep * stayval;

            if (staypatt == 0 || staypatt > 5) {
                curr.x -= cstep*rm;
                curr.y -= cstep*gm;
                curr.z -= cstep*bm;
            }
            else if (staypatt == 1) {
                if(curr.x > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                    curr.z -= cstep*bm;
                }
            }
            else if (staypatt == 2) {
                if(curr.y > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                    curr.z -= cstep*bm;
                }
            }
            else if (staypatt == 3) {
                if(curr.z > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                    curr.z -= cstep*bm;
                }
            }
            else if (staypatt == 4) {
                if(curr.z > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                }
                if(curr.x > cstep) curr.z -= cstep*bm;
            }
            else if (staypatt == 5) {
                curr.x -= cstep*rm;
                curr.z=curr.y=curr.x;
            }
        }
    }

	fragColor = vec4( clamp(curr.xyz,0.,1.), ff );
}

