const float MAX_STEPS = 80.0;
const float MIN_DIST = 0.01;

const float AMPLITUDE = 0.13;
const float OCTAVES = 5.0;
const float LACUNARITY = 1.8;
const float GAIN = 0.5;

struct RayResult {
    vec3 ray;
    float d;
    float steps;
    bool hit;
    vec3 p;
    vec3 color;
};

float fbm(in vec2 q) {
    float value = 0.0;
    float amplitude = AMPLITUDE;
    for (float i = 0.0; i < OCTAVES; i++) {
        float n = 0.7 * cos(0.5 * q.x + 0.5 * q.y + 0.5 * iTime) +
                  0.7 * cos(0.5 * q.x - 0.5 * q.y + 0.5 * iTime) +
                  0.7 * cos(q.x + 3.0 * iTime) +
                  1.0 * cos(q.y + 5.0 * iTime);
        n += sign(n) * pow(0.9 * n, 1.1);
        value += amplitude * n;
        q *= LACUNARITY;
        amplitude *= GAIN;
    }
    float base = 0.2 * cos(0.05 * q.y + iTime);
    base += sign(base) * pow(0.9 * base, 1.1);
    return value + base;
}


float scene(in vec3 p) {
    float y = p.y + fbm(p.xz);
    float d = y;
 	return d;
}

vec3 sceneNormals(in vec3 p) {
    const float h = 0.005;
    const vec2 k = vec2(1.0, -1.0);
    return normalize(k.xyy * scene(p + k.xyy * h) +
                     k.yyx * scene(p + k.yyx * h) +
                     k.yxy * scene(p + k.yxy * h) +
                     k.xxx * scene(p + k.xxx * h));
}

void shade(inout RayResult r) {
    const vec3 SKY_COLOR = vec3(0.3, 0.65, 0.8);
    const vec3 SUN = normalize(vec3(0.0, 9.0, 1.0));
    const vec3 FAKE_SUN = normalize(vec3(0.0, 2.0, 1.0));
    const float FOG_DIST = 70.0;
    const vec3 WATER_COLOR = vec3(0.08, 0.3, 0.7);

    vec3 sky = SKY_COLOR + pow(dot(r.ray, FAKE_SUN), 5.0);
    if (!r.hit) {
        r.color = sky;
        return;
    }

    vec3 normal = sceneNormals(r.p);
    float diffuse = max(dot(SUN, normal), 0.0);
    float specular = pow(max(dot(SUN, normal), 0.0), 99.0);

    vec3 color = diffuse * WATER_COLOR + specular * vec3(1.0);

    float fog = min(r.d / FOG_DIST, 1.0);
    color = mix(color, sky, fog);

    r.color = color;
}

RayResult march(in vec3 origin, in vec3 ray) {
    RayResult r;
    r.ray = ray;
    r.d = 0.0;
    for (r.steps = 0.0; r.steps < MAX_STEPS; r.steps++) {
     	r.p = origin + ray * r.d;
        float sd = scene(r.p);
        if (sd < MIN_DIST) {
            r.hit = true;
            break;
        }
        r.d += sd;
    }
    shade(r);
    return r;
}

vec3 gamma(in vec3 color) {
    return pow(max(color, 0.0), vec3(1.0 / 2.2));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec3 ray = normalize(vec3(uv, 1.0));
    vec3 origin = vec3(0.0, 4.0, -1.0);

    RayResult r = march(origin, ray);

    vec3 color = r.color;
    color = gamma(color);
	fragColor = vec4(color, 1.0);
}

