// value for key to reset with
const int Key_R = 32;

// imploding rectangles
const int stayrules[9] = int[9](0,0,1,1,0,0,0,0,1);
const int bornrules[9] = int[9](0,0,1,0,0,0,1,1,0);
const float liveval = 0.01;

float hash1( float n )
{
    return fract(sin(n)*138.5453123);
}

