vec2 screen_to_uv(vec2 coord)
{
    return (2.0 * coord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

vec3 view_transform(vec3 col)
{
	// OETF (Gamma)
    return pow(col, vec3(1.0 / 2.2));
}

vec3 render(vec2 coord)
{
    // UV
    vec2 uv = screen_to_uv(coord);

    // Primaries and white point
    vec2 r = vec2(0.7, -0.5);
    vec2 g = vec2(0.0, 0.5);
    vec2 b = vec2(-0.7, -0.5);
    vec2 w = vec2(0.0, -0.16667);

    // Animate the white point
    float t = TAU * iTime * 0.5;
    w += vec2(cos(t), sin(t)) * cos(0.2 * t) * 0.2;

    // Drag the white point
    if (iMouse.z > 0.5)
    {
        w = screen_to_uv(iMouse.xy);
    }

    // Stuff
    vec3 col = vec3(0.0);
    bool outside;
    vec3 bary;

    // Check if UV is inside
    bary = cart2barycentric(uv, r, g, b, false, outside);
    if (!outside)
    {
        // Check if white point is inside
        bary = cart2barycentric(w, r, g, b, false, outside);
        if (outside)
        {
            col = vec3(1.0, 0.05, 0.03);
        }
        else
        {
            // Get interpolated color
            bary = cart2barycentric(uv, r, w, b, false, outside);
            if (!outside)
            {
                col = bary.x * vec3(1, 0, 0)
                    + bary.y * vec3(1.0 / 3.0)
                    + bary.z * vec3(0, 0, 1);
            }
            bary = cart2barycentric(uv, g, w, r, false, outside);
            if (!outside)
            {
                col = bary.x * vec3(0, 1, 0)
                    + bary.y * vec3(1.0 / 3.0)
                    + bary.z * vec3(1, 0, 0);
            }
            bary = cart2barycentric(uv, b, w, g, false, outside);
            if (!outside)
            {
                col = bary.x * vec3(0, 0, 1)
                    + bary.y * vec3(1.0 / 3.0)
                    + bary.z * vec3(0, 1, 0);
            }
            col = max(col, 0.0);
            float mono = max(max(col.r, col.g), col.b);
            if (mono > 0.0) col /= mono;
        }
    }

    // Draw dots on the points
    if (inside_circle(uv, r, 0.03)) col = vec3(0.5);
    if (inside_circle(uv, g, 0.03)) col = vec3(0.5);
    if (inside_circle(uv, b, 0.03)) col = vec3(0.5);
    if (inside_circle(uv, w, 0.01)) col = vec3(0.5);

    return col;
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // Initialize PRNG
    prng_init(vec3(frag_coord / iResolution.y, iTime));

    // Jitter sampling
    const int jitter_samples = 32;
    vec3 col = vec3(0.0);
    for (int i = 0; i < jitter_samples; i++)
    {
        col += render(frag_coord + vec2(random(), random()) - 0.5);
    }
    col /= float(jitter_samples);

    // Output
    col = view_transform(col);
    frag_col = vec4(col, 1.0);
}

