#define PI 3.1415926535897932384626433832
#define TAU 6.283185307179586476925286766

// Basically |a| * |b| * sin(theta)
float cross2d(vec2 a, vec2 b)
{
    return a.x * b.y - a.y * b.x;
}

vec2 barycentric2cart(vec3 b, vec2 v0, vec2 v1, vec2 v2)
{
    return b.x * v0 + b.y * v1 + b.z * v2;
}

// https://www.desmos.com/calculator/8g8xjejuox
vec3 cart2barycentric(vec2 p, vec2 v0, vec2 v1, vec2 v2)
{
    vec3 b = vec3(
        cross2d(v1 - p, v2 - p),
        cross2d(v2 - p, v0 - p),
        cross2d(v0 - p, v1 - p)
    ) / cross2d(v1 - v0, v2 - v0);
    return b;
}

vec3 cart2barycentric(vec2 p, vec2 v0, vec2 v1, vec2 v2, bool clamped, out bool outside)
{
    vec3 b = vec3(
        cross2d(v1 - p, v2 - p),
        cross2d(v2 - p, v0 - p),
        cross2d(v0 - p, v1 - p)
    ) / cross2d(v1 - v0, v2 - v0);
    outside = min(min(b.x, b.y), b.z) < -0.001;
    if (clamped)
    {
        b = max(b, 0.0);
        b /= (b.x + b.y + b.z);
    }
    return b;
}

bool inside_circle(vec2 p, vec2 center, float radius)
{
    return distance(p, center) < radius;
}



// -----------------------------------------------
// Pseudo-Random Number Generator
// Source: https://www.shadertoy.com/view/WdSSRt
// -----------------------------------------------

/* Usage Example:
    void mainImage(out vec4 frag_col, in vec2 frag_coord)
    {
        // Initialize PRNG
        prng_init(vec3(frag_coord / iResolution.y, iTime));

        // Using the function
        float a = random();
        vec3 b = vec3(random(), random(), random());

        ...
    }
*/

uint prng_state[2];

uint prng_rot(uint x, int k)
{
    return (x << k) | (x >> (32 - k));
}

float random()
{
    uint s0 = prng_state[0];
    uint s1 = prng_state[1];
    uint result = prng_rot(s0 * 0x9e3779bbu, 5) * 5u;
    s1 ^= s0;
    prng_state[0] = prng_rot(s0, 26) ^ s1 ^ (s1 << 9);
    prng_state[1] = prng_rot(s1, 13);
    return float(result) / float(0xffffffffu);
}

void prng_init(vec3 seed)
{
    seed.xy *= (seed.z + 10.258);
    seed += 3.49276101561702;
    prng_state[0] = floatBitsToUint(seed.x);
    prng_state[1] = floatBitsToUint(seed.y);
    random();
}

