float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv += vec2(noise(uv + vec2(0.2, 0.4)* iTime), noise(uv.yx + vec2(0.7, 0.1)* iTime))*2.2f;
    if (fract(uv.x) > 0.2) {

        // Time varying pixel color
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

        // Output to screen
        fragColor = vec4(col,1.0);
    } else {

        // Time varying pixel color
        vec3 col = 0.5 + 0.5*cos(iTime+uv.yyx+vec3(1,2,3));

        // Output to screen
        fragColor = vec4(col,1.0);
    }
}

///UV
vec2 CenterUV(vec2 fragCoord)
{
    ///Centering Operation
	vec2 uv = vec2( fragCoord.xy - 0.5*iResolution.xy );
	// [0, iResolution.x] -> [-0.5*iResolution.x, 0.5*iResolution.x]
	// [0, iResolution.y] -> [-0.5*iResolution.y, 0.5*iResolution.y]
    return uv;
}
vec2 AspectRatioUV(vec2 fragCoord)
{	
    vec2 uv = CenterUV(fragCoord.xy);
	uv = 2.0 * uv.xy / iResolution.y;
    // AspectRatio = 1920/1080 = 1.778
    // [-iResolution.x/2, iResolution.x/2] ->[-1.778, 1.778]
	// [-iResolution.y/2, iResolution.y/2] ->[-1, 1]
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV manipulation
    vec2 uv = AspectRatioUV(fragCoord.xy);

    vec2 center = iResolution.xy /2.0;
    vec2 mousePos = iMouse.xy;
    vec2 toHandle = normalize(mousePos - center);

    float value = atan(toHandle.x, -toHandle.y);
    value /= PI;
    value = 1. - value;

    //value = atan(normalize(mousePos - center));
    float AA = 0.05;
    //Visu
    vec3 pix = vec3(uv,0);
    pix = vec3(1.0);

    float angle = iTime * 90.0;
    angle = value * PI;

    //BackGround
    float circleMask = Disk(uv,vec2(0.), 0.85, AA);
    pix = mix(vec3(0.85, 0.83, 0.93), pix, circleMask);

    //Handle

    vec2 pos = vec2(sin(angle),cos(angle)) * 0.75;
    float BgMask = Disk(uv,pos, 0.45, AA);
    pix = mix(vec3(0.48, 0.65, 0.96), pix, BgMask);
    BgMask = Disk(uv,pos, 0.25, AA);
    pix = mix(vec3(0.38, 0.4, 0.97), pix, BgMask);


    pos = vec2(sin(angle),cos(angle)) * 0.75;
    circleMask = Disk(uv,pos, 0.09, AA);
    pix = mix(vec3(1.0), pix, circleMask);
    circleMask = Disk(uv,pos, 0.07, AA);
    pix = mix(vec3(0.45, 0.25, 0.92), pix, circleMask);


    circleMask = Disk(uv,vec2(0.), 0.65, AA);
    pix = mix(vec3(0.7, 0.62, 0.93), pix, circleMask);

    //Pie
    vec2 rotUv = RotateUV(uv, -angle/2.);
    float pieMask = Pie(rotUv, vec2(0), angle/2., 0.65, AA);
    pix = mix(vec3(0.45, 0.25, 0.92), pix, pieMask);  


    for(int i = 0; i < 24; i++)
    {
        float val = TAU/24.; 
        pos = vec2(sin(val * float(i)),cos(val * float(i))) * 0.5;

        circleMask = Disk(uv, pos, 0.02, AA);
        pix = mix(vec3(0.56, 0.44, 0.83),pix, circleMask);
    } 

    //BackGround
    circleMask = Disk(uv,vec2(0.), 0.85, AA);
    pix = mix(vec3(1.), pix, (1.-circleMask));

	fragColor = vec4(pix, 1.0);
}

