void n(vec2 u, inout vec4 O)
{
    vec4 v = T(u), a; // get neighbor value
    for (int x = -1; x <= 1; ++x)
    for (int y = -1; y <= 1; ++y)
    {
        a = T(v.yz + vec2(x, y)); // search near neighbor's root
        a.x += distance(u, v.yz + vec2(x, y)); // new prospective distance
        O = (a.x <= O.x) ? vec4(a.x, v.yz, 0) : O; // when we find a shorter path, update our distance and root
    }
}

void mainImage( out vec4 O, vec2 u )
{
    vec3 R = iResolution, m = iMouse.xyz, e = vec3(1, 0, distance(m.xy, u));

    if (max(u.x, u.y) < 1.)
        O.x = R.x * R.y; // resolution stored in lower left corner, unreachable by sim
    else if (iFrame == 0 || // bootstrap
        T(vec2(0)).x != R.x * R.y || // detect resolution changes
        texelFetch(iChannel3,ivec2(32,0),0).x > 0. || // support keyboard "space" reset
        u.x < 1. || u.x == R.x - .5 || // detect edges
        u.y < 1. || u.y == R.y - .5)

        O = vec4(1e6); // clear edges and screen
    else
    {
        // support drawing with mouse
        O = m.z > 0. && e.z < R.y * .005 ? vec4(max(0., e.z - R.y * .005), u, 0) : T(u);

        // search neighbors for new shorter path
        n(u + e.xy, O);
        n(u - e.xy, O);
        n(u + e.yx, O);
        n(u - e.yx, O);
    }
}

