// X - X11 - new twitter logo

#define X11 clamp(-cos(iTime*.3)+.5,0.,1.)

float linstep(float a, float b, float x) { return clamp((x-a)/(b-a),0.,1.); }
float smin(float a, float b, float k) { return mix(a,b,linstep(-2.*k,2.*k,a-b))-k*linstep(-2.*k,0.,-abs(a+b)); }
float smax(float a, float b, float k) { return -smin(-a,-b,k); }

float distX(vec3 p)
{
    float W=.8;
    float D=.15;
    float H=1.;
    float w1=.15;
    float w2=.04;
    float w3=.03;
    float sr=.007;
    float dX11=.015;

    float d=10000.;

    float d1,d2,dh,dThick;
    dh=abs(p.y)-H*.5;
    dThick=abs(p.z)-D*.5;

    vec3 Xn;

    Xn=normalize(vec3(H+.05-X11*.03,W+X11*.03,0));
    float d10=dot(p,Xn);
    d1=abs(d10)-w1*.5;
    d1=smax(d1,dh,sr);

    Xn=normalize(vec3(-(H-.05+X11*.03),W-X11*.03,0));
    float d20=dot(p,Xn);
    d2=abs(d20+X11*(w2+dX11)*.5*sign(d10))-w2*.5;
    d2=smax(d2,dh,sr);

    d=smin(d1,d2,sr);
    d=smax(d,-d1-w3-X11*w1,sr);
    d=smax(d,dThick,sr);
    d=smax(d,-abs(d20)+(dX11*(-1.5+2.*X11)),sr);
    return d;
}


float dist(vec3 pos)
{
    return min(distX(pos.xzy*vec3(1,1,1)*.75)/.75,pos.z+.7);
}

vec3 getGrad(vec3 pos, float eps)
{
    vec2 d=vec2(eps,0);
    float d0=dist(pos);
    return vec3(dist(pos+d.xyy)-d0,
                dist(pos+d.yxy)-d0,
                dist(pos+d.yyx)-d0)/eps;

}

// march it...
vec4 march(inout vec3 pos, vec3 dir)
{
    float eps=0.001;
    for(int cnt=0;cnt<100;cnt++)
    {
        float d = dist(pos);
        pos+=d*dir;
        if(d<eps) { break; }
    }
    vec3 n = getGrad(pos,.001);
    return vec4(n,0.); // .w=1 => background
}

mat3 rotX(float ang)
{
    float c=cos(ang), s=sin(ang);
    return mat3(1,0,0, 0,c,s, 0,-s,c);
}

mat3 rotZ(float ang)
{
    float c=cos(ang), s=sin(ang);
    return mat3(c,s,0, -s,c,0, 0,0,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // screen coord -1..1
    vec2 sc = (fragCoord.xy/iResolution.xy)*2.-1.;
    // viewer position
    vec3 pos = vec3(0,-3.5,0);
    // pixel view direction
    vec3 dir = normalize(2.*normalize(-pos)+vec3(sc.x,0,sc.y*iResolution.y/iResolution.x));
    // rotate view around x,z
    float phi = iMouse.x/iResolution.x*7.;
    float th  = iMouse.y/iResolution.y*7.;
    if (iMouse.x==0.) { phi=sin(iTime*.5)*.5; th=.3*sin(iTime*.3)-.15; }
    mat3 rx = rotX(th);
    mat3 rz = rotZ(phi);
    pos = rz*(rx*pos);
    dir = rz*(rx*dir);

    // march it...
   	vec4 n=march(pos,dir);
    float bg=n.w;

    // calc some ambient occlusion
    float ao=1.;
    #if 0
    // calc simple ao by stepping along radius
    ao*=dist(pos*1.02)/.02;
    ao*=dist(pos*1.05)/.05;
    ao*=dist(pos*1.1)/.1;
    #else
    // calc ao by stepping along normal
    ao*=dist(pos+n.xyz*.02)/.02;
    ao*=dist(pos+n.xyz*.05)/.05;
    ao*=dist(pos+n.xyz*.10)/.10;
    #endif
    // adjust contrast of ao
    ao=pow(ao,.4);

    // reflection dir
    vec3 R = pos-2.0*dot(pos,n.xyz)*n.xyz;
    R = -((R*rz)*rx).yzx;

    vec3 c = vec3(1);
    // simply add some parts of the normal to the color
    // gives impression of 3 lights from different dir with different color temperature
    c += n.xyz*.1+.1;

    //  reflection of cubemap
    c *= texture(iChannel1,R).xyz*1.5+.4;

    // add some depth darkening
	c*=clamp(-dot(dir,pos)*.7+.7, .2, 1.);

    // apply ambient occlusion
    c*=ao;

    // apply background
    if(bg>=.5) c=vec3(.5,.6,.75)-.17;

    // vignetting
    float vign = (1.1-.3*length(sc.xy));

	fragColor = vec4(c*vign,1);
}

