    text_pos(iResolution.x/2. - (13./2.)*CHAR_SPACING.x, iResolution.y - STRHEIGHT(1.0)*2.);
    col += _p() + _r() + _e() + _s() + _s() + _spc() + _d() + _o() + _w() + _n() + _lsl() + _u() + _p();

