void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color0 = texture(iChannel0, uv);
    vec4 color1 = texture(iChannel1, uv);

    fragColor = ACESFilm(color0 + color1);
}

