#ifndef __COMMON_BASIC_GLSL__
#define __COMMON_BASIC_GLSL__

#define S(x, y, z) smoothstep(x, y, z)
#define A(v1,v2,t1,t2) mix(v1,v2,S(t1,t2,iTime))

const float PI = 3.14159;
const float HALF_PI = PI * 0.5;
const float TAU = PI * 2.0;
const float DEG2RAD = TAU/360.0;

float invLerp(float a, float b, float x) {
    x = clamp(x, a, b);
    return (x - a) / (b - a);
}

mat3 lookAtMatrix(in vec3 lookAtDirection) {
	vec3 ww = normalize(lookAtDirection);
    vec3 uu = cross(ww, vec3(0.0, 1.0, 0.0));
    vec3 vv = cross(uu, ww);
    return mat3(uu, vv, -ww);
}

struct Camera {
    vec3 position;
	vec3 direction;
};

Camera createOrbitCamera(vec2 uv, vec2 mouse, vec2 resolution, float fov, vec3 target, float height, float distanceToTarget, float iTime)
{
    vec2 r = mouse / resolution * vec2(3.0 * PI, 0.5 * PI);
    float halfFov = fov * 0.5;
    float zoom = cos(halfFov) / sin(halfFov);

    vec3 position = target + vec3(sin(r.x), 0.0, cos(r.x)) * distanceToTarget + vec3(0, height, 0);
    vec3 direction = normalize(vec3(uv, -zoom));
    direction = lookAtMatrix(target - position) * direction;

    return Camera(position, direction);
}

mat2 rot2d(float a) {
    float ca = cos(a), sa = sin(a);
    return mat2(ca, sa, -sa, ca);
}

float rand(vec2 co) {
    return fract(sin(dot(co.xy,vec2(12.9898,78.233))) * 100000.5453);
}

float hash12(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * .1031);
	p3 += dot(p3, p3.yzx + 33.33);
	return fract((p3.x + p3.y) * p3.z);
}

#endif // __COMMON_BASIC_GLSL__
#ifndef __COMMON_COLORS_GLSL__
#define __COMMON_COLORS_GLSL__

vec4 linearTosRGB(vec4 linearRGB)
{
    bvec4 cutoff = lessThan(linearRGB, vec4(0.0031308));
    vec4 higher = vec4(1.055)*pow(linearRGB, vec4(1.0/2.4)) - vec4(0.055);
    vec4 lower = linearRGB * vec4(12.92);

    return mix(higher, lower, cutoff);
}

vec4 sRGBToLinear(vec4 sRGB)
{
    bvec4 cutoff = lessThan(sRGB, vec4(0.04045));
    vec4 higher = pow((sRGB + vec4(0.055))/vec4(1.055), vec4(2.4));
    vec4 lower = sRGB/vec4(12.92);

    return mix(higher, lower, cutoff);
}

vec4 ACESFilm(vec4 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e), vec4(0), vec4(1));
}

#endif // __COMMON_COLORS_GLSL__
#ifndef __COMMON_DIST_FUNCTIONS_GLSL__
#define __COMMON_DIST_FUNCTIONS_GLSL__

float sdPlane(vec3 p, float height) {
    return p.y - height;
}

float sdSphere(vec3 p, float s) {
  return length(p)-s;
}

float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdPrism(vec3 p, float tall, vec2 topSize, vec2 botSize) {
    vec3 top = vec3(topSize.x, tall, topSize.y);
    vec3 bot = vec3(botSize.x, tall, botSize.y);

    float q = (p.y+tall)*.5/tall;
    vec3 shape = mix(bot, top, q);

    return sdBox(p, shape);
}

float sdLink( vec3 p, float le, float r1, float r2 ) {
  vec3 q = vec3( p.x, max(abs(p.y)-le,0.0), p.z );
  return length(vec2(length(q.xy)-r1,q.z)) - r2;
}

#endif // __COMMON_DIST_FUNCTIONS_GLSL__

// Repeat around the origin by a fixed angle.
// For easier use, num of repetitions is use to specify the angle.
float pModPolar(inout vec2 p, float repetitions) {
	float angle = 2.0*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.0;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.0)) c = abs(c);
	return c;
}

struct Hit {
    int id;
    float dist; // means distance from ray to hit (<= surfHit usually)
};

struct TraceResult {
    Hit hit;
    float dist; // means distance from ro to closest point to hit
    vec3 ro;
    vec3 rd;
};

float smin(float a, float b, float k) {
	float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}
float smax(float a, float b, float k) {
	float h = max(k-abs(a-b),0.0);
	return max(a, b) + h*h*0.25/k;
}

Hit hmin(Hit a, Hit b) { if (a.dist < b.dist) return a; return b; }
Hit hmax(Hit a, Hit b) { if (a.dist < b.dist) return b; return a; }
Hit hsmin(in Hit a, in Hit b, in float k) {
    Hit h = hmin(a, b);
    h.dist = smin(a.dist, b.dist, k);
    return h;
}
Hit hsmax(in Hit a, in Hit b, in float k) {
    Hit h = hmax(a, b);
    h.dist = smin(a.dist, b.dist, k);
    return h;
}
Hit hnegate(Hit a) { return Hit(a.id, -a.dist); }
Hit opUnion(Hit a, Hit b) { return hmin(a, b); }
Hit opSubtraction(Hit a, Hit b) { return hmax(hnegate(a), b); }
Hit opIntersection(Hit a, Hit b) { return hmax(a, b); }

struct DirectionalLight {
    vec3 direction;
    vec3 color;
};

struct Material {
    vec3 albedo;
    float metallic;
    float roughness;
    vec3 emissive;
    float ao;
};

struct Surface {
    int materialId;
    float dist;
    vec3 position;
    vec3 normal;
    float ao;
    vec3 view;
};

// -----------------------------------------------------------------------------
// PBR Implementation
// - Lambert or Burley diffuse
// - Schlick Fresnel
// - GGX NDF
// - Smith-GGX height-correlated visibility function

// Sources
//
// https://learnopengl.com/PBR/Lighting
// https://google.github.io/filament/Filament.html
// https://github.com/KhronosGroup/glTF/tree/master/specification/2.0#appendix-b-brdf-implementation

vec3 F_Schlick_full(float HoV, vec3 f0, vec3 f90) {
    return f0 + (f90 - f0) * pow(1.0 - HoV, 5.0);
}

vec3 F_Schlick(float HoV, vec3 f0) {
    return F_Schlick_full(HoV, f0, vec3(1.0));
}

float Diff_Lambert() {
    return 1.0 / PI;
}

vec3 Diff_Burley(float NoV, float NoL, float LoH, float roughness) {
    float f90 = 0.5 + 2.0 * roughness * LoH * LoH;
    vec3 lightScatter = F_Schlick_full(NoL, vec3(1.0), vec3(f90));
    vec3 viewScatter = F_Schlick_full(NoV, vec3(1.0), vec3(f90));
    return lightScatter * viewScatter * (1.0 / PI);
}

float D_GGX(float NoH, float a) {
    float a2 = a * a;
    float f = (NoH * a2 - NoH) * NoH + 1.0;
    return a2 / (PI * f * f);
}

float V_SmithGGXCorrelated(float NoV, float NoL, float a) {
    float a2 = a * a;
    float NoV2 = NoV*NoV;
    float NoL2 = NoL*NoL;
    float GGL = NoL * sqrt(NoV2 * (1.0 - a2) + a2);
    float GGV = NoV * sqrt(NoL2 * (1.0 - a2) + a2);
	return 0.5 / (GGL + GGV);
}

float GGX_Smith_Approx_Visibility(float NoV, float NoL, float a) {
    return 1.0 / (2.0 * mix(2.0*NoL*NoV, NoL+NoV, a));
}


vec3 BRDF(DirectionalLight l, Surface surf, Material mat, float shadow) {
    vec3 V = -surf.view;
    vec3 N = surf.normal;
    vec3 L = l.direction;
    vec3 H = normalize(V + L);

    float NoV = max(dot(N, V), 0.0);
    float NoL = max(dot(N, L), 0.0);
    float NoH = max(dot(N, H), 0.0);
    float HoV = max(dot(H, V), 0.0);

    vec3  albedo     = mat.albedo;
    float roughness  = mat.roughness;
    float a          = roughness * roughness;
    float metallic   = mat.metallic;
    float dielectric = 1.0 - metallic;

    // Constants
    vec3 dielectricSpecular = vec3(0.04);
    vec3 black = vec3(0);

    // Frenel term
    vec3 F0 = mix(dielectricSpecular, albedo, metallic);
    vec3 F  = F_Schlick(HoV, F0);

    // Normal distribution
    float D = D_GGX(NoH, a);

    // Visibility term
    //     should be equivalent to G / (4.0 * NoL * NoV)
    //     but it doesn't look the same as https://www.shadertoy.com/view/tdKXR3
    float Vis = V_SmithGGXCorrelated(NoV, NoL, a);

    // Specular BRDF Cook Torrance
    vec3 specular = F * (Vis * D);

    // Lambert Diffuse
    //     Should we scale by (1.0 - F) ?? gltf and learnopengl have it but filament doesn't
    //     Also what about lambert 1/PI ?? https://seblagarde.wordpress.com/2012/01/08/pi-or-not-to-pi-in-game-lighting-equation/
    //     PI might not be used in IBL only?
    vec3 kD = vec3(1.0) - F;
    vec3 c = mix(albedo * (1.0-dielectricSpecular), black, metallic);
    vec3 diffuse = kD * (c / PI);
    // vec3 diffuse = (1.0 - F) * diffuseColor * Diff_Burley(NoV, NoL, NoH, a);

    // Final Color
    vec3 emissive = mat.emissive;
    vec3 directLight = l.color * NoL * (diffuse + specular);

    //return vec3(Vis)*NoL;
    return emissive + directLight * shadow;
}

