// ***********************************************************************
// *************************** SCENE *************************************
// ***********************************************************************


float straigthChain(vec3 p, float l) {
    const float le = 0.15, r1 = 0.2, r2 = 0.07;
    vec3 a = p;
    vec3 b = p;

    if (p.y > -.5 && p.y < l) {
        a.y = fract(a.y)-0.5;
        b.y = fract(b.y+.5)-0.5;
    }

    return min(
        sdLink(a.xyz, le, r1, r2),
        sdLink(b.zyx, le, r1, r2)
    );
}

float circularChain(vec3 p, float radius, float repetitions) {\
    const float le = 0.15, r1 = 0.2, r2 = 0.065;
    float arc = 360.0 / repetitions * DEG2RAD;

    vec3 q = p;
    pModPolar(q.xz, repetitions);
    q -= vec3(radius, 0, 0);
    q.yz = rot2d(90.0 * DEG2RAD) * q.yz;

    float link1 = sdLink(q, le, r1, r2);

    vec3 q2 = p;
    q2.xz = rot2d(arc * 0.5) * q2.xz;
    pModPolar(q2.xz, repetitions);
    q2 -= vec3(radius, 0, 0);
    // alternate link rotation
    q2.xz = rot2d(90.0 * DEG2RAD) * q2.xz;
    q2.xy = rot2d(90.0 * DEG2RAD) * q2.xy;
    float link2 = sdLink(q2, le, r1, r2);

    return min(link1, link2);
}

void r2d(inout vec2 axis, float angle) {
    axis = rot2d(angle) * axis;
}

Hit map(vec3 p) {
    //Hit floorPlane = Hit(0, sdPlane(p, -20.5));
    Hit ball = Hit(1, sdSphere(p - vec3(0,1.0,0), 0.75));

    float linkRadius = 2.0;
    float linkRepetitions = 12.0;
    float selfRotationSpeed = 1.5;

    vec3 q = p;
    q -= vec3(0, 1, 0);

    vec3 q1 = q;
    r2d(q1.yz, iTime + 1.*TAU/3.);
    r2d(q1.xz, iTime * selfRotationSpeed * 2.0);
    Hit chainLink = Hit(2, circularChain(q1, linkRadius, 12.0));

    vec3 q2 = q;
    r2d(q2.xy, iTime + 2.*TAU/3.);
    r2d(q2.xz, iTime * selfRotationSpeed * 1.5);
    chainLink = hmin(chainLink, Hit(2, circularChain(q2, linkRadius * 1.4, 16.0)));

    vec3 q3 = q;
    r2d(q3.yz, iTime + TAU);
    r2d(q3.xz, iTime * selfRotationSpeed * 1.0);
    chainLink = hmin(chainLink, Hit(2, circularChain(q3, linkRadius * 1.7, 21.0)));

    //Hit result = hmin(floorPlane, chainLink);
    Hit result = chainLink;
    result = hmin(result, ball);
    return result;
}

// ***********************************************************************
// *************************** END SCENE *********************************
// ***********************************************************************

vec3 mapNormal(vec3 p, float surfHit) {
    vec2 e = vec2(surfHit, 0);
    float d = map(p).dist;

    return normalize(vec3(
        d - map(p - e.xyy).dist,
        d - map(p - e.yxy).dist,
        d - map(p - e.yyx).dist
    ));
}

float calcAO( in vec3 pos, in vec3 nor ) {
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ ) {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).dist;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

// Fast raymarching
TraceResult trace(vec3 ro, vec3 rd, float surfHit, float maxDistance, int maxSteps) {
    float d = 0.0;
    for (int i = 0; i < maxSteps && d <= maxDistance; i++) {
        vec3 p = ro + rd * d;
        Hit h = map(p);
        if (h.dist <= surfHit)
            return TraceResult(h, d, ro, rd);
        d += h.dist;
    }
    return TraceResult(Hit(-1, maxDistance), d, ro, rd);
}

// Raymarching
// Regular sphere tracing but returning the closest hit
TraceResult traceClosest(vec3 ro, vec3 rd, float surfHit, float maxDistance, int maxSteps) {
    float d = 0.0;
    Hit closest = Hit(-1, maxDistance);
    for (int i=0; i < maxSteps && d <= maxDistance; i++){
        vec3 p = ro + rd * d;
        Hit h = map(p);
        if (h.dist < closest.dist)
            closest = h;
        if (h.dist <= surfHit)
            return TraceResult(h, d, ro, rd);
        d += h.dist;
    }
    return TraceResult(closest, d, ro, rd);
}

// Traces soft shadows
float traceShadow(vec3 ro, vec3 rd, float hardness, float surfHit, float farPlane, int maxSteps) {
    float d = surfHit;
    float k = hardness;
    float res = 1.0;

    for (int i=0; i < maxSteps && d <= farPlane; i++) {
        vec3 p = ro + rd * d;
        float stepDistance = map(p).dist;
        if (stepDistance < surfHit) return 0.0;
        res = min(res, k * stepDistance / d);
        d += stepDistance;
    }

    return clamp(res, 0.0, 1.0);
}


Surface getSurf(TraceResult tr) {
    vec3 position = tr.ro + tr.rd * tr.dist;
    vec3 normal = mapNormal(position, 0.01);
    vec3 view = tr.rd;
    float ao = calcAO(position, normal);

    return Surface(
        tr.hit.id,  // material id
        tr.dist,    // distance
        position,
        normal,
        ao,
        view
    );
}

Material matFromSurface(Surface s) {
    Material m;
    m.albedo    = vec3(0.0);
    m.metallic  = 0.0;
    m.roughness = 1.0;
    m.emissive  = vec3(0.0);
    m.ao = s.ao;

    if (s.materialId == 0) { // Floor
        m.albedo    = vec3(0.2, 0.2, 0.2);
        m.roughness = 0.0;
    } else if (s.materialId == 1) { // Ball
        m.albedo = vec3(0.2);
        m.roughness = 0.6;
        m.emissive = vec3(15000,15000,20000);
    } else if (s.materialId == 2) { // Chains
        m.albedo = vec3(.05);
        m.roughness = 0.2;
        m.metallic = 1.0;
        // m.emissive = vec3(0);
    } else { // Error
        m.emissive = vec3(1, 0, 1);
    }

    return m;
}

struct RenderInfo {
    vec3 color;
    float dist;
};


vec3 fakeReflectionIBL(Surface s, Material m) {
    vec3 reflection = reflect(s.view, s.normal);

    vec3 roughRef = sRGBToLinear(texture(iChannel0, reflection)).rgb;
    vec3 glossyRef = sRGBToLinear(texture(iChannel1, reflection)).rgb;

    float a = m.roughness * m.roughness;
    vec3 ref = mix(glossyRef, roughRef, a);

    return mix(vec3(0), ref, m.metallic);
}

RenderInfo render(Camera cam) {
    DirectionalLight sun = DirectionalLight(
        normalize(vec3(1,1,1)),
        vec3(10, 10, 10)
    );
    vec3 ambientLight = vec3(1);

    int primarySteps = 256;
    float farPlane = 20.0;
    float surfHit = 0.01;

    TraceResult traceRes = trace(cam.position, cam.direction, surfHit, farPlane, primarySteps);
    Surface surface = getSurf(traceRes);

    if (surface.materialId == -1) {
        return RenderInfo(
            texture(iChannel0, surface.view).rgb,
            farPlane
        );
    }

    Material material = matFromSurface(surface);

    vec3 shadowOrigin = surface.position + surface.normal * surfHit * 2.0;
    int shadowSteps = 32;
    float shadow = traceShadow(shadowOrigin, sun.direction, 28.0, surfHit, farPlane, shadowSteps);

    vec3 brdfColor = max(vec3(0.0), BRDF(sun, surface, material, shadow));
    vec3 ambientColor = ambientLight * material.albedo;

    vec3 finalColor = brdfColor + ambientColor * surface.ao + fakeReflectionIBL(surface, material);

    return RenderInfo(
        finalColor,
        traceRes.dist
    );
}

