// Fake volumetrics

const float INTENSITY = 1.0;
const float RANDOMIZE = 1.0;

vec4 sampleTex(vec2 uv) {
    vec4 color = texture(iChannel0, uv);
    float maxAllowed = 20000.0;
    if (length(color.rgb) < maxAllowed) {
        color = vec4(0.0);
    } else {
        color = pow(color, vec4(1.0));
        color *= INTENSITY / maxAllowed;
    }
    return color;
}

vec3 shaft(vec2 uv, int steps) {
    vec2 centerPos = vec2(0.5, 0.5);
    uv -= centerPos;
    float invSamples = 1.0 / float(steps);

    vec3 result = vec3(0.0);
    float scale = 0.0;

    for( int i = 1; i < steps; i++ ) {
        scale = (float(i) + rand(uv) * RANDOMIZE)*invSamples;
        result += sampleTex(uv * scale + centerPos).rgb;
    }

    return result * invSamples;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = shaft(uv, 32);
    fragColor = vec4(color, 1.0);
}

