/*
This performance test aims to compare planar mapping implementations. 

The test works by measuring frames per second (FPS) on Shadertoy. To 
ensure valid results, we repeat the test function with varying values, 
accumulating their results into a final image.

Steps:
- Increase REPEAT until the FPS are below your monitors refreshrate 
    (probably somewhere around 60-144 fps)
- Modify TEST value to switch between test cases
- The highest FPS indicates the fastest implementation
- Congratulate yourself for doing SCIENCE
*/

// Number of repeats of the test, 1500 should be enough for RTX3080
#define REPEAT 1

// switch between the test cases
// 0 = uniplanarIf using dithered deriviative Mips ~ 82 fps
// 1 = uniplanarIf using deriviative Mips ~ 78 fps
// 2 = uniplanarIf (use linear texture) ~ 132 fps
// 3 = uniplanarLerp (use linear texture) ~ 105 fps
// 4 = Biplanar  ~ 19 fps
// 5 = Triplanar ~ 63 fps
// 6 = Triplanar with Branching ~ 83 fps
// 7 = Triplanar with Less Branching ~ 80 fps
// 8 = Triplanar with Nested Branching ~ 78 fps
// 9 = Triplanar with Nested Branching Broken mips ~ 110 fps
#define TEST 6

// shows the blend area
//#define SHOWBLEND
#define LINETHICKNESS 0.05

// 1 for no contrast, higher values for more contrast
#define CONTRAST 16.0 

// one texture sample, uses dithering to make the edges smoother
vec4 uniplanarIfDitheredMips( sampler2D sam, vec3 normal, vec3 position, vec2 fragCoord, float contrast) {    
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    vec3 alpha = abs(normal); // Alpha Mask for blending

    alpha = smoothContrast(alpha, contrast); // increase contrast

    // static dither
    float dither = InterleavedGradientNoise(fragCoord);

    // Apply float dither to three values of Alpha
    float xDitherPart = alpha.x - dither;
    float yDitherPart = alpha.y + xDitherPart;

    // Mip caculation as the automatic ones don't work
    vec3 duvwdx = dFdx( position );
    vec3 duvwdy = dFdy( position );

    // uvs for derivatives Mips
    vec2 duvdx; 
    vec2 duvdy; 

    // "interpolate" the UVs
    vec2 uv;
    if (xDitherPart >= 0.0 && yDitherPart >= 0.0) {
        uv = uvX;
        duvdx = duvwdx.yz; // how is this thing allowed?
        duvdy = duvwdy.yz;
    } else if (yDitherPart >= 0.0) {
        uv = uvY;
        duvdx = duvwdx.zx;
        duvdy = duvwdy.zx;	
    } else {
        uv = uvZ;
        duvdx = duvwdx.xy;
        duvdy = duvwdy.xy;
    }

    // texture sample
    vec4 col = textureGrad( sam, uv, duvdx, duvdy);

    #ifdef SHOWBLEND
    float lineThickness = 0.05; // must be smaller than 0.1
    vec3 mask = step(abs(alpha - 0.1), vec3(lineThickness));
    return vec4(max(mask, col.xyz), col.w);
    #else
    return col;
    #endif
}

// one texture sample, uses dithering to make the edges smoother
vec4 uniplanarIfMips( sampler2D sam, vec3 normal, vec3 position, vec2 fragCoord, float contrast) {    
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    vec3 alpha = abs(normal); // Alpha Mask for blending

    alpha = smoothContrast(alpha, contrast); // increase contrast

    // static dither
    float dither = InterleavedGradientNoise(fragCoord);

    // Apply float dither to three values of Alpha
    float xDitherPart = alpha.x - dither;
    float yDitherPart = alpha.y + xDitherPart;

    // "interpolate" the UVs
    vec2 uv;
    if (xDitherPart >= 0.0 && yDitherPart >= 0.0) {
        uv = uvX;
    } else if (yDitherPart >= 0.0) {
        uv = uvY;
    } else {
        uv = uvZ;
    }

    // Mip caculation as the automatic ones don't work
    vec3 duvwdx = dFdx( position );
    vec3 duvwdy = dFdy( position );

    // uvs for derivatives Mips
    vec2 duvdx; 
    vec2 duvdy; 
    if (alpha.x>alpha.y && alpha.x>alpha.z) {
        duvdx = duvwdx.yz;
        duvdy = duvwdy.yz;
    } else if (alpha.y>alpha.z) {
        duvdx = duvwdx.zx;
        duvdy = duvwdy.zx;	
    } else {
        duvdx = duvwdx.xy;
        duvdy = duvwdy.xy;
    }  

    // texture sample
    vec4 col = textureGrad( sam, uv, duvdx, duvdy);

    #ifdef SHOWBLEND
    vec3 mask = step(abs(alpha - 0.1), vec3(LINETHICKNESS));
    return vec4(max(mask, col.xyz), col.w);
    #else
    return col;
    #endif
}

// one texture sample, uses dithering to make the edges smoother
vec4 uniplanarIf( sampler2D sam, vec3 normal, vec3 position, vec2 fragCoord, float contrast) {    
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    vec3 alpha = abs(normal); // Alpha Mask for blending

    alpha = smoothContrast(alpha, contrast); // increase contrast

    // static dither
    float dither = InterleavedGradientNoise(fragCoord);

    // Apply float dither to three values of Alpha
    float xDitherPart = alpha.x - dither;
    float yDitherPart = alpha.y + xDitherPart;

    // "interpolate" the UVs
    vec2 uv;
    if (xDitherPart >= 0.0 && yDitherPart >= 0.0) {
        uv = uvX;
    } else if (yDitherPart >= 0.0) {
        uv = uvY;
    } else {
        uv = uvZ;
    }

    vec4 col = texture( sam, uv);

    #ifdef SHOWBLEND
    vec3 mask = step(abs(alpha - 0.1), vec3(LINETHICKNESS));
    return vec4(max(mask, col.xyz), col.w);
    #else
    return col;
    #endif
}

// one texture sample, uses dithering to make the edges smoother
vec4 uniplanarLerp( sampler2D sam, vec3 normal, vec3 position, vec2 fragCoord, float contrast) {
    vec3 alpha = abs(normal); // Alpha Mask for blending

    alpha = smoothContrast(alpha, contrast); // increase contrast

    // static dither
    float dither = InterleavedGradientNoise(fragCoord);

    //apply float dither to three values of Alpha
    float xDitherPart = alpha.x - dither;
    float yDitherPart = step(0., alpha.y + xDitherPart);
    float xDither = step(0., xDitherPart);
    vec3 ditherAlpha = vec3(xDither, yDitherPart - xDither, 1.0 - yDitherPart);

    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    // interpolate (since we use bitmasks it is allowed for UVs)
    vec2 uv = uvX * ditherAlpha.x + uvY * ditherAlpha.y + uvZ * ditherAlpha.z;

    vec4 col = texture( sam, uv);

    #ifdef SHOWBLEND
    vec3 mask = step(abs(alpha - 0.1), vec3(LINETHICKNESS));
    return vec4(max(mask, col.xyz), col.w);
    #else
    return col;
    #endif
}

// IQ's Biplanar mapping implementation (https://www.shadertoy.com/view/ws3Bzf)
// The MIT License
// Copyright  2020 Inigo Quilez
vec4 biplanar( sampler2D sam, in vec3 p, in vec3 n, in float k ) {
    // grab coord derivatives for texturing
    vec3 dpdx = dFdx(p);
    vec3 dpdy = dFdy(p);
    n = abs(n);

    // major axis (in x; yz are following axis)
    ivec3 ma = (n.x>n.y && n.x>n.z) ? ivec3(0,1,2) :
               (n.y>n.z)            ? ivec3(1,2,0) :
                                      ivec3(2,0,1) ;
    // minor axis (in x; yz are following axis)
    ivec3 mi = (n.x<n.y && n.x<n.z) ? ivec3(0,1,2) :
               (n.y<n.z)            ? ivec3(1,2,0) :
                                      ivec3(2,0,1) ;

    // median axis (in x;  yz are following axis)
    ivec3 me = ivec3(3) - mi - ma;

    // project+fetch
    vec4 x = textureGrad( sam, vec2(   p[ma.y],   p[ma.z]), 
                               vec2(dpdx[ma.y],dpdx[ma.z]), 
                               vec2(dpdy[ma.y],dpdy[ma.z]) );
    vec4 y = textureGrad( sam, vec2(   p[me.y],   p[me.z]), 
                               vec2(dpdx[me.y],dpdx[me.z]),
                               vec2(dpdy[me.y],dpdy[me.z]) );

    // contrast
    vec2 m = vec2(n[ma.x],n[me.x]);
    // optional - add local support (prevents discontinuty)
    m = clamp( (m-0.5773)/(1.0-0.5773), 0.0, 1.0 );
    // transition control
    m = pow( m, vec2(k/8.0) );
    m = m / (m.x + m.y);

    // interpolate
	vec4 col = x*m.x + y*m.y;

    #ifdef SHOWBLEND
    vec2 mask = step(abs(m - 0.1), vec2(LINETHICKNESS));
    return vec4(max(mask, col.xy), col.zw);
    #else
    return col;
    #endif
}

// IQ's Triplanar mapping implementation (https://www.shadertoy.com/view/MtsGWH)
// The MIT License
// Copyright  2015 Inigo Quilez
vec4 triplanar( in sampler2D s, in vec3 p, in vec3 n, in float k ) {
    // project+fetch
	vec4 x = texture( s, p.yz );
	vec4 y = texture( s, p.zx );
	vec4 z = texture( s, p.xy );

    // and contrast
    vec3 m = pow( abs(n), vec3(k) );
    m = m / (m.x + m.y + m.z);

    // interpolate
    vec4 col = (x*m.x + y*m.y + z*m.z);

    #ifdef SHOWBLEND
    vec3 mask = step(abs(m - 0.1), vec3(LINETHICKNESS));
    return vec4(max(mask, col.xyz), col.w);
    #else
    return col;
    #endif
}

// Triplanar with branching, using 3,2,1 samples where possible. 
#define JUST_ABOVE_ZERO (1.0 / float(0xffffU))
#define JUST_BELOW_ONE (1.0 - JUST_ABOVE_ZERO)
vec4 branchingTriplanar( in sampler2D sam, in vec3 position, in vec3 normal, in float contrast ){
    // Alpha Mask for blending
    vec3 weights = abs(normal); 

    // Normalize alpha to sum=1
    weights = weights / (weights.x + weights.y + weights.z);

    // Apply contrast 
    weights = straightContrast(weights, contrast); // increase contrast    

    // Coordinate projections
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    // Mip caculation as the automatic ones don't work
    vec3 duvwdx = dFdx(position);
    vec3 duvwdy = dFdy(position);

    // Partial Derivative Projection
    vec2 duvdxX = duvwdx.yz; 
    vec2 duvdyX = duvwdy.yz;
    vec2 duvdxY = duvwdx.zx; 
    vec2 duvdyY = duvwdy.zx;
    vec2 duvdxZ = duvwdx.xy; 
    vec2 duvdyZ = duvwdy.xy;

    vec4 color;
    if (weights.x > JUST_BELOW_ONE) {
        // Sample X Side
        color = textureGrad(sam, uvX, duvdxX, duvdyX);

    } else if (weights.y > JUST_BELOW_ONE) {
        // Sample Y Side
        color = textureGrad(sam, uvY, duvdxY, duvdyY);

    } else if (weights.z > JUST_BELOW_ONE) {
        // Sample Z Side
        color = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

    } else if (weights.x < JUST_ABOVE_ZERO) {
        // Samples YZ Side 
        vec4 colorY = textureGrad(sam, uvY, duvdxY, duvdyY);
        vec4 colorZ = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

        // Color interpolation
        color = colorY * weights.y + 
                colorZ * weights.z;

    } else if (weights.y < JUST_ABOVE_ZERO) {
        // Samples XZ Side
        vec4 colorX = textureGrad(sam, uvX, duvdxX, duvdyX);
        vec4 colorZ = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

        // Color interpolation
        color = colorX * weights.x +
                colorZ * weights.z;

    } else if (weights.z < JUST_ABOVE_ZERO) {
        // Samples XY Side
        vec4 colorX = textureGrad(sam, uvX, duvdxX, duvdyX);
        vec4 colorY = textureGrad(sam, uvY, duvdxY, duvdyY);

        // Color interpolation
        color = colorX * weights.x + 
                colorY * weights.y;

    } else {
       // Samples all 3 
        vec4 colorX = textureGrad(sam, uvX, duvdxX, duvdyX);
        vec4 colorY = textureGrad(sam, uvY, duvdxY, duvdyY);
        vec4 colorZ = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

        // Color interpolation
        color = colorX * weights.x + 
                colorY * weights.y + 
                colorZ * weights.z;
    }

    #ifdef SHOWBLEND
        vec3 mask = step(abs(weights - 0.1), vec3(LINETHICKNESS));
        return vec4(max(mask, color.xyz), color.w);
    #else
        return color;
    #endif
}

// Triplanar with branching where a samples is 100% and 3 samples otherwise
#define JUST_ABOVE_ZERO (1.0 / float(0xffffU))
#define JUST_BELOW_ONE (1.0 - JUST_ABOVE_ZERO)
vec4 lessBanchingTriplanar( in sampler2D sam, in vec3 position, in vec3 normal, in float contrast ){
    // Alpha Mask for blending
    vec3 weights = abs(normal); 

    // Normalize alpha to sum=1
    weights = weights / (weights.x + weights.y + weights.z);

    // Apply contrast 
    weights = straightContrast(weights, contrast); // increase contrast    

    // Coordinate projections
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    // Mip caculation as the automatic ones don't work
    vec3 duvwdx = dFdx(position);
    vec3 duvwdy = dFdy(position);

    // Partial Derivative Projection
    vec2 duvdxX = duvwdx.yz; 
    vec2 duvdyX = duvwdy.yz;
    vec2 duvdxY = duvwdx.zx; 
    vec2 duvdyY = duvwdy.zx;
    vec2 duvdxZ = duvwdx.xy; 
    vec2 duvdyZ = duvwdy.xy;

    vec4 color;
    if (weights.x > JUST_BELOW_ONE) {
        // Sample X Side
        color = textureGrad(sam, uvX, duvdxX, duvdyX);

    } else if (weights.y > JUST_BELOW_ONE) {
        // Sample Y Side
        color = textureGrad(sam, uvY, duvdxY, duvdyY);

    } else if (weights.z > JUST_BELOW_ONE) {
        // Sample Z Side
        color = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

    } else {
        // Samples all 3 
        vec4 colorX = textureGrad(sam, uvX, duvdxX, duvdyX);
        vec4 colorY = textureGrad(sam, uvY, duvdxY, duvdyY);
        vec4 colorZ = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

        // Color interpolation
        color = colorX * weights.x + 
                colorY * weights.y + 
                colorZ * weights.z;
    }

    #ifdef SHOWBLEND
        vec3 mask = step(abs(weights - 0.1), vec3(LINETHICKNESS));
        return vec4(max(mask, color.xyz), color.w);
    #else
        return color;
    #endif
}

// Triplanar with nested branching, using texture grad to fix edges
vec4 NestedBranchingTriplanar( in sampler2D sam, in vec3 position, in vec3 normal, in float contrast ){
    // Alpha Mask for blending
    vec3 weights = abs(normal); 

    // Normalize alpha to sum=1
    weights = weights / (weights.x + weights.y + weights.z);

    // Apply contrast 
    weights = straightContrast(weights, contrast); // increase contrast    

    // Coordinate projections
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    // Mip caculation as the automatic ones don't work
    vec3 duvwdx = dFdx(position);
    vec3 duvwdy = dFdy(position);

    // Partial Derivative Projection
    vec2 duvdxX = duvwdx.yz; 
    vec2 duvdyX = duvwdy.yz;
    vec2 duvdxY = duvwdx.zx; 
    vec2 duvdyY = duvwdy.zx;
    vec2 duvdxZ = duvwdx.xy; 
    vec2 duvdyZ = duvwdy.xy;

    // Precalculate boolean statments for branchnig
    bool sampleX = weights.x > JUST_BELOW_ONE;
    bool sampleY = weights.y > JUST_BELOW_ONE;
    bool sampleZ = weights.z > JUST_BELOW_ONE;
    bool sampleYZ = weights.x < JUST_ABOVE_ZERO;
    bool sampleXZ = weights.y < JUST_ABOVE_ZERO;
    bool sampleXY = weights.z < JUST_ABOVE_ZERO;

    vec4 color;
    if (sampleX || sampleY || sampleZ) {
        // In case just one is visible
        // Find roughly the largest UVs
        vec2 uvSingle;
        vec2 ddxSingle;
        vec2 ddySingle;
        if (sampleX) {
            // X Side
            uvSingle = uvX;
            ddxSingle = duvdxX;
            ddySingle = duvdyX;

        } else if (sampleY) {
            // Y Side
            uvSingle = uvY;
            ddxSingle = duvdxY;
            ddySingle = duvdyY;

        } else {
            // Z Side
            uvSingle = uvZ;
            ddxSingle = duvdxZ;
            ddySingle = duvdyZ;
        }

        // sample Single
        color = textureGrad(sam, uvSingle, ddxSingle, ddySingle);

    } else if (sampleYZ || sampleXZ || sampleXY) {
        // In Case two values are Blending         
        vec2 uvDoubleA;
        vec2 uvDoubleB;
        vec2 ddxDoubleA;
        vec2 ddyDoubleA;
        vec2 ddxDoubleB;
        vec2 ddyDoubleB;
        vec2 weightsDouble;
        if (sampleYZ) {
            // YZ Side
            uvDoubleA = uvY;
            ddxDoubleA = duvdxY;
            ddyDoubleA = duvdyY;

            uvDoubleB = uvZ; 
            ddxDoubleB = duvdxZ;
            ddyDoubleB = duvdyZ;

            weightsDouble = vec2(weights.y, weights.z);

        } else if (sampleXZ) {
            // XZ Side
            uvDoubleA = uvX;
            ddxDoubleA = duvdxX;
            ddyDoubleA = duvdyX;

            uvDoubleB = uvZ;
            ddxDoubleB = duvdxZ;
            ddyDoubleB = duvdyZ;

            weightsDouble = vec2(weights.x, weights.z);

        } else {
            //  XY Side
            uvDoubleA = uvX;
            ddxDoubleA = duvdxX;
            ddyDoubleA = duvdyX;

            uvDoubleB = uvY;   
            ddxDoubleB = duvdxY;
            ddyDoubleB = duvdyY;

            weightsDouble = vec2(weights.x, weights.y);
        }

        // Samples 2 Sides
        vec4 colorX = textureGrad(sam, uvDoubleA, ddxDoubleA, ddyDoubleA);
        vec4 colorY = textureGrad(sam, uvDoubleB, ddxDoubleB, ddyDoubleB);

        // Color interpolation
        color = colorX * weightsDouble.x + 
                colorY * weightsDouble.y;

    } else {
        // Samples all 3 
        vec4 colorX = textureGrad(sam, uvX, duvdxX, duvdyX);
        vec4 colorY = textureGrad(sam, uvY, duvdxY, duvdyY);
        vec4 colorZ = textureGrad(sam, uvZ, duvdxZ, duvdyZ);

        // Color interpolation
        color = colorX * weights.x + 
                colorY * weights.y + 
                colorZ * weights.z;
    } 

    #ifdef SHOWBLEND
        vec3 mask = step(abs(weights - 0.1), vec3(LINETHICKNESS));
        return vec4(max(mask, color.xyz), color.w);
    #else
        return color;
    #endif
}

// Triplanar with nested branching each should hide the Mip edges ... but branching creates new edges?
vec4 FixNestedBranchingTriplanar( in sampler2D sam, in vec3 position, in vec3 normal, in float contrast ){
    // Alpha Mask for blending
    vec3 weightsO = abs(normal); 

    // Normalize alpha to sum=1
    weightsO = weightsO / (weightsO.x + weightsO.y + weightsO.z);

    // Apply contrast 
    vec3 weights = straightContrast(weightsO, contrast); // increase contrast    

    // Coordinate projections
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    vec4 color;    
    if (any(greaterThan(weights, vec3(JUST_BELOW_ONE)))) {
        // In case just one is visible
        // Find roughly the largest UVs
        vec2 uvSingle;
        if (weights.x > 0.5) {
            // X Side
            uvSingle = uvX;

        } else if (weights.y > 0.5) {
            // Y Side
            uvSingle = uvY;

        } else {
            // Z Side
            uvSingle = uvZ;
        }

        // sample Single
        color = texture(sam, uvSingle);

    } else if (any(greaterThan(vec3(JUST_ABOVE_ZERO), weights))) {
        // In Case two values are Blending         
        vec2 uvDoubleA;
        vec2 uvDoubleB;
        vec2 weightsDouble;
        if (weightsO.x < weightsO.y && weightsO.x < weightsO.z) {
            // YZ Side
            uvDoubleA = uvY;
            uvDoubleB = uvZ;   

            weightsDouble = vec2(weights.y, weights.z);

        } else if (weightsO.y < weightsO.z) {
            // XZ Side
            uvDoubleA = uvX;
            uvDoubleB = uvZ; 

            weightsDouble = vec2(weights.x, weights.z);

        } else {
            //  XY Side
            uvDoubleA = uvX;
            uvDoubleB = uvY;   

            weightsDouble = vec2(weights.x, weights.y);
        }

        // Samples 2 Sides
        vec4 colorX = texture(sam, uvDoubleA);
        vec4 colorY = texture(sam, uvDoubleB);

        // Color interpolation
        color = colorX * weightsDouble.x + 
                colorY * weightsDouble.y;

    } else {
        // Samples all 3 
        vec4 colorX = texture(sam, uvX);
        vec4 colorY = texture(sam, uvY);
        vec4 colorZ = texture(sam, uvZ);

        // Color interpolation
        color = colorX * weights.x + 
                colorY * weights.y + 
                colorZ * weights.z;
    } 

    #ifdef SHOWBLEND
        vec3 mask = step(abs(weights - 0.1), vec3(LINETHICKNESS));
        return vec4(max(mask, color.xyz), color.w);
    #else
        return color;
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 dir = sphereNormals(uv);

    // if you get around 144 fps use a higher repeat value
    vec4 test;
    for(int i=0; i<REPEAT; i++) {
        float mut = (float(i)/float(REPEAT)); // permutation each iteration

        // use which ever you wish to test
        #if TEST == 0
            test += uniplanarIfDitheredMips(iChannel0, dir + mut, dir - mut, fragCoord + mut, CONTRAST);
        #elif TEST == 1
            test += uniplanarIfMips(iChannel0, dir + mut, dir - mut, fragCoord + mut, CONTRAST);
        #elif TEST == 2
            test += uniplanarIf(iChannel0, dir + mut, dir - mut, fragCoord + mut, CONTRAST);
        #elif TEST == 3
            test += uniplanarLerp(iChannel0, dir + mut, dir - mut, fragCoord + mut, CONTRAST);
        #elif TEST == 4
            test += biplanar(iChannel0, dir + mut, dir - mut, CONTRAST);
        #elif TEST == 5
            test += triplanar(iChannel0, dir + mut, dir - mut, CONTRAST);
        #elif TEST == 6
            test += branchingTriplanar(iChannel0, dir + mut, dir - mut, CONTRAST);
        #elif TEST == 7
            test += lessBanchingTriplanar(iChannel0, dir + mut, dir - mut, CONTRAST);
        #elif TEST == 8
            test += NestedBranchingTriplanar(iChannel0, dir + mut, dir - mut, CONTRAST);
        #else
            test += FixNestedBranchingTriplanar(iChannel0, dir + mut, dir - mut, CONTRAST);
        #endif
    }

    #if REPEAT > 1
        // Ensure test vector stays in visible range
        test = fract(test);
    #endif

    // Output to screen
    fragColor = test;
}

