/* This is using dither to make a triplanar mapping variant with one 
texture sample. So it would be Uniplanar mapping then. 

There is a little bit of flickering in the resulting image, I would 
recomend using TAA to blend it with past frames which makes this 
effect basically visually the same as the triplanar mapping. 

To see how Dither "Interpolation" works see this demo:
https://www.shadertoy.com/view/dsjBzh

See here for a performance test between Uni, Bi, TriPlanar
https://www.shadertoy.com/view/cs2BD3
*/

// number of previous frames looked at for AA effect also hides dithering
#define AA 1

// Enable debug grid where the two mappings blend with each other
//#define SHOWBLEND

//1 no contrast, higher values increase contrast
#define Contrast 16.

// valve edition (slightly modified to be a true 12 dither)
// http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
// input in pixels (ie not normalized uv)
float ScreenSpaceDither12(vec2 vScreenPos, float time)
{
    float vDither = dot( vec2( 171.0, 231.0 ), vScreenPos.xy + time); 
    return fract( vDither / 103.0);
}

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See 2 value example (https://www.desmos.com/calculator/dpxa6mytnv)
vec3 smoothContrast(vec3 alpha, float contrast) {
    // increase steepness using power
    vec3 powAlpha = pow(alpha, vec3(contrast));

    // normalize back to precentage of 1
    return powAlpha/(powAlpha.x + powAlpha.y + powAlpha.z);
}

vec4 uniplanar( sampler2D sam, vec3 normal, vec3 position, vec2 fragCoord, float contrast, int tOffset) {
    vec2 uvX = position.yz;
    vec2 uvY = position.zx;
    vec2 uvZ = position.xy;

    vec3 alpha = abs(normal); // Alpha Mask for blending

    alpha = smoothContrast(alpha, contrast); // increase contrast

    // sample dither
    float dither = ScreenSpaceDither12(fragCoord, iTime-float(tOffset));

    // smooth contrast creates artifacts in the dither. Clamp on the dither removes it
    dither = clamp(dither, 0.01, 0.99);

    // Mip caculation as the automatic ones don't work
    vec3 duvwdx = dFdx(position);
    vec3 duvwdy = dFdy(position);

    // uvs for derivatives Mips
    vec2 duvdx; 
    vec2 duvdy;

    // "interpolate" the UVs using dither
    vec2 uv;
    if (alpha.x > dither) {
        uv = uvX;
        duvdx = duvwdx.yz; // how is this thing allowed?
        duvdy = duvwdy.yz;
    } else if (1.-alpha.z > dither) {
        uv = uvY;
        duvdx = duvwdx.zx;
        duvdy = duvwdy.zx;	
    } else {
        uv = uvZ;
        duvdx = duvwdx.xy;
        duvdy = duvwdy.xy;
    } 

    // texture sample
    vec4 col = textureGrad( sam, uv, duvdx, duvdy);

    #ifdef SHOWBLEND
    float lineThickness = 0.05; // must be smaller than 0.1
    vec3 mask = step(abs(alpha - 0.1), vec3(lineThickness));
    return vec4(max(mask, col.xyz), col.w);
    #else
    return col;
    #endif
}

