float smin( float a, float b, float k )
{
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}

float map( in vec3 p )
{
	float d = length(p-vec3(0.0,1.0,0.0))-1.0;
    d = smin( d, p.y, 1.0 );
    return d;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos, in float eps )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*eps;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        int tOffset = m+n;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        int tOffset = 0;
#endif

        // camera movement	        
        vec3 ro; // camera pos
        float dist = 2.5;
        // If the mouse was not clicked, animate;
        if (iMouse.x > 0.0) {
            // Scale mouse coords to 0-1 range of screen
            vec2 mouse = iMouse.xy / iResolution.xy;

            float dist = 2.0 + mouse.y * 4.0;
            float angle = mouse.x * 6.283 * 1.25;

             // Calculate direction clockwise
            vec2 direction = vec2(sin(angle), cos(angle));
            ro = vec3(sin(angle)* dist, 2.0, cos(angle)* dist) ;

        } else {        
            // Animated 
            float an = 0.2*iTime;
            ro = vec3( 2.5*sin(an), 2.0, 2.5*cos(an) );
        }


        vec3 ta = vec3( 0.0, 1.0, 0.0 ); // camera target
        // camera matrix
        vec3 ww = normalize( ta - ro );
        vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
        vec3 vv = normalize( cross(uu,ww));
        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        float tmax = dist+8.0;
        float t = dist-2.;
        for( int i=0; i<128; i++ )
        {
            vec3 pos = ro + rd*t;
            float h = map(pos);
            if( h<0.001 ) break;
            t += h;
            if( t>tmax ) break;
        }

        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + rd*t;
            vec3 nor = calcNormal( pos, 0.001 );
            float occ = clamp(0.4 + 0.6*nor.y, 0.0, 1.0);
            col = uniplanar(iChannel0, nor, 0.5*pos, fragCoord, Contrast, tOffset).xyz;
            col = col*col;
            col *= occ;
            col *= 2.0;
            col *= 1.0-smoothstep(1.0,6.0,length(pos.xz));
        }
        // to gamma space
        col = sqrt( col );
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

	fragColor = vec4( tot, 1.0 );
}

