/*
This is a simple variant of the Square Directional Flow. This one simply doesn't move.
It is a great way to create a non repeating tiling. It uses dithering to interpolate
UVs and is there able to only use 1 texture lookup.  
This uses 1 cheap hash call, 1 cheap dither, and 1 texture lookup.

Directional Flow animates and rotates a texture in multiple directions with 
a flowmap. This means that water wave textures can flow in multiple directions 
at the same time, while the waves point in the right direction. This is not 
possible with trational flow animation, which either has only one wind 
direction or stretches the texture with temporal blending. The Directional Flow
does this by blending in the spatial dimension. 

The earliest iteration of directional flow I could find is from 2010:
https://www.rug.nl/society-business/centre-for-information-technology/research/hpcv/publications/watershader/
But CatlikeCoding's version is much better explained, so I recommend reading 
it as a guide:
https://catlikecoding.com/unity/tutorials/flow/directional-flow/
*/

//#define RepeatTiling
//#define ShowGrid
//#define AnimatedDither

// My customized hash which is faster than a texture lookup
// inspired by David Hoskins https://www.shadertoy.com/view/XdGfRR
#define VPRIME1 uvec2(3480082861U, 2420690917U)
#define VPRIME2 uvec2(1317666547U, 2149110343U)
#define SMALLESTFLOAT (1.0 / float(0xffffffffU))
vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * VPRIME1;
	q = (q.x & q.y) * VPRIME2;
	return vec2(q) * SMALLESTFLOAT;
}

// "NEXT GENERATION POST PROCESSING IN CALL OF DUTY: ADVANCED WARFARE"
// https://advances.realtimerendering.com/s2014/index.html#_NEXT_GENERATION_POST
float InterleavedGradientNoise( vec2 uv )
{
    const vec3 magic = vec3( 0.06711056, 0.00583715, 52.9829189 );
    return fract( magic.z * fract( dot( uv, magic.xy ) ) );
}

// valve edition (slightly modified to be a true 12 dither)
// http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
// input in pixels (ie not normalized uv)
float ScreenSpaceDither12(vec2 vScreenPos)
{
    float vDither = dot( vec2( 171.0, 231.0 ), vScreenPos.xy + iTime); 
    return fract( vDither / 103.0);
}

// struct to hold 5 floats at a time of my tiling functions
struct tilingVal 
{
    vec2 grid;       // Coordinates of the cell in the grid (UV centered on cell)
    vec2 id;         // ID values
    float edgeDist;  // Edge distance from the cell's center to its boundaries
};

// Square tiling function
tilingVal squareTile(vec2 uv) {
    vec2 grid = fract(uv) - 0.5;   // the cell coordinates in the grid

    vec2 distanceFromCenter = abs(grid);
    float edgeDist = (0.5 - max(distanceFromCenter.x, distanceFromCenter.y)) * 2.0;

    vec2 id = uv - grid;  // ID values for the cell

    return tilingVal(grid, id, edgeDist);  // make tilingVal struct
}

// Rhombus tiling function
tilingVal rhomTile(vec2 uv) {
    vec2 a = fract(uv- vec2(.0, .5))-.5;
    vec2 b = fract(uv- vec2(.5, .0))-.5;    
    vec2 grid = dot(a,a) < dot(b,b) ? a : b; //UV centered in cell

    vec2 ma = abs(a);
    vec2 mb = abs(b);
    float eDist = (max(ma.x+ma.y,mb.x+mb.y)-0.5)*2.; //Edge Distance

    // adding tiny offset to prevent floating point errors
    vec2 id = uv - grid  + 0.0000001; //ID values
    return tilingVal(grid, id, eDist);  // make tilingVal struct
}

// rotates and animates texture in flowmap direction 
vec3 directionalTex (vec2 uv, vec2 flowMap, vec2 duvdx, vec2 duvdy) {
    vec2 direction = normalize(flowMap); // normalize for rotation
    uv = mat2x2(direction.y, direction.x, -direction.x, direction.y)*uv; // rotate UVs

    uv += flowMap; // offset

    // scale UVs so the entire texture is visible in a tile
    // the proper range is ~1-3 and artists don't read, so this is hard set to
    uv *= 2.0; 

    return textureGrad(iChannel0, uv, duvdx, duvdy).xyz; // sample texture
}

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See 2 value example (https://www.desmos.com/calculator/dpxa6mytnv)
vec3 smoothContrast(vec3 alpha, float contrast) {
    // increase steepness using power
    vec3 powAlpha = pow(alpha, vec3(contrast));

    // normalize back to precentage of 1
    return powAlpha/(powAlpha.x + powAlpha.y + powAlpha.z);
}

// 3 Grids blending between each other with each tile having a unique rotation and offset
vec3 squareDirectionalNoiseDither(vec2 uv, float gridRes, float contrast, vec2 fragCoord) {
    #ifdef RepeatTiling    
        uv = fract(uv); // repeating UVs is sometimes useful
        // needs to be a hole number or tiling won't be seamless
        gridRes = floor(gridRes);
    #endif    

    uv *= gridRes; // scalue UVs to gridres

    tilingVal A = squareTile(uv); // grid A
    tilingVal B = squareTile(uv + vec2(0.5)); // grid B offset to corner of grid A
    tilingVal C = rhomTile(uv); // grid C

    //All edgeDist added = 1 -> so we can skip the more expensive C calculation
    vec3 alpha = vec3(A.edgeDist, B.edgeDist, 1.-(A.edgeDist + B.edgeDist)); 

    alpha = smoothContrast(alpha, contrast); // increase contrast

    #ifdef AnimatedDither
        // smooth contrast creates artifacts in the dither. Clamp on the dither removes it
        float dither = clamp(ScreenSpaceDither12(fragCoord), 0.01, 0.99);
    #else
        // static dither
        float dither = InterleavedGradientNoise(fragCoord);
    #endif

    // add prime number to avoid 0 which hash doesn't like
    A.id = A.id + 181.;    
    #ifdef RepeatTiling
        // mod for repeating at UV=1
        B.id = mod(B.id, gridRes) + 691.;
        // also multiply by 2 as hash rounds UVs, adding ti
        C.id = mod(C.id, gridRes)*2. + 353.);
    #else
        B.id = B.id + 691.;
        C.id = C.id * 2. + 353.;
    #endif

    // "interpolate" the UVs using dither
    float sumXY = alpha.x + alpha.y;
    vec2 ditherId;
    vec2 ditherGrid;
    if (alpha.x > dither) {
        ditherId = A.id;
        ditherGrid = A.grid;
    } else if (1.-alpha.z > dither) {
        ditherId = B.id;
        ditherGrid = B.grid;
    } else {
        ditherId = C.id;
        ditherGrid = C.grid;
    }

    // Sample Noise using dithered ID
    vec2 noise = hash22(ditherId)*2.-1.;

    // Mip caculation as the automatic ones don't work
    vec2 duvdx = dFdx( uv );
    vec2 duvdy = dFdy( uv );

    // rotate and offset then sample texture using dithered UVs. 
    // !!! Auto Mips must be disabled, but computed values still work
    vec3 col = directionalTex(ditherGrid, noise, duvdx, duvdy);

    #ifdef ShowGrid
        float lineThickness = 0.05; // must be smaller than 0.1
        vec3 mask = step(abs(alpha - 0.1), vec3(lineThickness));
        return max(mask, col);
    #else
        return col;
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // rectangular UVs
    vec2 uv = fragCoord/iResolution.y;    
    // uv *= 2.; // to better see repeating tilings

    float gridRes = 2.;
    float contrast = 4.; //1 no contrast, higher values increase contrast

    vec3 col = squareDirectionalNoiseDither(uv, gridRes, contrast, fragCoord);

    fragColor = vec4(col, 1.);
}

