void mainImage( out vec4 C, in vec2 F )
{
    vec2 uv = (( F.xy / iResolution.xy ) * 2.) - 1.;
    float r = length( uv );
    uv /= ( 2. * distortion_amount * r * r );
    uv = ( ( uv * ( 1. - sqrt( 1. - 4. * distortion_amount * r * r ) ) ) + 1. ) / 2.;
    float v = min( min( uv.x, 1. - uv.x ), min( uv.y, 1. - uv.y ) ),
        AA = 0.5 * length( vec2( dFdx( v ), dFdy( v ) ) );
    vec4 t = mix( vec4( 0. ), texture( iChannel0, uv ), smoothstep( -AA, AA, v ) );
    // bloom
    float weight[ 7 ] = float[]( 0.25, 0.5, 1.0, 2.0, 1.0, 0.5, 0.25 );
    C = vec4(0.);
    for( int x = -3; x <= 3; ++x )
        for( int y = -3; y <= 3; ++y )
            C += weight[ x + 3 ] *
                weight[ y + 3 ] *
                texture( iChannel0, uv + vec2( x, y ) * (1.0 / iResolution.xy) );
    C = mix(texture( iChannel0, uv ), C / 7., bloom_amount * mask_amount)
        // barrel-distortion mask
        * smoothstep( -AA, AA, v );
}

