float hash( vec3 p )
{
	p = fract( p * 0.1031 );
	p += dot( p, p.yzx + 19.19 );
	return fract( ( p.x + p.y ) * p.z );
}

float noise( vec3 x )
{
	vec3 p = floor( x ), f = fract( x ), m = f * f * ( 3.0 - 2.0 * f );
	vec3 i = p + vec3( 1.0, 0.0, 0.0 );
	vec4 hash = vec4( hash( p ), hash( i ), hash( p + vec3( 0.0, 1.0, 0.0 ) ), hash( i + vec3( 0.0, 1.0, 0.0 ) ) );
	return mix( mix( hash.x, hash.y, m.x ), mix( hash.z, hash.w, m.x ), m.y );
}

float grain( vec3 x )
{
	return 0.5 + ( 4.0 * noise( x ) - noise( x + 1. ) + noise( x - 1. ) ) / 4.0;
}

void mainImage( out vec4 C, in vec2 F )
{
	vec2 uv = F / iResolution.xy,
        aber_dis = ( uv - vec2( 0.5 ) ) * aberration_amount * length( uv - 0.5 ) * .5;
	vec3 aber = vec3(
            texture( iChannel0, uv ).r,
            texture( iChannel0, uv - aber_dis ).g,
            texture( iChannel0, uv - 2.0 * aber_dis ).b
        );
	float frame = floor( float( iFrame ) );
	vec3 rgb_grain = vec3(
            grain( vec3( F, frame ) ),
            grain( vec3( F, frame + 9.0 ) ),
            grain( vec3( F, frame - 9.0 ) )
        );
    float radius = rounded_amount * ((iResolution.x + iResolution.y)*.5) * .5;
	//
    C = vec4(vec3(
        // aberration + RGB grain
        mix( aber, mix( aber * rgb_grain, aber + ( rgb_grain - 1.0 ), 0.5 ), noise_amount )
        // vignette
        * mix( 1.0, 1.0 - clamp( smoothstep( 0.25, 1.0, length( (uv - vec2( 0.5 )) * vec2( 1.0, iResolution.y / iResolution.x * 2.0 ) ) ), 0.0, 1.0 ), vignette_amount )
        )
        // rounded corners
        * step(length(max(abs(F - iResolution.xy / 2.)-(iResolution.xy / 2.)+radius,0.0))-radius, 0.)
        ,1.);
}

