#define R vec3( 1.0, 0.0, 0.0 )
#define G vec3( 0.0, 1.0, 0.0 )
#define B vec3( 0.0, 0.0, 1.0 )
#define M vec3[ 28 ]( X, X, X, X, X, X, X, X, R, R, G, G, B, B, X, R, R, G, G, B, B, X, R, R, G, G, B, B )

void mainImage( out vec4 C, in vec2 F )
{
    vec2 uv = floor((F / iResolution.xy) * (iResolution.xy / vec2(7., 4.)));
    float hex_offset = mod(uv.x, 2.0) * 2.;
    uv.y += floor(mod(F.y, 4.) / 2.) * hex_offset * .5;

    // 7x4 pixelation
    C = vec4(0.0);
    for(float y = 0.0; y < 4.; y++) {
        for(float x = 0.0; x < 7.; x++) {
            C += texture(iChannel0, ((uv * vec2(7., 4.)) + vec2(x, y)) / iResolution.xy);
        }
    }
    C = mix(texture(iChannel0,F / iResolution.xy), C/28.,pixelate_amount);

    C.rgb *= mix(vec3(1.),
        // 7x4 sub-pixel RGB mask
        M[int(int(mod(F.y + hex_offset, 4.0)) * 7 + int(mod(F.x, 7.0)))],
        mask_amount);
}

