#define TWOPI 6.28318530718f
#define AA 0.001f
#define SIXTY_DEGREES 1.0471975512f

#define u_TimeScaleModifier 0.1f
#define u_HexRadius .15f

struct HexData {
    // value (0 -> 1) representing the uv's value in radial space, origin is (1, 0)
    float radialVal; 

    // value (0 -> 1) representing approximity to center compared to hex radius
    float distFromHexCenter;  

    // center vertex of the hexagon
    vec2 hexCenter;           

    // locations of each vertex of the hex
    vec2[6] hexVertices;      

    // value (0 -> 1) representing approximity to center compared to hexagon's edge
    float edgeCoefficient;  

    // value (0 -> 1) representinglinear interpolation of radians between local triangle vertices
    float radialLerp;         

    // value (0 -> 1) representing the sumtotal of each hex vertex's distance to the uv compared
    //       to the maximum sumtotal distance possible
    float sumDistCoefficient; 
};

float circleSmoothStepFill(vec2 center, float radius, vec2 p, float antiAlias) {
	return 1.f - smoothstep(radius - antiAlias, radius + antiAlias, distance(center, p));
}

// from https://www.shadertoy.com/view/4djSRW
float hash(vec2 p)
{
    float val = sin(dot(p, vec2(12.9898f, 78.233f))) * 43758.5453f;
    return val - floor(val);
}

vec2 rotateAroundAxis(vec2 point, vec2 axis, float angle) 
{
    point -= axis;
    float x = point.x * cos(angle) - point.y * sin(angle);
    float y = point.y * cos(angle) + point.x * sin(angle);
    return vec2(x, y) + axis;
}

float lineSegmentSmoothStep(vec2 start, vec2 end, vec2 p, float lineThickness, float antiAlias, bool roundEnds) {
    float halfThickness = lineThickness / 2.f;
    if (dot(p-end, start - end) < 0.f || dot(p - start, end - start) < 0.f) {
        return !roundEnds ? 0.f : max(
			circleSmoothStepFill(start, halfThickness, p, antiAlias),
			circleSmoothStepFill(end, halfThickness, p, antiAlias)
        );  
    }

    vec2 lineVec = normalize(end - start);
    vec2 pVec = normalize(p - start);

    float angle = acos(dot(lineVec, pVec) / length(lineVec) * length(pVec));
    float distFromLine = sin(angle) * distance(start, p);

    return 1.f - smoothstep(lineThickness / 2.f - antiAlias, halfThickness + antiAlias, distFromLine);
}

struct AspectRatioData {
    mat2x2 scaleMatrix;
    mat2x2 inverseScaleMatrix;
    float aspectRatio;
};

AspectRatioData getAspectRatioData(vec2 uvSize) {
    float aspectRatio = uvSize.x / uvSize.y;
    AspectRatioData aspectRatioData;
    aspectRatioData.aspectRatio = aspectRatio;
    aspectRatioData.scaleMatrix = mat2x2(
        aspectRatio, 0.f,
        0.f, 1.f
    );

    aspectRatioData.inverseScaleMatrix = mat2x2(
        1.f / aspectRatio, 0.f,
        0.f, 1.f
    );

    return aspectRatioData;
}

bool isHexCenter(vec2 pos, float hexXIncrement, float hexYIncrement) {
    float columnIndex = round(pos.x / hexXIncrement);
    float rowIndex = round(pos.y / hexYIncrement);
    return int(round(mod(abs(columnIndex), 2.f))) == int(round(mod(abs(rowIndex), 2.f)));
}

float getOffsetAngle(vec2 first, vec2 second) {
    vec2 offsetVec = second - first;
    float angle = atan(offsetVec.y / offsetVec.x);


    if (first.x < second.x) {
        angle = TWOPI / 2.f + angle;
    } else if (first.y > second.y) {
        angle = TWOPI + angle;
    }

    return angle;
}

mat2x2 createRotationMatrix(float rotation) {
    return mat2x2(
        cos(rotation), -sin(rotation),
        sin(rotation), cos(rotation)
    );
}

float getHoldTransitionValue(float lower, float upper, float holdTime, float transitionTime, float time) {
    float period = 2. * holdTime + 2. * transitionTime;
    float halfPeriod = period / 2.;
    float relativeTime = fract(time / halfPeriod);
    float halfHoldTime = holdTime / 2.;

    float val = smoothstep(halfHoldTime, halfHoldTime + transitionTime, relativeTime * halfPeriod);

    if (int(time / halfPeriod) % 2 == 0) {
        val = 1. - val;
    }

    return mix(lower, upper, val);
}

vec3 vec2Tovec3(vec2 vec) {
    return vec3(vec.x, vec.y, 0.f);
}

vec2 getHexCenter(vec2 aspectUV, 
                    vec2 leftBottom, 
                    vec2 leftTop, 
                    vec2 rightBottom, 
                    vec2 rightTop,
                    float aspectHexGridXIncrement, 
                    float hexGridYIncrement,
                    float aspectHexRadius) {
    vec2 hexCenter = vec2(-1.f, -1.f); 

    if (isHexCenter(leftBottom, aspectHexGridXIncrement, hexGridYIncrement)) {
        vec2 hexDiagRight = leftBottom + vec2(aspectHexRadius, 0.f);
        vec2 hexDiagLeft = leftTop + vec2(aspectHexRadius / 2.f, 0.f);
        vec2 sharedEdgeVector = normalize(vec2(hexDiagLeft - hexDiagRight));
        vec2 sharedToRightTopVector = normalize(vec2(rightTop - hexDiagRight));
        vec2 sharedToUVVector = normalize(vec2(aspectUV - hexDiagRight));

        vec3 crossRightTop = cross(vec2Tovec3(sharedEdgeVector), 
                                    vec2Tovec3(sharedToRightTopVector));
        vec3 crossUV = cross(vec2Tovec3(sharedEdgeVector), 
                                vec2Tovec3(sharedToUVVector));

        hexCenter = (crossRightTop.z == crossUV.z) || 
            (crossRightTop.z < 0.f && crossUV.z < 0.f) || 
            (crossRightTop.z > 0.f && crossUV.z > 0.f) ? rightTop : leftBottom;
    } else {
        vec2 hexDiagRight = leftTop + vec2(aspectHexRadius, 0.f);
        vec2 hexDiagLeft = rightBottom - vec2(aspectHexRadius, 0.f);
        vec2 sharedEdgeVector = normalize(vec2(hexDiagRight - hexDiagLeft));
        vec2 sharedToRightBottomVector = normalize(vec2(rightBottom - hexDiagLeft));
        vec2 sharedToUVVector = normalize(vec2(aspectUV - hexDiagLeft));

        vec3 crossRightBottom = cross(vec2Tovec3(sharedEdgeVector), 
                                        vec2Tovec3(sharedToRightBottomVector));
        vec3 crossUV = cross(vec2Tovec3(sharedEdgeVector), 
                                vec2Tovec3(sharedToUVVector));

        hexCenter = crossRightBottom.z == crossUV.z || 
            (crossRightBottom.z < 0.f && crossUV.z < 0.f) || 
            (crossRightBottom.z > 0.f && crossUV.z > 0.f) ? rightBottom : leftTop;
    }

    return hexCenter;
}

vec2[6] getHexVertices(vec2 hexCenter, float hexRadius) {
    vec2[6] hexVertices;

    float rotationIncrement = TWOPI / 6.;

    vec2 firstVertex = vec2(hexRadius, 0.) * createRotationMatrix(rotationIncrement * 2.);

    for (int i=0; i<6; ++i) {
        hexVertices[i] = firstVertex * createRotationMatrix(rotationIncrement * float(i)) + hexCenter;
    }

    return hexVertices;
}

float getEdgeCoefficient(vec2 uv, vec2 hexCenter, float hexRadius, float radialVal) {
    vec2 adjustedUv = uv - hexCenter;
    float adjustmentRotation = mod(radialVal, TWOPI / 6.);
    mat2x2 adjsutmentRotationMatrix = createRotationMatrix(adjustmentRotation);
    adjustedUv = adjustedUv * adjsutmentRotationMatrix;

    float radialLerp = mod(radialVal, 1./6.) / (1./6.);
    radialLerp = .5 - (radialLerp > .5 ? 1. - radialLerp : radialLerp);
    float operativeAngle = radialLerp * TWOPI / 6.;

    float tangentDistance = cos(TWOPI / 12.) * hexRadius;

    float maxToEdge = tangentDistance / cos(operativeAngle);
    return distance(uv, hexCenter) / maxToEdge;
}

float getRadialVal(vec2 hexCenter, vec2 p) {
    float offsetAngle = getOffsetAngle(hexCenter, p);
    offsetAngle = mod(TWOPI - offsetAngle + 3.f * TWOPI / 6.f, TWOPI);
    return offsetAngle / TWOPI;
}

HexData getHexData(
    vec2 uv, 
    float hexRadius, 
    float hexGridXIncrement,
    float hexGridYIncrement) 
{
    float aspectHexGridXIncrement = hexGridXIncrement;

    float leftEdge = floor(uv.x / aspectHexGridXIncrement) * aspectHexGridXIncrement;
    float rightEdge = leftEdge + aspectHexGridXIncrement;
    float bottomEdge = floor(uv.y / hexGridYIncrement) * hexGridYIncrement;
    float topEdge = bottomEdge + hexGridYIncrement;


    vec2 leftBottom = vec2(leftEdge, bottomEdge);
    vec2 leftTop = vec2(leftEdge, topEdge);
    vec2 rightTop = vec2(rightEdge, topEdge);
    vec2 rightBottom = vec2(rightEdge, bottomEdge);

    float aspectHexRadius = hexRadius;
    vec2 hexCenter = getHexCenter(uv,
                            leftBottom, 
                            leftTop, 
                            rightBottom, 
                            rightTop, 
                            aspectHexGridXIncrement, 
                            hexGridYIncrement,
                            aspectHexRadius);

    float distMin = 6. * u_HexRadius;
    float distMax = 4. * u_HexRadius + 4. * cos(SIXTY_DEGREES / 2.) * u_HexRadius;

    HexData hexData;
    hexData.radialVal = getRadialVal(hexCenter, uv);
    hexData.hexCenter = hexCenter;
    hexData.distFromHexCenter = distance(uv, hexCenter) / hexRadius;
    hexData.hexVertices = getHexVertices(hexCenter, hexRadius);
    hexData.edgeCoefficient = getEdgeCoefficient(uv, hexCenter, hexRadius, hexData.radialVal);
    hexData.radialLerp = mod(hexData.radialVal, 1./6.) / (1./6.);

    float distTotal = 0.;
    for (int i=0; i<6; ++i) {
        distTotal += distance(uv, hexData.hexVertices[i]);
    }
    hexData.sumDistCoefficient = (distTotal - distMin) / (distMax - distMin);

    return hexData;
}

float getVal(HexData hexData, vec2 uv, float timeOffset) {
    vec2 hexCenter = hexData.hexCenter;
    float pointRadius = u_HexRadius * .1;

    float timeVal = (sin((iTime * .5) + timeOffset * TWOPI) + 1.) / 2.;
    float timeIncrement = 8.;
    //timeVal = getHoldTransitionValue(0., 1., timeIncrement / 4., timeIncrement / 4., iTime * .25 + (timeIncrement * timeOffset));

    vec2 farRightPoint = hexData.hexCenter + vec2(u_HexRadius, 0.);
    float shortSide = cos(TWOPI/12.) * u_HexRadius;
    vec2 secondPoint = rotateAroundAxis(hexData.hexCenter + vec2(shortSide, 0.), hexData.hexCenter, TWOPI / 12.);
    vec2 thirdPoint = rotateAroundAxis(hexData.hexCenter + vec2(shortSide, 0.), hexData.hexCenter, TWOPI / -12.);
    vec2 fourthPoint = farRightPoint - vec2(2. * (farRightPoint.x - secondPoint.x), 0.);
    vec2 fifthPoint = rotateAroundAxis(fourthPoint, hexData.hexCenter, TWOPI / -6.);
    vec2 sixthPoint = rotateAroundAxis(farRightPoint, hexData.hexCenter, TWOPI / -6.);

    //float val = circleSmoothStepFill(secondPoint, pointRadius, uv, AA);
    float val = max(0., circleSmoothStepFill(thirdPoint, pointRadius, uv, AA));
    val = max(val, circleSmoothStepFill(fourthPoint, pointRadius, uv, AA));
    val = max(val, circleSmoothStepFill(farRightPoint, pointRadius, uv, AA));
    val = max(val, circleSmoothStepFill(fifthPoint, pointRadius, uv, AA));
    val = max(val, circleSmoothStepFill(sixthPoint, pointRadius, uv, AA));
    val = 0.;

    vec2 connectionPoint = mix(fourthPoint, farRightPoint, timeVal);
    vec2 connectionPoint2 = mix(fifthPoint, sixthPoint, timeVal);

    float lineThickness = pointRadius * .4;
    val = max(val, lineSegmentSmoothStep(thirdPoint, connectionPoint, uv, lineThickness, AA, true));
    val = max(val, lineSegmentSmoothStep(thirdPoint, connectionPoint2, uv, lineThickness, AA, true));
    return val;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    AspectRatioData aspectRatioData = getAspectRatioData(iResolution.xy);

    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * aspectRatioData.scaleMatrix;

    uv += vec2(iTime * .05, sin(iTime * .05));

    float hexGridXIncrement = 1.5f * u_HexRadius;
    float hexGridYIncrement = u_HexRadius * sin(SIXTY_DEGREES);

    HexData hexData = getHexData(
        uv, 
        u_HexRadius, 
        hexGridXIncrement,
        hexGridYIncrement);

    for (int i=0; i<6; i++) {
        vec2 hexVertex = hexData.hexVertices[i];
    }


    int offset = int(hexData.radialVal * 6.);
    vec2 originalUV = uv;
    uv = rotateAroundAxis(uv, hexData.hexCenter, (float(offset) * TWOPI / 6.));

    float timeOffset = hexData.radialVal;

    float val = getVal(hexData, uv, timeOffset);
    float val2 = getVal(hexData, uv, timeOffset * -1.);
    float val3 = getVal(hexData, uv, 0.);
    float val4 = getVal(hexData, uv, hexData.radialLerp);

    vec4 color1 = vec4(vec3(0., 0., mix(.5, 1., val4)), 1.0);

    float colorMix = getHoldTransitionValue(0., 1., 10., 2., iTime);
    vec4 color2 = vec4(vec3(val * .8, val2 * .8, .5), 1.0);
    vec4 color3 = vec4(vec3(0., val2 * .8, .5), 1.0);
    vec4 color4 = vec4(vec3(val * .8, 0., .5), 1.0);

    vec4 oscillateColor = mix(color3, color4, colorMix);
    vec4 oscCombined = mix(color2, oscillateColor, getHoldTransitionValue(0., 1., 5., 1., iTime));

    fragColor = mix(color1, color2, vec4(getHoldTransitionValue(0., 1., 10., 2., iTime)));
}

float text(vec2 uv, float ix)
{
    float col = 0.0;
    vec2 center = text_res/2.0;

    text_pos(2, 2.0 + STRHEIGHT(0.0));
    col += print_integer(ix*10.,0,uv) + _lsl() + print_integer(LAST_PATT,0,uv); 

    return col;
