void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    // Time varying pixel color
    vec3 col = ( 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)) ) * 0.3;

    col += createLandscape_base( uv, col ) * 0.2;
    col += createLandscape2( uv, col ) * 0.2;
    col += createLandscape3( uv, col ) * 0.2;
    col += createSun( uv, col );

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.141592

const vec3 a = vec3(0.500,  0.400, 0.500);
const vec3 b = vec3(3.000,  0.100, 3.000);
const vec3 c = vec3(0.500,  1.000, 0.500);
const vec3 d = vec3(0.000, -0.500, 0.500);

vec3 palette( float t ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 rotate(vec2 uv, float th)
{
    return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

float sdfCircle(vec2 uv, float r, vec2 offset)
{
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;

    return length(vec2(x, y)) - r;
}

float sdfSun(vec2 uv, vec2 center)
{
    float sun = sdfCircle(uv, 0.1, center);
    return smoothstep(-.01, .01, sun);
}

float sdfMoon(vec2 uv, vec2 center)
{
    float d1 = sdfCircle(uv, 0.1, center);
    float d2 = sdfCircle(
        uv, 0.09,
        center + rotate(vec2(0.04, 0.00), atan(center.y/center.x))
        );

    float res; // result
    res = max(d1, -d2); // subtraction - subtract d1 from d2

    res = smoothstep(-0.01, 0.01, res); // Same as res > 0. ? 1. : 0.;

    return res;
}

vec3 getSunglow(vec2 uv, vec2 center)
{
    vec3 gradientStartColor = vec3(1., 0., 1.);
    vec3 gradientEndColor = vec3(0., .7, .7);
    return mix(
        gradientStartColor,
        gradientEndColor,
        1.2*distance(uv, center));
}

vec3 getMoonglow(vec2 uv, vec2 center)
{
    vec3 gradientStartColor = vec3(.0, .0, .8);
    vec3 gradientEndColor = vec3(0., .7, .7);
    return mix(
        gradientStartColor,
        gradientEndColor,
        1.2*distance(uv, center));
}

float sdfSea(vec2 uv)
{
    float noise = 0.005*sin(uv.x+.2*iTime)+0.0075*sin(uv.x+0.3*iTime)
                    + 0.0015*cos(uv.x+.25*iTime)+0.00125*sin(uv.x+0.15*iTime);
    return smoothstep(-0.01, 0.01, uv.y + .25 + noise);
}

vec3 drawScene(vec2 uv)
{
    float t = .2*iTime-.5;
    vec2 offsetSun = vec2(-0.5*cos(PI*t), -0.5+0.5*sin(PI*t));
    vec2 offsetMoon = vec2(-0.5*cos(PI*t+PI), -0.5+0.5*sin(PI*t+PI));

    // gradient background of Sun and Moon
    vec3 col = getSunglow(uv, offsetSun);
    // dot operation cuts daynight cycle in half (night<0.0, day>0.0)
    col = mix(
        col,
        getMoonglow(uv, offsetMoon),
        smoothstep(-1., 1., dot(uv+vec2(0.0, .75), -offsetSun+offsetMoon))
        );

    // sun
    float sun = sdfSun(uv, offsetSun);
    vec3 colorSun = vec3(1.0, 0.2, 0.1);
    col = mix(colorSun, col, sun);

    // moon
    float moon = sdfMoon(uv, offsetMoon);
    vec3 colorMoon = vec3(0.0); // change color
    col = mix(colorMoon, col, moon);

    float ground = sdfSea(uv);
    float paletteT = .5 + .2*sin(PI*t+PI);


    col = mix(palette(paletteT), col, ground);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // <0, 1>
    uv -= 0.5; // <-0.5,0.5>
    uv.x *= iResolution.x/iResolution.y; // fix aspect ratio



    vec3 col = drawScene(uv);


    // Output to screen
    fragColor = vec4(col,1.0);
}

/* -----------------------------------------------
  Shadertoy Unit: Image
  Description: None
  iChannel0: Buffer A
  iChannel1: None
  iChannel2: None
  iChannel3: None
----------------------------------------------- */

vec2 screen_to_uv(vec2 coord)
{
    return (2.0 * coord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

vec3 view_transform(vec3 col)
{
	// OETF (Gamma)
    return pow(col, vec3(1.0 / 2.5));
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // UV
    vec2 uv = screen_to_uv(frag_coord);

    // Render
    vec3 col = vec3(0.0);
    if (max(abs(uv.x), abs(uv.y)) < 1.0)
    {
        col = texture(iChannel0, vec2(uv * 0.5 + 0.5) * vec2(fft_res) / iResolution.xy).rgb;

        vec3 tint = rgb_sweep(iTime * 0.14) + 0.03;
        tint /= dot(tint, vec3(0.3, 0.59, 0.12));
        col *= tint;
    }
    else
    {
        col = vec3(0);
        //col = vec3(0.005, 0.01, 0.02);
    }

    // Output
    col = view_transform(col);
    frag_col = vec4(col, 1.0);
}

