#define PI 3.141592
#define EPSILON .004

struct Source
{
    float c; // charge
    vec2 r;  // position
};

Source sources[4] = Source[4](
    Source(1.0, vec2(0.0, -0.1)),
    Source(-1.0, vec2(0.0, 0.1)),
    Source(-1.0, vec2(-0.1, 0.0)),
    Source(-1.0, vec2(0.1, 0.0))
    );

const vec3 a = vec3(0.5, 0.5, 0.5);
const vec3 b = vec3(0.5, 0.5, 0.5);
const vec3 c = vec3(1.0, 1.0, 1.0);
const vec3 d = vec3(0.263,0.416,0.557);

vec3 palette( float t ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 rotate(vec2 uv, float th)
{
    return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // <0, 1>
    uv -= 0.5; // <-0.5,0.5>
    uv.x *= iResolution.x/iResolution.y; // fix aspect ratio


    vec3 col = vec3(0.0);
    float force_const = 0.01;
    float force = 0.0;
    float t = 0.2*iTime;

    for (int i = 0; i < sources.length(); i++)
    {
        sources[i].r = rotate(sources[i].r, 2.0*PI*sin(t));

        vec2 r   = sources[i].r - uv;
        force += sin(t)*sources[i].c*force_const / (dot(r, r) + EPSILON);
    }

    col = palette(force);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// CC0: Avoiding overestimation 
// Inspired by: https://www.shadertoy.com/view/WtXcWB

// Watched blackie's youtube on the estimating the distance field of grid
// where each object in the cell is differently sized which typically leads
// to artifacts (or fine tuning of the step size).

// I thought I try adapt the approach to also utilize the ray direction as
// I figured that it's only in the ray direction we have to make sure not
// to over estimate

// Like blackie I try to compute the nearest neighbour but with the twist 
// to only consider cells in the ray direction.

// In addition, it turned out that I needed to also consider the cells that 
// touch the corner to not overestimate when I get in the corner near the edges

// My hope is that this also eliminates artifacts as well as being cheap enough
// to be useful

// I thought it was rather interesting to do so sharing in case some find 
// something useful in the idea.

#define TIME_MULT   0.0

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define SCA(a)      vec2(sin(a), cos(a))
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float pie( in vec2 p, in vec2 c, in float r ) {
  p.x = abs(p.x);
  float l = length(p) - r;
  float m = length(p-c*clamp(dot(p,c),0.0,r)); // c=sin/cos of aperture
  return max(l,m*sign(c.y*p.x-c.x*p.y));
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float segment(vec2 p, vec2 a, vec2 b ) {
  vec2 pa = p-a, ba = b-a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h );
}

const float z = 0.2;
// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  co += 123.4;
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

vec2 g_thing_edge    = vec2(0.0);
vec2 g_thing_ird    = vec2(0.0);
vec3 g_thing_step   = vec3(0.0);
mat2 g_thing_rot    = mat2(1.0);
vec2 g_ro     = vec2(0.0);
vec2 g_la     = vec2(0.0);
mat2 g_pie_rot= mat2(1.0);

float dobj(vec2 p, float h) {
  float w = 0.45*mix(0.1, 1.0, h);
  //return box(p, vec2(w-0.1))-0.1;
  return length(p)-w;
}

float drep(vec2 p) {
  p -= 0.5;

  // Compute the the object in the current cell
  vec2 c0   = round(p);
  float h0  = hash(c0);
  vec2 p0   = p-c0;

  // Let's figure out the which adjacent cell that is the nearest with respect to the ray direction
  vec2 t1   = (g_thing_edge-p0)*g_thing_ird;
  vec2 c1   = c0 + (t1.x < t1.y ? g_thing_step.xz : g_thing_step.zy); 
  float h1  = hash(c1);
  vec2 p1   = p-c1;

  // Also consider the diagonal cell for those cases when we step past the adjacent cell
  vec2 c2 = c0 + g_thing_step.xy;
  float h2  = hash(c2);
  vec2 p2   = p-c2;

  float d0 = dobj(p0, h0);
  float d1 = dobj(p1, h1);
  float d2 = dobj(p2, h2);

  float d = d0;
  d = min(d, d1);
  d = min(d, d2);
  return d;
}

float dthing(vec2 p) {
  p *= g_thing_rot;
  float d0 = drep(p/z)*z;
  float d1 = box(p, vec2(4.0*z));
  float d = d0;
  d = max(d, d1);
  return d;
}

vec4 df(vec2 p) {
  const vec2 cone = SCA(PI/4.0);
  float d0 = dthing(p);
  float r1 = dthing(g_ro);
  float d1 = pie(g_pie_rot*(p-g_ro), cone, r1);
  float d2 = length(p-g_la)-0.1;
  d2 = abs(d2) - 0.005;
  float d3 = segment(p, g_ro, g_la)-0.005;
  vec4 d = vec4(d0, d1, d2, d3);
  return d;
}

vec3 effect(vec2 p) {
  float aa      = 4.0/RESOLUTION.y;
  float tm      = TIME_MULT*TIME;
  mat2 rot      = ROT(sin(tm)+tm);

  vec2 rd       = (normalize(g_la-g_ro));

  g_pie_rot     = ROT(PI/2.0)*transpose(mat2(rd.x, rd.y, -rd.y, rd.x));
  rd *= rot;
  vec2 srd      = sign(rd);

  g_thing_rot   = rot;
  g_thing_edge  = srd*vec2(0.5);
  g_thing_step  = vec3(srd, 0.0)*vec3(1.0, 1.0, 0.0);
  g_thing_ird   = 1.0/rd;

  vec4 d = df(p);
  vec3 col = vec3(0.0);
  const vec3 bcol = vec3(1.0, 0.125, 0.25); 
  vec3 gcol = mix(sqrt(bcol), sqrt(bcol).yzx, 0.5*(sign(-d.x)+1.0));
  col += gcol*mix(0.125, 1.0, smoothstep(-0.5, 0.5, sin(TAU*d.x/(3.0*aa))))*mix(0.125, 1.0, exp(-20.0*abs(d.x)));
  col = mix(col, bcol.xxx, smoothstep(0.0, -aa, abs(d.x)-aa));
  col = mix(col, bcol.yzx, smoothstep(0.0, -aa, d.w));
  col = mix(col, bcol.zxy, smoothstep(0.0, -aa, d.z));
  col = mix(col, bcol.xzy, smoothstep(0.0, -aa, d.y));
  col = sqrt(col);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  float r = RESOLUTION.x/RESOLUTION.y; 
  p.x *= r;
  g_ro = (-1.0+2.0*iMouse.xy/RESOLUTION.xy)*vec2(r, 1.0);
  vec3 col = effect(p);
  fragColor = vec4(col, 1.0);
}

#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define deg45 .707
#define R45(p) (( p + vec2(p.y,-p.x) ) *deg45)
#define Tri(p,s) max(R45(p).x,max(R45(p).y,B(p,s)))
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )
#define SkewX(a) mat2(1.0,tan(a),0.0,1.0)
#define seg_0 0
#define seg_1 1
#define seg_2 2
#define seg_3 3
#define seg_4 4
#define seg_5 5
#define seg_6 6
#define seg_7 7
#define seg_8 8
#define seg_9 9

float segBase(vec2 p){
    vec2 prevP = p;

    float size = 0.02;
    float padding = 0.05;

    float w = padding*3.0;
    float h = padding*5.0;

    p = mod(p,0.05)-0.025;
    float thickness = 0.005;
    float gridMask = min(abs(p.x)-thickness,abs(p.y)-thickness);

    p = prevP;
    float d = B(p,vec2(w*0.5,h*0.5));
    return d;
}

float seg0(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    float mask = B(p,vec2(size,size*2.7));
    d = max(-mask,d);
    return d;
}

float seg1(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.x+=size;
    p.y+=size;
    float mask = B(p,vec2(size*2.,size*3.7));
    d = max(-mask,d);

    p = prevP;

    p.x+=size*1.9;
    p.y-=size*3.2;
    mask = B(p,vec2(size,size+0.01));
    d = max(-mask,d);

    return d;
}

float seg2(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.x+=size;
    p.y-=0.05;
    float mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    p = prevP;
    p.x-=size;
    p.y+=0.05;
    mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    return d;
}

float seg3(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.y = abs(p.y);
    p.x+=size;
    p.y-=0.05;
    float mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    p = prevP;
    p.x+=0.06;
    mask = B(p,vec2(size,size+0.01));
    d = max(-mask,d);

    return d;
}

float seg4(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;

    p.x+=size;
    p.y+=0.08;
    float mask = B(p,vec2(size*2.,size*2.0));
    d = max(-mask,d);

    p = prevP;

    p.y-=0.08;
    mask = B(p,vec2(size,size*2.0));
    d = max(-mask,d);

    return d;
}

float seg5(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.x-=size;
    p.y-=0.05;
    float mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    p = prevP;
    p.x+=size;
    p.y+=0.05;
    mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    return d;
}

float seg6(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.x-=size;
    p.y-=0.05;
    float mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    p = prevP;
    p.y+=0.05;
    mask = B(p,vec2(size,size));
    d = max(-mask,d);

    return d;
}

float seg7(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.x+=size;
    p.y+=size;
    float mask = B(p,vec2(size*2.,size*3.7));
    d = max(-mask,d);
    return d;
}


float seg8(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.y = abs(p.y);
    p.y-=0.05;
    float mask = B(p,vec2(size,size));
    d = max(-mask,d);

    return d;
}

float seg9(vec2 p){
    vec2 prevP = p;
    float d = segBase(p);
    float size = 0.03;
    p.y-=0.05;
    float mask = B(p,vec2(size,size));
    d = max(-mask,d);

    p = prevP;
    p.x+=size;
    p.y+=0.05;
    mask = B(p,vec2(size*2.,size));
    d = max(-mask,d);

    return d;
}

float segDot(vec2 p){
    vec2 prevP = p;
    p*=SkewX(-0.4);
    float size = 0.03;
    p.y = abs(p.y)-0.07;
    float d = B(p,vec2(size));
    return d;
}

float checkChar(int targetChar, int char){
    return 1.-abs(sign(float(targetChar) - float(char)));
}

float drawFont(vec2 p, int char){
    p*=SkewX(-0.4);
    float d = seg0(p)*checkChar(seg_0,char);
    d += seg1(p)*checkChar(seg_1,char);
    d += seg2(p)*checkChar(seg_2,char);
    d += seg3(p)*checkChar(seg_3,char);
    d += seg4(p)*checkChar(seg_4,char);
    d += seg5(p)*checkChar(seg_5,char);
    d += seg6(p)*checkChar(seg_6,char);
    d += seg7(p)*checkChar(seg_7,char);
    d += seg8(p)*checkChar(seg_8,char);
    d += seg9(p)*checkChar(seg_9,char);

    return d;
}

float random (vec2 p) {
    return fract(sin(dot(p.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

// principal value of logarithm of z
// https://gist.github.com/ikr7/d31b0ead87c73e6378e6911e85661b93
vec2 clog (vec2 z) {
	return vec2(log(length(z)), atan(z.y, z.x));
}

// The following code will return the Droste Zoom UV.
// by roywig https://www.shadertoy.com/view/Ml33R7
vec2 drosteUV(vec2 p){
    float speed = 0.25;
    float animate = mod(iTime*speed,2.07);
    float rate = sin(iTime*0.5);
    //p = clog(p)*mat2(1,.11,rate*0.5,1);
    p = clog(p);
    p = exp(p.x-animate) * vec2( cos(p.y), sin(p.y));
    vec2 c = abs(p);
    vec2 duv = .5+p*exp2(ceil(-log2(max(c.y,c.x))-2.));
    return duv;
}

// circle animation
float widgetItem0(vec2 p, float rval){
    vec2 prevP = p;

    float d = abs(length(p)-0.12)-0.005;
    p*=Rot(radians(iTime*60.*rval));
    d = max((abs(p.y)-0.05),d);
    float d2 = abs(length(p)-0.12)-0.03;
    d2 = max(-(abs(p.y)-0.05),d2);
    d = min(d,abs(d2)-0.005);

    d2 = abs(length(p)-0.03)-0.005;
    d = min(d,d2);

    p.y = abs(p.y)-0.135;
    d2 = Tri(p,vec3(0.03));
    d = min(d,d2);

    p = prevP;
    p*=Rot(radians(sin(iTime*0.5)*-100.));
    p.y = abs(p.y)-0.175;
    p.y*=-1.;
    d2 = abs(Tri(p,vec3(0.04)))-0.005;
    d = min(d,d2);    

    return d;
}

// clock animation
float widgetItem1(vec2 p){
    vec2 prevP = p;

    p*=Rot(radians(iTime*30.));
    p = DF(p,8.);
    p-=0.25;
    p*=Rot(radians(45.));
    float d = B(p,vec2(0.005,0.03)); 

    p = prevP;

    p*=Rot(radians(iTime*30.));
    p = DF(p,4.);
    p-=0.27;
    p*=Rot(radians(45.));
    float d2 = B(p,vec2(0.005,0.05));     

    d = min(d,d2);

    p = prevP;

    p*=Rot(radians(sin(iTime)*180.));
    p.y = abs(p.y);
    p.y-=0.28;
    p.y*=0.07;
    d2 = Tri(p,vec2(0.05));
    d = min(d,d2);

    p = prevP;
    d2 = length(p)-0.03;
    d = min(d,d2);

    return d;
}

// digit number animation
float widgetItem2(vec2 p){
    vec2 prevP = p;
    p*=1.05;
    float d = drawFont(p-vec2(-0.35,0.0),int(mod(iTime,9.)));
    float d2 = drawFont(p-vec2(-0.15,0.0),int(mod(iTime*2.,9.)));
    d = min(d,d2);
    d2 = drawFont(p-vec2(0.15,0.0),int(mod(iTime*15.,9.)));
    d = min(d,d2);
    d2 = drawFont(p-vec2(0.35,0.0),int(mod(iTime*30.,9.)));
    d = min(d,d2);
    d2 = segDot(p);
    d = min(d,d2);
    return abs(d)-0.002;
}

// slider animation
float widgetItem3(vec2 p){
    vec2 prevP = p;
    float d = B(p,vec2(0.42,0.002));
    p.x += sin(iTime)*0.35;
    d = max(-(length(p)-0.02),d);
    p = prevP;
    p.x = abs(p.x)-0.42;
    float d2 = B(p,vec2(0.002,0.03));
    d = min(d,d2);
    p = prevP;
    p.x += sin(iTime)*0.35;
    d2 = abs(length(p)-0.02)-0.002;
    d = min(d,d2);

    p = prevP;
    p.x-=iTime*0.1;
    p.x = mod(p.x,0.06)-0.03;
    d2 = length(p)-0.008;
    p = prevP;
    d2 = max((abs(p.x)-0.4),d2);
    p.x += sin(iTime)*0.35;
    d2 = max(-(length(p)-0.02),d2);
    d = min(d,d2);


    p = prevP;
    p*=15.;
    p.x+=iTime*1.5;
    d2 = sin(p.y*0.6)*0.23+cos(p.x*1.5)*0.2;
    d2 = abs(d2)-0.005;
    p = prevP;
    d2 = max(abs(p.x)-0.4,d2);
    d2 = max(abs(p.y)-0.2,d2);
    d = min(d,d2);

    return d;
}

float widgetItem4(vec2 p){
    vec2 prevP = p;
    float d = B(p,vec2(0.42,0.002));
    p.x = abs(p.x)-0.42;
    float d2 = B(p,vec2(0.002,0.05));
    d = min(d,d2);

    p = prevP;
    p.x-=iTime*0.1;
    p.x = mod(p.x,0.06)-0.03;
    d2 = B(p,vec2(0.002,0.025));
    p = prevP;
    d2 = max((abs(p.x)-0.4),d2);
    d = min(d,d2);

    p.x -= sin(iTime*1.1)*-0.35;
    p.y = abs(p.y)-0.05;
    p.y*=-1.;
    d2 = abs(Tri(p,vec2(0.03)))-0.002;
    d = min(d,d2);

    return d;
}

float widgetItem5(vec2 p){
    vec2 prevP = p;
    float d = B(p,vec2(0.002,0.42));
    p.y -= sin(iTime)*0.33;
    p*=Rot(radians(45.));
    d = max(-B(p,vec2(0.03)),d);
    p = prevP;
    p.y = abs(p.y)-0.42;
    float d2 = B(p,vec2(0.03,0.002));
    d = min(d,d2);
    p = prevP;
    p.y -= sin(iTime)*0.33;
    p*=Rot(radians(45.));
    d2 = abs(B(p,vec2(0.03)))-0.002;
    d = min(d,d2);
    return d;
}

float widgetItem6(vec2 p){
    vec2 prevP = p;
    float d = B(p,vec2(0.002,0.42));
    p.y = abs(p.y)-0.42;
    float d2 = B(p,vec2(0.05,0.002));
    d = min(d,d2);

    p = prevP;
    p.y+=iTime*0.1;
    p.y = mod(p.y,0.06)-0.03;
    d2 = B(p,vec2(0.025,0.002));
    p = prevP;
    d2 = max((abs(p.y)-0.4),d2);
    d = min(d,d2);

    p.y -= sin(iTime*1.1)*-0.35;
    p.x = abs(p.x)-0.05;
    p*=Rot(radians(-90.));
    d2 = abs(Tri(p,vec2(0.03)))-0.002;
    d = min(d,d2);

    return d;
}

float pattern1(vec2 p){
    float d = abs(B(p-vec2(0.002),vec2(0.5)))-0.004;

    float d2 = widgetItem1(p);
    d = min(d,d2);
    return d;
}

float pattern2(vec2 p){
    float d = abs(B(p-vec2(0.002,0.002+0.25),vec2(0.5,0.25)))-0.004;
    float d2 = abs(B(p-vec2(0.002,0.002-0.25),vec2(0.5,0.25)))-0.004;
    d = min(d,d2);

    d2 = widgetItem3(p-vec2(0,0.25));
    d = min(d,d2);
    d2 = widgetItem4(p-vec2(0,-0.25));
    d = min(d,d2);

    return d;
}

float pattern3(vec2 p){
    float d = abs(B(p-vec2(0.002+0.25,0.002),vec2(0.25,0.5)))-0.004;
    float d2 = abs(B(p-vec2(0.002-0.25,0.002),vec2(0.25,0.5)))-0.004;

    d = min(d,d2);

    d2 = length(p-vec2(0.25,0.0))-0.2;
    d2 = widgetItem5(p-vec2(0.25,0.0));
    d = min(d,d2);
    d2 = length(p-vec2(-0.25,0.0))-0.2;
    d2 = widgetItem6(p-vec2(-0.25,0.0));
    d = min(d,d2);    

    return d;
}

float pattern4(vec2 p, float rval){
    vec2 prevP = p;
    float d = abs(B(p-vec2(0.002+0.25,0.002+0.25),vec2(0.25)))-0.004;
    float d2 = abs(B(p-vec2(0.002-0.25,0.002+0.25),vec2(0.25)))-0.004;
    d = min(d,d2);
    d2 = abs(B(p-vec2(0.002,0.002-0.25),vec2(0.5,0.25)))-0.004;
    d = min(d,d2);

    p.x = abs(p.x);
    p.x-=0.25;
    p*= vec2(sign(prevP.x),1);
    d2 = widgetItem0(p-vec2(0.0,0.25),-rval);
    d = min(d,d2);

    p = prevP;
    d2 = widgetItem2(p-vec2(0.0,-0.25));
    d = min(d,d2);  

    return min(d,d2);
}

float pattern5(vec2 p, float rval){
    vec2 prevP = p;
    float d = abs(B(p-vec2(0.002+0.25,0.002-0.25),vec2(0.25)))-0.004;
    float d2 = abs(B(p-vec2(0.002-0.25,0.002-0.25),vec2(0.25)))-0.004;
    d = min(d,d2);
    d2 = abs(B(p-vec2(0.002,0.002+0.25),vec2(0.5,0.25)))-0.004;
    d = min(d,d2);

    p.x = abs(p.x);
    p.x-=0.25;
    p*= vec2(sign(prevP.x),1);
    d2 = widgetItem0(p-vec2(0.0,-0.25),-rval);
    d = min(d,d2);

    p = prevP;
    d2 = widgetItem2(p-vec2(0.0,0.25));
    d = min(d,d2);  

    return min(d,d2);
}

vec3 draw(vec2 p, vec3 col, float size){
    vec2 prevP = p;

    p*=size;
    vec2 id = floor(p);
    vec2 gr = fract(p)-0.5;
    float n = random(id);

    if(n<0.2){
        float d = pattern1(gr);
        col = mix(col, vec3(1.), S(d,0.0));
    } else if(n>=0.2 && n<0.35){
        float d = pattern2(gr);
        col = mix(col, vec3(1.), S(d,0.0));
    } else if(n>=0.35 && n<0.5){
        float d = pattern3(gr);
        col = mix(col, vec3(1.), S(d,0.0));
    } else if(n>=0.5 && n<0.75){
        float d = pattern4(gr,n);
        col = mix(col, vec3(1.), S(d,0.0));
    } else {
        float d = pattern5(gr,n);
        col = mix(col, vec3(1.), S(d,0.0));
    }

    return col;
}

