void CameraOrbitRay(in vec2 fragCoord, in float n, in vec3 c, in float d, float zmod, int view,
                    out vec3 ro, out vec3 rd, out mat3 t) 
{
    float yp=-3., xrot=-0.66;
    if (view == 1) yp=0., xrot=0.0;
    if (view == 2) yp=0., xrot=1.;

    float a = 1.0/max(iResolution.x, iResolution.y);
    rd = normalize(vec3((fragCoord - iResolution.xy*0.5)*a, n));

    ro = vec3(0.0, yp, -d+zmod);

    float mxc, myc;
    if (iMouse.z > 0.) {
        mxc = clamp(-((iMouse.x / iResolution.x)*2.0 - 1.0), -1.0, 1.0);
        myc = clamp((iMouse.y / iResolution.y)*2.0 - 1.0, -1.0, 1.0);
    } else {
        mxc=sin(sin(iTime*0.33))*1.17;
        myc = xrot;
    }

    float mxs = sqrt(1.0-(mxc*mxc));
    float mys = sqrt(1.0-(myc*myc));

    t = mat3(mxc, mxs, 0, -mxs*myc, mxc*myc, mys, mxs*mys, -mxc*mys, myc);

    ro = t * ro;
    ro = c + ro;

    rd = t * rd;
    rd = normalize(rd);
}

vec3 LightDir(in mat3 t) 
{
    vec3 l = normalize(vec3(-1.0, -1.0, 1.0));
    return t * l;
}

