#define SIZE 125.

const int CPP = 24; // Cells Per Pixel (only 6,12,18,24 will work)

#define HORIZON

const float LAST_PATT = 23.01;

int liveset, bornset, clearmethod, countmethod, withermethod;
float liveval;
float radius;
float density;
int speedplus, wrap;

void setRules(int index) {
    int v1, v2;
    countmethod=0, clearmethod=0, withermethod=-1;
    speedplus=0, wrap=1;

    if (index == 0) {  liveval=10.; v1=240, v2=448; radius=6.; density=0.4;  withermethod=8; }                          // pyroclastic
    if (index == 1) {  countmethod=1; liveval=2.; v1=127, v2=10; clearmethod=1; radius=1.0; density=1.0; speedplus=4; wrap=0; } // crystal
    if (index == 2) {  liveval=8.;  v1=5382088, v2=9701408; radius=8.0; density=0.5; speedplus=-1; wrap=0; }            // amoeba II
    if (index == 3) {  liveval=2.;  v1=134209536, v2=942080; clearmethod=2; density=0.499; wrap=1; }                    // clouds
    if (index == 4) {  liveval=2.;  v1=593808, v2=2195520; radius=35.0; density=0.3; wrap=0; }                          // slowburning star
    if (index == 5) {  liveval=4.;  v1=134217600, v2=16; clearmethod=1; radius=12.0; density=0.5; wrap=1; }             // struct builder
    if (index == 6) {  liveval=5.;  v1=371520, v2=63793088; clearmethod=1; radius=6.0; density=0.6; }                   // expanding shells
    if (index == 7) {  liveval=2.;  v1=0, v2=2; clearmethod=1; radius=1.0; density=1.0; speedplus=20; wrap=0; }         // single point
    if (index == 8) {  liveval=10.; v1=580, v2=848; radius=13.; density=0.1;  withermethod=0; }                         // builder
    if (index == 9) {  liveval=5.;  v1=16, v2=16; clearmethod=1; radius=15.0; density=0.4; }                            // 445
    if (index == 10) { countmethod=1; liveval=2.; v1=127, v2=10; clearmethod=1; radius=4.0; density=1.0; speedplus=4;wrap=0; } // crystal (same, radius change)
    if (index == 11) { liveval=3.;  v1=448, v2=448; radius=24.0; density=0.4; wrap=0;}                                  // 678
    if (index == 12) { liveval=10.; v1=5382088, v2=9701408; radius=10.0; density=0.5; speedplus=-1; wrap=0; }           // amoeba II slow
    if (index == 13) { liveval=7.;  v1=148064, v2=53477506; radius=1.0; clearmethod=1; density=1.; speedplus=12; }      // build cube
    if (index == 14) { liveval=8.;  v1=21635016, v2=1052704; clearmethod=2; density=0.5; wrap=0; }                      // wither
    if (index == 15) { liveval=11.; v1=65646754, v2=1123384; radius=7.; density=0.5; wrap=0; }                          // constant explosions
    if (index == 16) { liveval=2.;  v1=134209536, v2=942080; clearmethod=2; density=0.515; wrap=0; }                    // clouds II
    if (index == 17) { liveval=2.;  v1=134209536, v2=942080; clearmethod=2; density=0.58; wrap=0; }                     // clouds III (funkycube)

    if (index == 18) { liveval=4.;  v1=134217600, v2=16; clearmethod=1; radius=17.0; density=0.5; wrap=0; }             // inv. struct builder
    if (index == 19) { liveval=8.;  v1=5382088, v2=9701408; radius=10.0; density=0.5; speedplus=-1; wrap=0; }           // inv. amoeba II
    if (index == 20) { liveval=5.;  v1=16, v2=16; clearmethod=1; radius=20.0; density=0.4; }                            // inv. 445
    if (index == 21) { liveval=2.;  v1=134209536, v2=942080; clearmethod=2; density=0.52; wrap=1; }                     // inv. clouds
    if (index == 22) { liveval=5.;  v1=134217216, v2=45280; clearmethod=0; radius=10.; density=0.8; wrap=0; }           // inv. amoeba I
    if (index == 23) { liveval=5.;  v1=38553186, v2=2506768; clearmethod=0; radius=14.; density=0.58; wrap=0; }         // inv. little puffs

    if (index==1 || index==10 || index==13) withermethod=4;
    if (index==2 || index==15) withermethod=14;

    liveset = v1; bornset = v2;
}

bool getInverted(int index) {
    return index >= 18;
}

