void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv=fragCoord/iResolution.xy*2.0-1.0;
    uv.x*=iResolution.x/iResolution.y;
    float d=length(uv);
    vec3 color=vec3(1.0,2.0,3.0);
    d=sin(d*4.0-iTime)/4.;
    d=abs(d);
    d=0.005/d;
    color*=d;
    fragColor = vec4(color,1.0);
}

const float music_length=120.0;
const float blur_turn = 13.0;
const float fading_turn=14.0;
const float first_turn=16.0;
const float end_turn=20.0;


/////////////////////////////////////////////////////////////////////////////
// Add the original image to the blurred image to get the final image.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     float t = mod(iTime, music_length);
     // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = fragCoord/iResolution.xy;

    //The range of this pix fit the OriginalImageTex's size,
    //Use the texel coordinate of gl_fragcoord and texelFetch can imporve the accuracy
    vec4 origin = texture(iChannel0,uv);

    //The size of the blurred threshold image is smaller,
    //Use the texCoord and texture() to sample the corresponding value;
    vec4 color = texture(iChannel1,uv);

     fragColor = origin + color;
    if(t>blur_turn && t<fading_turn){    
        fragColor =origin + color*100.0*((t-blur_turn)/(fading_turn-blur_turn)) ;
    }
    if(t>fading_turn && t<first_turn){    
        fragColor = (origin + color*100.0)*(1.0-(t-fading_turn)/(fading_turn-blur_turn)) ;
    }
    if(t> first_turn && t<end_turn){    
        fragColor = (origin + color)*((t-first_turn)/(end_turn-first_turn)) ;
    }

}

