const int blurFilterWidth = 51;
const int halfWidth = 25;
float weights[blurFilterWidth];

/////////////////////////////////////////////////////////////////////////////
// 1D gaussian function.
/////////////////////////////////////////////////////////////////////////////
float gauss(float x, float sigma2)
{
    const float PI = 3.1415926535897932384626433832795;
    float coeff = 1.0 / sqrt(2.0 * PI * sigma2);
    float expon = -(x * x) / (2.0 * sigma2);
    return (coeff * exp(expon));
}

/////////////////////////////////////////////////////////////////////////////
// Set up 1D texture map that contains the 1D blur filter.
/////////////////////////////////////////////////////////////////////////////
void SetUpBlurFilterTexture()
{
    float sigma = float(halfWidth) / 2.0;  // Filter spans over 4 sigma (standard deviation).
    float sigma2 = sigma * sigma;

    float sum = 0.0;

    for (int i = 0; i < blurFilterWidth; i++) {
        float offset=float(i) - float(halfWidth);
        weights[i] = gauss(offset, sigma2);
        sum += weights[i];
    }

    for (int i = 0; i < blurFilterWidth; i++) {
        weights[i] = weights[i] / sum;
    }
}


/////////////////////////////////////////////////////////////////////////////
// Apply horizontal blurring to the threshold image.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = fragCoord/iResolution.xy;

    //After getting the fragment coordinate of fragment,
    //Use the middle value of the blurFilterTex on the beginning fragment;
    ivec2 pix = ivec2(fragCoord.xy);
    SetUpBlurFilterTexture();

    vec4 sum = texture(iChannel0, uv) * weights[halfWidth];

    //Apply the 1D Gaussian 
    for( int i = 1; i < halfWidth+1; i++ ) {
        ivec2 offsetCoord = ivec2(pix.x + i, pix.y);
        sum += texelFetch(iChannel0, offsetCoord, 0)*weights[halfWidth+i];
        offsetCoord = ivec2(pix.x - i, pix.y);
        sum += texelFetch(iChannel0, offsetCoord, 0)*weights[halfWidth-i];
    }

    //fragColor =texelFetch(iChannel0, pix, 0);
    //fragColor =texture(iChannel2, uv.xy);
    fragColor = sum;
}

