const int blurFilterWidth = 51;
const int halfWidth = 25;
float weights[blurFilterWidth];

/////////////////////////////////////////////////////////////////////////////
// 1D gaussian function.
/////////////////////////////////////////////////////////////////////////////
float gauss(float x, float sigma2)
{
    const float PI = 3.1415926535897932384626433832795;
    float coeff = 1.0 / sqrt(2.0 * PI * sigma2);
    float expon = -(x * x) / (2.0 * sigma2);
    return (coeff * exp(expon));
}

/////////////////////////////////////////////////////////////////////////////
// Set up 1D texture map that contains the 1D blur filter.
/////////////////////////////////////////////////////////////////////////////
void SetUpBlurFilterTexture()
{
    float sigma = float(halfWidth) / 2.0;  // Filter spans over 4 sigma (standard deviation).
    float sigma2 = sigma * sigma;

    float sum = 0.0;

    for (int i = 0; i < blurFilterWidth; i++) {
        float offset=float(i) - float(halfWidth);
        weights[i] = gauss(offset, sigma2);
        sum += weights[i];
    }

    for (int i = 0; i < blurFilterWidth; i++) {
        weights[i] = weights[i] / sum;
    }
}


/////////////////////////////////////////////////////////////////////////////
// Apply horizontal blurring to the threshold image.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

