     vec2 uv = fragCoord/iResolution.xy;

    //After getting the fragment coordinate of fragment,
    //Use the middle value of the blurFilterTex on the beginning fragment;
    ivec2 pix = ivec2(fragCoord.xy);
    SetUpBlurFilterTexture();

    vec4 sum = texture(iChannel0, uv) * weights[halfWidth];

    //Apply the 1D Gaussian 
    for( int i = 1; i < halfWidth+1; i++ ) {
        ivec2 offsetCoord = ivec2(pix.x + i, pix.y);
        sum += texelFetch(iChannel0, offsetCoord, 0)*weights[halfWidth+i];
        offsetCoord = ivec2(pix.x - i, pix.y);
        sum += texelFetch(iChannel0, offsetCoord, 0)*weights[halfWidth-i];
    }

    //fragColor =texelFetch(iChannel0, pix, 0);
    //fragColor =texture(iChannel2, uv.xy);
    fragColor = sum;
}

