/*original  https://www.shadertoy.com/view/XlfGRj */
#define iterations 15
#define formuparam 0.53

#define volsteps 20
#define stepsize 0.3

#define zoom   2.300
#define tile   0.250
#define speed  0.010 

#define brightness 0.0115
#define darkmatter 0.500
#define distfading 0.530
#define saturation 0.750

const float tau = 6.28318530717958647692;
const float music_length=120.0;
const float first_turn=16.0;
const float second_turn=90.0;

// Gamma correction
#define GAMMA (2.2)

vec3 ToLinear( in vec3 col )
{
	// simulate a monitor, converting colour values into light values
	return pow( col, vec3(GAMMA) );
}

vec3 ToGamma( in vec3 col )
{
	// convert back into colour values, so the correct light will come out of the monitor
	return pow( col, vec3(1.0/GAMMA) );
}

vec4 Noise( in ivec2 x )
{
	return texture( iChannel0, (vec2(x)+0.5)/256.0, -100.0 );
}

vec4 Rand( in int x )
{
	vec2 uv;
	uv.x = (float(x)+0.5)/256.0;
	uv.y = (floor(uv.x)+0.5)/256.0;
	return texture( iChannel0, uv, -100.0 );
}


void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    //the music will begin after 2 secs
    float t = mod(iTime, music_length);
    //t=t+first_turn;
      //  t=t+second_turn;

    if(t<=first_turn){
        vec3 ray;
        ray.xy = 2.0*(fragCoord.xy-iResolution.xy*.5)/iResolution.x;
        ray.z = 1.0;

        float offset = t*.5;	
        float speed2 = (cos(offset)+1.0)*2.0;
        float speed3 = speed2+.1;
        offset += sin(offset)*.96;
        offset *= 2.0;


        vec3 col = vec3(0);

        vec3 stp = ray/max(abs(ray.x),abs(ray.y));

        vec3 pos = 2.0*stp+.5;
        for ( int i=0; i < 20; i++ )
        {
            float z = Noise(ivec2(pos.xy)).x;
            z = fract(z-offset);
            float d = 50.0*z-pos.z;
            float w = pow(max(0.0,1.0-8.0*length(fract(pos.xy)-.5)),2.0);
            vec3 c = max(vec3(0),vec3(1.0-abs(d+speed2*.5)/speed3,1.0-abs(d)/speed3,1.0-abs(d-speed2*.5)/speed3));
            col += 1.5*(1.0-z)*c*w;
            pos += stp;
        }

        fragColor = vec4(ToGamma(col),1.0);
    }
    if(t>first_turn){
        vec2 uv=fragCoord.xy/iResolution.xy-.5;
        uv.y*=iResolution.y/iResolution.x;
        vec3 dir=vec3(uv*zoom,1.);
        float time=t*speed+.25;


        float a1=.5+iMouse.x/iResolution.x*2.;
        float a2=.8+iMouse.y/iResolution.y*2.;
        mat2 rot1=mat2(cos(a1),sin(a1),-sin(a1),cos(a1));
        mat2 rot2=mat2(cos(a2),sin(a2),-sin(a2),cos(a2));
        dir.xz*=rot1;
        dir.xy*=rot2;
        vec3 from=vec3(1.,.5,0.5);
        from+=vec3(time*0.,time,time*10.);
        from.xz*=rot1;
        from.xy*=rot2;


        float s=0.1,fade=1.;
        vec3 v=vec3(0.);
        for (int r=0; r<volsteps; r++) {
            vec3 p=sin(fract(from-s*dir)*1.10);
            p = abs(vec3(tile)-mod(p,vec3(tile*2.))); // tiling fold
            float pa,a=pa=0.;
            for (int i=0; i<iterations; i++) { 
                p=abs(p)/dot(p,p*3.1)-formuparam; // the magic formula
                a+=abs(length(p)+pa); // absolute sum of average change
                pa=0.2*fract(abs(length(p/2.)));
            }
                float dm=max(0.,darkmatter-a*a*.001); //dark matter
                a*=a*a; // add contrast
                if (r>10) fade*=1.-dm; // dark matter, don't render near
                v+=vec3(fract(smoothstep(dm,dm*.1,0.)));
                v+=fade;
                v+=vec3(s,1.*fract(s*s),s*s*s)*a*brightness*fade; // coloring based on distance
                fade*=distfading; // distance fading
                s+=stepsize;
            }
            v=mix(vec3(length(v)),v,saturation); //color adjust
            fragColor = vec4(v*0.01,1.);
    }

}

