const float PI = 3.14159265358979323;
const float FOVY = 20.0 * PI / 180.0;

// SDF parameters
const float MAX_DIST = 100.0;
const int MAX_MARCHING_STEPS = 100;
const float EPSILON = 0.001;
const float OFFSET_SURFACE = 10.0; // offset for shadow ray and secondary ray

// shadow parameters 
// greater SHADOW_HARDNESS, harder shadow
const float SHADOW_HARDNESS = 128.0; 

// lava lamp parameters
// number of lava blobs
const uint LAVA_COUNT = 12u;

// normal bump parameters
// clinderical texture projection is used for normal bump
const float THETA0 = -1.7;
const float THETA1 = 0.3;
const float DELTA_THETA = THETA1 - THETA0;
const float HEIGHT0 = 1.175;
const float HEIGHT1 = 1.475;
const float DELTA_HEIGHT = HEIGHT1 - HEIGHT0;

// scene parameters
const int NUM_LIGHTS = 2;
const int NUM_MATERIALS = 5;
const int NUM_ITERATIONS = 2;
const int NUM_DOTS = 90;

// material id
const int LAVA_LAMP_BODY = 1;
const int LAVA_LAMP_GLASS = 2;
const int LAVA_LAMP_LAVA = 3;
const int FLOOR = 4;
const int BACKGROUND = 0;

// random number generator
// generate a random number in [0, 1]
float random(uint seed) {
    seed ^= 2747636419u;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
    return float(seed) / 4294967295.0;
}

// data structures

struct Ray_t {
    vec3 o; // origin
    vec3 d; // direction
};

struct Light_t {
    vec3 position;  // Point light 3D position.
    vec3 I_a;       // For Ambient.
    vec3 I_source;  // For Diffuse and Specular.
};

struct Material_t {
    vec3 k_a;   // Ambient coefficient.
    vec3 k_d;   // Diffuse coefficient.
    vec3 k_r;   // Reflected specular coefficient.
    vec3 k_rg;  // Global reflection coefficient.
    float n;    // The specular reflection exponent. Ranges from 0.0 to 128.0.
};

struct Dot_t {
    vec2 position; // position in screen space [-1, 1]
    float radius;  // radius in screen space
    vec3 color;    // color
};

Light_t Light[NUM_LIGHTS];
Material_t Material[NUM_MATERIALS];
Dot_t Dots[NUM_DOTS];

void InitScene() {

    float radius = 4.0;

    // Light 0.
    Light[0].position = vec3(cos(iTime) * radius, 10.0, sin(iTime) * radius);
    Light[0].I_a = vec3(0.1, 0.1, 0.1);
    Light[0].I_source = vec3(0.4, 0.4, 0.4);

    // Light 1.
    Light[1].position = vec3(8.0, 8.0, 6.0);
    Light[1].I_a = vec3(0.1, 0.1, 0.1);
    Light[1].I_source = vec3(1.0, 1.0, 1.0);

    // background material.
    Material[0].k_d = vec3(1.0, 1.0, 1.0);
    Material[0].k_a = 0.2 * Material[0].k_d;
    Material[0].k_r = 2.0 * Material[0].k_d;
    Material[0].k_rg = 0.01 * Material[0].k_r;
    Material[0].n = 64.0;

    // Silver material.
    Material[1].k_d = vec3(0.5, 0.5, 0.5);
    Material[1].k_a = 0.2 * Material[1].k_d;
    Material[1].k_r = 2.0 * Material[1].k_d;
    Material[1].k_rg = 0.5 * Material[1].k_r;
    Material[1].n = 64.0;

    // Silver material.
    Material[2].k_d = vec3(1.0, 1.0, 1.0);
    Material[2].k_a = 0.2 * Material[2].k_d;
    Material[2].k_r = 2.0 * Material[2].k_d;
    Material[2].k_rg = 0.5 * Material[2].k_r;
    Material[2].n = 64.0;

     // Lava material.
    Material[3].k_d = vec3(0.0, 1.0, 0.0);
    Material[3].k_a = 0.9 * Material[3].k_d;
    Material[3].k_r = vec3(0.0, 1.0, 0.0);
    Material[3].k_rg = 0.5 * Material[3].k_r;
    Material[3].n = 128.0;

    // floor material.
    Material[4].k_d = vec3(0.1, 0.1, 0.1);
    Material[4].k_a = 0.1 * Material[4].k_d;
    Material[4].k_r = vec3(1.0, 1.0, 1.0);
    Material[4].k_rg = 0.9 * Material[4].k_r;
    Material[4].n = 128.0;

    // Dots
    for (uint i = 0u; i < uint(NUM_DOTS); i++) {
        float x = random(i) * 4.0 - 2.0 + 0.05 * sin(iTime + random(i) * 100.0) * sin(iTime + random(i) * 200.0);
        float y = random(i * 2u) * 2.0 - 1.0 + 0.05 * sin(iTime + random(i) * 100.0) * cos(iTime + random(i) * 200.0);

        Dots[i].position = vec2(x, y);
        Dots[i].radius = random(i * 3u) * 0.06 + 0.03;

        float r = random(i + 4u) * (0.4 - 0.2) + 0.2;
        float g = random(i + 3u) * (0.8 - 0.4) + 0.4;
        float b = random(i + 2u) * (1.0 - 0.6) + 0.6;
        Dots[i].color = vec3(r, g, b);
    }

}

//=================== Signed Distance Functions primitives ==================
// reference: https://iquilezles.org/articles/distfunctions

// signed distance functions operations
float sdfUnion(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

float sdfIntersection(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h);
}

// signed distance functions for primitives
float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

float sdPlane(vec3 p, vec3 n, float h) {
    return dot(p, n) + h;
}

float sdRoundBox(vec3 p, vec3 b, float r) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0) - r;
}

float sdRoundCone(vec3 p, float r1, float r2, float h) {
    float b = (r1 - r2) / h;
    float a = sqrt(1.0 - b * b);

    vec2 q = vec2(length(p.xz), p.y);
    float k = dot(q, vec2(-b, a));
    if (k < 0.0)
        return length(q) - r1;
    if (k > a * h)
        return length(q - vec2(0.0, h)) - r2;
    return dot(q, vec2(a, b)) - r1;
}
//===========================================================================

// signed distance function of lava lamp's glass
float sdLavaLampGlass(vec3 p) {
    // side 
    float d0 = sdRoundCone(p, 0.423, 0.15, 1.6);
    float d1 = sdPlane(p, vec3(0.0, -1.0, 0.0), 1.2);
    float d2 = sdPlane(p, vec3(0.0, 1.0, 0.0), -0.0);
    float d3 = sdfIntersection(d0, -d1, 0.01);
    return sdfIntersection(d3, -d2, 0.01);
}

// signed distance function of lava lamp's body
float sdLavaLampBody(vec3 p) {
    // body shape
    float d0 = sdRoundCone(p, 0.423, 0.15, 1.6);

    // top
    float d1 = sdPlane(p, normalize(vec3(0.5, 1.0, 0.5)), -1.3);
    float d2 = sdfIntersection(d0, d1, 0.02);
    float d3 = sdPlane(p, vec3(0.0, -1.0, 0.0), 1.2);
    float d4 = sdfIntersection(d2, d3, 0.01);

    // bottom
    float d5 = sdPlane(p, vec3(0.0, -1.0, 0.0), -0.3);
    float d6 = sdfIntersection(d0, d5, 0.025);
    float d7 = sdPlane(p, vec3(0.0, 1.0, 0.0), -0.0);
    float d8 = sdfIntersection(d6, d7, 0.01);
    float d9 = -sdSphere(p - vec3(0.0, 0.3, 0.0), 0.5);
    float d10 = sdfIntersection(d8, d9, 0.01);

    return min(d4, d10);
}

// signed distance function of lava lamp's with glass
float sdLavaLamp(vec3 p) {
    return min(sdLavaLampBody(p), sdLavaLampGlass(p));
}

// signed distance function of lava lamp's lava
float sdLavaLampLava(vec3 p) {
    float d = MAX_DIST;
    for (uint i = 1u; i <= LAVA_COUNT; i++) {
        vec3 offset = vec3(0.223 * random(uint(i)) * cos(float(i) / float(LAVA_COUNT) * 2.0 * PI + iTime * 0.5), 0.4 * sin(float(i) / float(LAVA_COUNT) * 2.0 * PI + iTime * 1.0) + 0.5, 0.223 * random(uint(i)) * sin(float(i) / float(LAVA_COUNT) * 2.0 * PI + iTime * 0.5));

        float radius = 0.123 * random(uint(i)) / ((abs(offset.y) + 0.95) * (abs(offset.y) + 0.95));

        d = sdfUnion(d, sdSphere(p - offset, radius), 0.2);
    }
    return d;
}

// signed distance function of background
// the background is a round box
float sdBackground(vec3 p) {
    return sdRoundBox(p - vec3(-0.0, -0.5, -0.0), vec3(1.1, 0.01, 1.1), 0.08);
}

// signed distance function of the scene with glass
float sceneSDF(vec3 p) {
    float d0 = min(sdLavaLamp(p), sdLavaLampLava(p));
    float d1 = min(d0, sdBackground(p));
    return d1;
}

// signed distance function of the scene without glass
float sceneSDFWithoutGlass(vec3 p) {
    float d0 = min(sdLavaLampBody(p), sdLavaLampLava(p));
    float d1 = min(d0, sdBackground(p));
    return d1;
}

// get normal of the scene
vec3 getNormal(in vec3 pos, in bool withoutGlass) {
    const float ep = 0.0001;
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    if (withoutGlass)
        return normalize(e.xyy * sceneSDFWithoutGlass(pos + e.xyy * ep) +
            e.yyx * sceneSDFWithoutGlass(pos + e.yyx * ep) +
            e.yxy * sceneSDFWithoutGlass(pos + e.yxy * ep) +
            e.xxx * sceneSDFWithoutGlass(pos + e.xxx * ep));
    else
        return normalize(e.xyy * sceneSDF(pos + e.xyy * ep) +
            e.yyx * sceneSDF(pos + e.yyx * ep) +
            e.yxy * sceneSDF(pos + e.yxy * ep) +
            e.xxx * sceneSDF(pos + e.xxx * ep));
}

// normal bump
vec3 normalBump(vec3 n, vec3 p) {
    // normal bump
    if (p.y <= HEIGHT1 && p.y >= HEIGHT0) {
        // surface's tangent space
        vec3 T = normalize(cross(p, n));
        vec3 B = normalize(cross(n, T));

        // cylindrical texture coordinates
        vec2 textureCord = normalize(p.xz);
        float angle = -atan(textureCord.y, textureCord.x);

        if (angle >= THETA0 && angle <= THETA1) {
            vec2 uv = vec2((angle - THETA0) / DELTA_THETA, (p.y - HEIGHT0) / DELTA_HEIGHT);
            vec3 tangentSpacePerturbation = texture(iChannel0, uv).xyz * 2.0 - 1.0;
            n = normalize(vec3(tangentSpacePerturbation.x * T +
                tangentSpacePerturbation.y * B +
                tangentSpacePerturbation.z * n));

        }
    }
    return n;
}

// get material index of hit point
int getMaterialIndex(in vec3 pos, in bool withoutGlass) {
    // hit lava lamp body
    if (sdLavaLampBody(pos) < EPSILON) {
        return LAVA_LAMP_BODY;
    }
    // hit lava lamp glass
    else if (!withoutGlass && sdLavaLampGlass(pos) < EPSILON) {
        return LAVA_LAMP_GLASS;
    }
    // hit lava lamp lava
    else if (sdLavaLampLava(pos) < EPSILON) {
        return LAVA_LAMP_LAVA;
    }
    // hit floor
    else if (sdBackground(pos) < EPSILON) {
        return FLOOR;
    }
    return BACKGROUND;
}

// soft shadow
// reference: https://iquilezles.org/articles/rmshadows
float softshadow(in Ray_t ray, in bool withoutGlass) {
    float res = 1.0;
    float t = EPSILON;
    for (int i = 0; i < 256 && t < MAX_DIST; i++) {
        float h = withoutGlass ? sceneSDFWithoutGlass(ray.o + ray.d * t) : sceneSDF(ray.o + ray.d * t);
        if (h < EPSILON)
            return 0.0;
        res = min(res, SHADOW_HARDNESS * h / t);
        t += h;
    }
    return res;
}

// Phong lighting
vec3 PhongLighting(
    in vec3 L,
    in vec3 N,
    in vec3 V,
    float shadow,
    in Material_t mat,
    in Light_t light
) {
    vec3 R = reflect(-L, N);
    float N_dot_L = max(0.0, dot(N, L));
    float R_dot_V = max(0.0, dot(R, V));
    float R_dot_V_pow_n = (R_dot_V == 0.0) ? 0.0 : pow(R_dot_V, mat.n);

    return light.I_a * mat.k_a +
        shadow * light.I_source * (mat.k_d * N_dot_L + mat.k_r * R_dot_V_pow_n);
}

// find intersection between ray and scene
bool Intersect(in Ray_t ray, in bool withoutGlass, out vec3 pos, out vec3 normal) {
    float t = 0.0;
    float d = 0.0;

    // ray marching
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        pos = ray.o + t * ray.d;
        d = withoutGlass ? sceneSDFWithoutGlass(pos) : sceneSDF(pos);
        t += d;
        if (d < EPSILON || t > MAX_DIST)
            break;
    }

    // if we hit something
    if (t >= 0.0 && d < EPSILON) {
        pos = ray.o + t * ray.d;
        normal = getNormal(pos, withoutGlass);
        // normal bump
        normal = normalBump(normal, pos);
        return true;
    }

    return false;
}

// cast ray
vec3 castRay(in Ray_t ray, in bool withoutGlass, in bool insideGlass, out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg, out bool hasHitGlass) {
    hasHit = Intersect(ray, withoutGlass && insideGlass, hitPos, hitNormal);
    if (!hasHit)
        return vec3(texture(iChannel1, ray.d)); // environment map

    // shading
    vec3 color = vec3(0.0);
    int materialIndex = getMaterialIndex(hitPos, withoutGlass && insideGlass);
    hasHitGlass = (materialIndex == LAVA_LAMP_GLASS);
    if (hasHitGlass) {
        k_rg = Material[materialIndex].k_rg;
        return color;
    }

    // for each light source, make a shadow ray.
    for (int i = 0; i < NUM_LIGHTS; i++) {
        vec3 L = normalize(Light[i].position - hitPos);
        vec3 V = -ray.d;
        float shadow = softshadow(Ray_t(hitPos + hitNormal * OFFSET_SURFACE * EPSILON, L), withoutGlass);
        // if the shadow ray hits something, then the point is in shadow.
        color += PhongLighting(L, hitNormal, V, shadow, Material[materialIndex], Light[i]);
    }

    k_rg = Material[materialIndex].k_rg;
    return color;
}

// background
vec3 background(vec2 uv) {
    for (int i = 0; i < NUM_DOTS; i++) {
        vec2 dir = uv - Dots[i].position;
        float distence_squr = dot(dir, dir);
        float radius_squr = Dots[i].radius * Dots[i].radius;
        if (distence_squr < radius_squr) {
            return Dots[i].color;
        }
    }
    return vec3(1.0, 0.6, 0.25);
}

float FresnelTerm(vec3 Normal, vec3 ViewDir, float Power) {
    return 0.1 + 0.9 * pow((1.0 - clamp(dot(normalize(Normal), normalize(ViewDir)), 0.0, 1.0)), Power);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    InitScene();

    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    // Position the camera.
    float radius = 7.0 + sin(iTime);
    float starting_angle = -35.0 / 180.0 * PI;
    float ending_angle = 125.0 / 180.0 * PI;
    float time = abs(sin(iTime)) * (ending_angle - starting_angle) + starting_angle;
    vec3 cam_pos = vec3(cos(time) * radius, 4.5, sin(time) * radius);
    vec3 cam_lookat = vec3(0.0, 0.6, 0.0);
    vec3 cam_up_vec = vec3(0.05 * sin(iTime), 1.0, 0.05 * cos(iTime));

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize(cam_pos - cam_lookat);
    vec3 cam_x_axis = normalize(cross(cam_up_vec, cam_z_axis));
    vec3 cam_y_axis = normalize(cross(cam_z_axis, cam_x_axis));

    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay;
    pRay.o = cam_pos;
    pRay.d = normalize(pixel_pos.x * cam_x_axis + pixel_pos.y * cam_y_axis + pixel_pos_z * cam_z_axis);

    // Start Ray Tracing.
    // Use iterations to emulate the recursion.
    vec3 color_without_glass = vec3(0.0);
    vec3 color_with_glass = vec3(0.0);
    vec3 compounded_k_rg = vec3(1.0);
    Ray_t nextRay = pRay;
    bool isGlass = false;
    vec3 glassNormal;
    bool insideGlass = false;

    // ray tracing without glass
    for (int level = 0; level <= NUM_ITERATIONS; level++) {
        bool hasHit, hasHitGlass;
        vec3 hitPos, hitNormal, k_rg;
        vec3 I_local = castRay(nextRay, true, insideGlass, hasHit, hitPos, hitNormal, k_rg, hasHitGlass);

        color_without_glass += compounded_k_rg * I_local;

        // if miss hitting something on primary ray, then use background color
        if (!hasHit) {
            if (level == 0) {
               // color_without_glass = background(pixel_pos);
            }
            break;
        }

        compounded_k_rg *= k_rg;

        if (hasHitGlass) {
            nextRay = Ray_t(hitPos - hitNormal * OFFSET_SURFACE * EPSILON, normalize(refract(nextRay.d, hitNormal, 1.1)));
            insideGlass = !insideGlass;
        } else {
            nextRay = Ray_t(hitPos + hitNormal * OFFSET_SURFACE * EPSILON, normalize(reflect(nextRay.d, hitNormal)));
        }
    }

    // ray tracing with glass
    compounded_k_rg = vec3(1.0);
    nextRay = pRay;
    for (int level = 0; level <= NUM_ITERATIONS; level++) {
        bool hasHit, hasHitGlass;
        vec3 hitPos, hitNormal, k_rg;

        vec3 I_local = castRay(nextRay, false, false, hasHit, hitPos, hitNormal, k_rg, hasHitGlass);

        // if we hit the glass, then we need to calculate the fresnel term
        // and mix the color with the color without glass
        // this requires the hit point normal of the glass
        if (level == 0 && hasHitGlass) {
            isGlass = true;
            glassNormal = hitNormal;
        }

        color_with_glass += compounded_k_rg * I_local;

        // if miss hitting something on primary ray, then use background color
        if (!hasHit) {
            if (level == 0) {
               // color_without_glass = background(pixel_pos);
            }
            break;
        }

        compounded_k_rg *= k_rg;

        nextRay = Ray_t(hitPos + hitNormal * OFFSET_SURFACE * EPSILON, normalize(reflect(nextRay.d, hitNormal)));
    }

    // mix the color with glass and the color without glass
    vec3 I_result;

    if (isGlass) {
        float fresnel = FresnelTerm(glassNormal, -pRay.d, 6.0);
        I_result = fresnel * color_with_glass + (1.0 - fresnel) * color_without_glass;
    } else {
        I_result = color_without_glass;
    }

    fragColor = vec4(I_result, 1.0);
}

