const float LuminanceThreshold = 0.50;

float Luminance(vec3 color) {
    const vec3 LuminanceWeights = vec3(-0.2, 0.9, -0.2);
    return dot(LuminanceWeights, color);
}

vec4 ThresholdImage(in vec2 fragCoord) {
    // step 1: read from the original image texture
    vec4 color = texelFetch(iChannel0, ivec2(fragCoord), 0);
    // step 2: calculate the luminance of the color
    float luminance = Luminance(color.rgb);
    // step 3: threshold the luminance
    if (luminance >= LuminanceThreshold) {
        return color;
    } else {
        return vec4(0.0, 0.0, 0.0, 1.0);
    }

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = ThresholdImage(fragCoord);
}

