};

struct Plane_t {
    // The plane equation is Ax + By + Cz + D = 0.
    float A, B, C, D;
    int materialID;
};

struct Sphere_t {
    vec3 center; // Sphere center
    float radius; // Sphere radius
    int materialID;
};

struct Cylinder_t {
    vec3 center; // Cylinder center
    float radius; // Cylinder radius
    float height; // Cylinder height
