                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if(sdHorseshoe(p - vec2(-0.1, 2.5), vec2(cos(1.6 * (iTime - 3.0) / 3.0), sin(1.6 * (iTime - 3.0) / 3.0)), 0.6, vec2(0.1, 0.1)) < 0.0) {
