        } else if(iTime >= 6.0 && iTime < 10.0) {
            if((udSegment(p, vec2(3.5, 2.0), vec2(3.5, 4.0)) - 0.1) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
                Material[5].k_d = vec3(0.6, 0.4, 0.5);
                Material[5].k_a = 0.0 * Material[2].k_d;
                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if((udSegment(p, vec2(3.5, 4.0), vec2(1.5, 2.0)) - 0.1) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
                Material[5].k_d = vec3(0.6, 0.4, 0.5);
                Material[5].k_a = 0.0 * Material[2].k_d;
                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if((udSegment(p, vec2(1.5, 2.0), vec2(1.5, 2.0 + (iTime - 6.0) / 2.0)) - 0.1) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
                Material[5].k_d = vec3(0.6, 0.4, 0.5);
                Material[5].k_a = 0.0 * Material[2].k_d;
                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if((udSegment(p, vec2(0.5, 4.0), vec2(0.5, 2.6)) - 0.1) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
