            } else if(sdHorseshoe(p - vec2(-0.1, 2.5), vec2(cos(1.6), sin(1.6)), 0.6, vec2(0.1, 0.1)) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
