            } else if((udSegment(p, vec2(-0.7, 2.6), vec2(-0.7, 2.6 + (iTime - 6.0) * (1.4 / 4.0))) - 0.1) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
