            } else if(sdHorseshoe(p.yx - vec2(2.5, -3.1), vec2(cos(1.6), sin(1.6)), 0.5, vec2(0.1, 0.1)) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
