                Material[5].k_d = vec3(0.6, 0.4, 0.5);
                Material[5].k_a = 0.0 * Material[2].k_d;
                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if(sdHorseshoe(-p.yx + vec2(3.5, -2.1), vec2(cos(1.6), sin(1.6)), 0.5, vec2(0.1, 0.1)) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
                Material[5].k_d = vec3(0.6, 0.4, 0.5);
                Material[5].k_a = 0.0 * Material[2].k_d;
                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if((udSegment(p, vec2(-2.2, 3.0), vec2(-3.1, 3.0)) - 0.1) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
                Material[5].k_d = vec3(0.6, 0.4, 0.5);
                Material[5].k_a = 0.0 * Material[2].k_d;
                Material[5].k_r = 0.0 * Material[2].k_d;
                Material[5].k_rg = 0.0 * Material[2].k_r;
                Material[5].n = 128.0;
            } else if(sdHorseshoe(p.yx - vec2(2.5, -3.1), vec2(cos(1.6), sin(1.6)), 0.5, vec2(0.1, 0.1)) < 0.0) {
                nearest_hitNormal = vec3(0.0, 0.0, -1.0);
