// https://iquilezles.org/articles/distfunctions/
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d1+d2)/k, 0.0, 1.0 );
    return mix( d1, -d2, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

vec2 opUnion(vec2 a, vec2 b){
    return (a.x<b.x)?a:b;
}
// SDF of line segment -> capsul
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

// Droplet -> line segment, but one side with r = 0 and anthoer side with r = r
float sdDroplet( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r*h;
}

// Mouth shape
float sdVesicaSegment( in vec3 p, in vec3 a, in vec3 b, in float w )
{
    vec3  c = (a+b)*0.5;
    float l = length(b-a);
    vec3  v = (b-a)/l;
    float y = dot(p-c,v);
    vec2  q = vec2(length(p-c-y*v),abs(y));

    float r = 0.5*l;
    float d = 0.5*(r*r-w*w)/w;
    vec3  h = (r*q.x<d*(q.y-r)) ? vec3(0.0,r,0.0) : vec3(-d,0.0,d+w);

    return length(q-h.xy) - h.z;
}

vec2 DistGhost(vec3 location, float time){
    float k = .01; //roundness
    vec2 res;

    // body
    float radial_change = cos(atan(location.x/location.z)*20.+time*10.+cos(location.y*3.+1.7)*10.);
    float to_ground = exp(-(location.y+0.5)*10.);
    float r = 0.48+.02*(radial_change)*C(to_ground)+0.05*to_ground;
    float dBody = sdCapsule(location, vec3(0., .5, 0.), vec3(0., -.5, 0.), r);

    // eye hole
    float dEyeH1 = sdCapsule(location, vec3(0.45, .45, 0.2), vec3(0.45, 0.2, 0.2), 0.05);
    float dEyeH2 = sdCapsule(location, vec3(0.45, .45, -0.2), vec3(0.45, 0.2, -0.2), 0.05);
    dBody = max(dBody, -dEyeH1);
    dBody = max(dBody, -dEyeH2);

    //mouth hole
    float dMouth = sdCapsule(location, vec3(0.47, -.13, 0.13), vec3(0.47, -.13, -0.13), 0.02);
    dBody = opSmoothSubtraction(dBody, dMouth, k);

    // float
    float dFloat = location.y + 0.45 + 0.01*sin(radial_change);
    dBody = max(dBody, -dFloat);
    res = vec2(dBody, 1.0);


    // eye
    float dEye1 = sdCapsule(location, vec3(0.41, .45, 0.19), vec3(0.41, 0.19, 0.19), 0.04);
    float dEye2 = sdCapsule(location, vec3(0.41, .45, -0.19), vec3(0.41, 0.19, -0.19), 0.04);
    float dEye = min(dEye1, dEye2);
    res = opUnion(res, vec2(dEye, 2.0));

    // mouth
    float dMouth2 = sdVesicaSegment(location, vec3(0.43, -.13, -0.13), vec3(0.43, -.13, 0.13), 0.05);
    res = opUnion(res, vec2(dMouth2, 3.0));

    // water droplet
    float dDrop = sdDroplet(location, vec3(0.42, -.11, -0.1), vec3(0.465, -.18, -0.1), 0.02);
    res = opUnion(res, vec2(dDrop, 4.0));



    return res;
}

vec2 Dist(vec3 location, float time){    
    float offset = pow(abs(sin(time*.6)), 2.)*3.-5.;
    vec2 dGhost = DistGhost(location + vec3(0., offset, 0.)*.1, time);

    float dFloor = location.y + 0.50;
    vec2 res = opUnion(dGhost, vec2(dFloor, 0.0));

    return res;
}

// get the gradiant of the distant -> normal vector of that "surface"
vec3 Norm(vec3 location, float time){
/* small step in all direction as our Normal
    vec2 diff = vec2(0.0005, 0);
    return normalize( vec3( Dist(location + diff.xyy, time) - Dist(location - diff.xyy, time), 
                            Dist(location + diff.yxy, time) - Dist(location - diff.yxy, time),
                            Dist(location + diff.yyx, time) - Dist(location - diff.yyx, time)));
*/
// https://iquilezles.org/articles/normalsSDF/
    vec3 n = vec3(0.0);
    for( int i=min(iFrame,0); i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*Dist(location+0.0005*e,time).x;
    }
    return normalize(n);    
}
float RayMarching(vec3 ro, vec3 rd, float time){
    // distant traveled
    float t = 0.; 
    float maxt = 20.;
    // ray marching
    for(float i=0.; i<1000. && t < maxt; i+=1.){
        // currect location
        vec3 loc = ro + t*rd;
        // distant to surface
        float d = Dist(loc, time).x;

        // we hit a surface
        if(d < 0.000001) break;

        // otherwise march forward
        t += d;
    }
    // max distant: 20, return -1 if didn't hit
    return t>maxt?-1.:t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float time = iTime;


   // camera	

#if MOUSE==1
    float an = 10.57*iMouse.x/iResolution.x;
    float bn = iMouse.y/iResolution.y*1.57;
    vec3  ta = vec3( 0.0, 0.5, 0.0);                         // pointing at
    float zoom = 1.;
    vec3  ro = ta + zoom*vec3( 1.3*cos(an), sin(bn)*2.-0.5, 1.3*sin(an) ); // camera location
#else
    float an = time*.392;
    vec3  ta = vec3( 0.0, 0.5, 0.0);                         // pointing at
    float zoom = sin(time*.234)*0.5+2.;
    vec3  ro = ta + zoom*vec3( 1.3*cos(an), sin(an*0.3)*0.4, 1.3*sin(an) ); // camera location
#endif 
    // frame
	vec3 cw = normalize(ta-ro);    // vector pointing toward target
	vec3 cp = vec3(0.0, 1.0, 0.0);
	vec3 cu = normalize( cross(cw,cp) ); // vector pointing up
	vec3 cv =          ( cross(cu,cw) ); // vector pointing right

    vec3 rd = normalize( p.x*cu + p.y*cv + 1.8*cw ); // actual ray direction from frame


    // distant to surface
    float t = RayMarching(ro, rd, time);

    // sky
    vec3 col = vec3(0.1, 0.05, 0.03) - max(rd.y,0.0)*0.6;

    // if we hit something with 20 unit
    if(t > 0.){
        // point we hit
        vec3 loc = ro + rd*t;
        // normal of that surface
        vec3 nor = Norm(loc, time);

        vec2 res = Dist(loc, time);
        // object color
        col = vec3(0.0);
        if(res.y < 0.5){
            col = vec3(0.03, .12, 0.04);
        } else if (res.y < 1.5){
            col = vec3(0.3);
        } else if (res.y < 2.5){
            col = vec3(0.01);
        } else if (res.y < 3.5){
            col = vec3(.247, .086, .049);
        } else if (res.y < 4.5){
            col = vec3(.124, .202, .235);
        }

        vec3 lightin = vec3(0.);
        // moon at 10. 10. -5.
        vec3 moon_loc = vec3(10., 10., -5.);
        vec3 moon_dir = normalize(moon_loc-loc);
        float moon_diffuse = C(dot(moon_dir, nor));

        //shadow
        float can_see_moon = step(RayMarching(loc+0.001*moon_dir, moon_dir, time), 0.);

        // sky
        float sky_diffuse = C(dot(normalize(vec3(0., 1., 0.)), nor));
        float sky_diffuse2 = C(dot(normalize(vec3(0., 1., 1.)), nor));

        // general bounced light
        float bounced = C(dot(normalize(vec3(0., -1., 0.)), nor));


        lightin += vec3(5., 5., 4.) * moon_diffuse * can_see_moon;
        lightin += vec3(0.2, 0.1, .5) * sky_diffuse;
        lightin += vec3(0.1, 0.15, .25) * sky_diffuse2;
        lightin += vec3(0.6, 0.4, 0.2) * bounced;
        col *= lightin;
        col = mix( col, vec3(0.7,0.4,0.3), 1.0-exp( -0.0001*pow(t, 3.) ) );
    }

    col = pow(col, vec3(0.4545));

    // Output to screen
    fragColor = vec4(col,1.0);
}

