// Persistive Keyboard Input buffer & resolution change check

const int KEY_LEFT  = 37;
const int KEY_RIGHT = 39;
const int KEY_UP    = 38;
const int KEY_DOWN  = 40;
const int KEY_7     = 55;
const int KEY_8     = 56;
const int KEY_9     = 57;
const int KEY_B     = 66;
const int KEY_C     = 67;
const int KEY_P     = 80;
const int KEY_S     = 83;
const int KEY_T     = 84;
const int KEY_V     = 86;
const int KEY_W     = 87;

bool readKey( int key ) {
	float keyVal = texture( iChannel1, vec2( (float(key)+.5)/256.0, .25 ) ).x;
	return (keyVal>.5)?true:false;
}

vec4 keyStep(int ix, int iy, int key, float delta, float maxval) {
    vec3 keystate = texelFetch(iChannel0, ivec2(ix,iy), 0 ).xyz;
    float kx=keystate.x;
    float ky=keystate.y;
    if( readKey(key) ) {
        if (ky == 0.) {
            kx+=1.;
            if (kx >= maxval) kx=0.;
            if (kx < 0.) kx=maxval-delta;
        }
        ky+=0.01;
        if (ky >= 1.0) ky=0.;
    }
    else {
        ky = 0.;
    }
    return vec4(kx,ky,0.,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int ix = int(fragCoord.x), iy = int(fragCoord.y);

    if (iy > 1) { fragColor = vec4(0.,0.,0.,1.0); return; }

    if (ix == 0 && iy == 0) {
        vec3 keystate = texelFetch(iChannel0, ivec2(ix,iy), 0 ).xyz;
        float kx=keystate.x;
        float ky=keystate.y;
        if( readKey(KEY_UP)||readKey(KEY_DOWN)||readKey(KEY_LEFT)||readKey(KEY_RIGHT) ) {
            if (ky == 0.) {
                if (readKey(KEY_DOWN) || readKey(KEY_RIGHT) ) {
                    kx+=1.001;
                    if (kx > LAST_PATT+0.5) kx=0.5;
                } else {
                    kx-=1.001;
                    if (kx < 0.) kx=LAST_PATT+0.5;
                }
            }
            ky+=0.01;
            if (ky >= 1.0) ky=0.;
        }
        else {
            ky = 0.;
        }
        fragColor = vec4(kx,ky,0.,0.);
        return;
    }

    if (ix == 2 && iy == 0) {
        float speed = texelFetch(iChannel0, ivec2(ix,iy), 0 ).x;
        if( readKey(KEY_7)) speed = 0.;
        if( readKey(KEY_8)) speed = -1.;
        if( readKey(KEY_9)) speed = 5.;
        fragColor = vec4(speed,0.,0.,0.);
        return;
    }

    if (ix == 4 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_T, 1., 2.);
        return;
    }

    if (ix == 6 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_C, 1., 2.);
        return;
    }

    if (ix == 7 && iy == 0) {
        float zmod = texelFetch(iChannel0, ivec2(ix,iy), 0 ).x;
        if( readKey(KEY_W)) zmod += 1.;
        if( readKey(KEY_S)) zmod -= 1.;
        zmod = clamp(zmod, -20., 40.);
        fragColor = vec4(zmod,0.,0.,0.);
        return;
    }

    if (ix == 8 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_B, 1., 2.);
        return;
    }

    if (ix == 9 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_P, 1., 2.);
        if (readKey(KEY_DOWN) || readKey(KEY_UP))
            fragColor = vec4(0.);
        return;
    }

    if (ix == 10 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_V, 1., 3.);
        return;
    }


    if (ix == 0 && iy == 1) {
        fragColor = vec4(iResolution.x,0.,0.,0.);
        return;
    }

    fragColor = vec4(0.,0.,0.,1.0);
}

buffer_b_code:
// Automata buffer

int N;

const int KEY_UP    = 38;
const int KEY_DOWN  = 40;
const int KEY_SPACE  = 32;
const int KEY_D  = 68;

bool readKey( int key ) {
	float keyVal = texture( iChannel2, vec2( (float(key)+.5)/256.0, .25 ) ).x;
	return (keyVal>.5)?true:false;
}

int cell( in ivec3 p, int xp,int yp, int zp) {
    int n=(p.x+xp)+(p.y+yp)*N+(p.z+zp)*N*N;

    int resx = int(iResolution.x);
    ivec2 p2 = ivec2(n%resx, n/resx);
    return texelFetch(iChannel0, p2, 0 ).x == liveval-1. ? 1 : 0;
}

int cellwrap( in ivec3 p, int xp,int yp, int zp) {
    int x=p.x+xp, y=p.y+yp, z=p.z+zp;
    if (x==0) x=N-2;  if (x==N-1) x=1;
    if (y==0) y=N-2;  if (y==N-1) y=1;
    if (z==0) z=N-2;  if (z==N-1) z=1;
    int n=x+y*N+z*N*N;

    int resx = int(iResolution.x);
    ivec2 p2 = ivec2(n%resx, n/resx);
    return texelFetch(iChannel0, p2, 0 ).x == liveval-1. ? 1 : 0;
}

int cellval( in ivec3 p, int xp,int yp, int zp) {
    int n=(p.x+xp)+(p.y+yp)*N+(p.z+zp)*N*N;

    int resx = int(iResolution.x);
    ivec2 p2 = ivec2(n%resx, n/resx);
    return int(texelFetch(iChannel0, p2, 0 ).x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    N=int(SIZE);
    int maxsize = int(pow(iResolution.x*iResolution.y, 1./3.));
    if (maxsize < N) { N=maxsize-(1-maxsize%2); }

    int pos1d=int(fragCoord.x) + int(fragCoord.y) * int(iResolution.x); 
    if (pos1d > N*N*N) { fragColor = vec4(0.); return; }

    int index = int(texelFetch(iChannel1, ivec2(0,0), 0 ).x);
    float xres = texelFetch(iChannel0, ivec2(0,0), 0 ).w;
    int speed = 1+int(texelFetch(iChannel1, ivec2(2,0), 0 ).x);
    int bbox = int(texelFetch(iChannel1, ivec2(8,0), 0 ).x);
    int pause = int(texelFetch(iChannel1, ivec2(9,0), 0 ).x);

    int z=pos1d/(N*N);
    pos1d-=z*N*N;
    int y=pos1d/N;
    int x=pos1d-y*N;
    ivec3 pos3d=ivec3(x,y,z);

    float wv=0.;
    if (fragCoord.x < 1. && fragCoord.y < 1.) {
        wv=iResolution.x;
    }

    // bounding box
    int NM=N-1;
    if (x==0 || y==0 || z==0 || x==NM || y==NM || z==NM) { 
        fragColor = vec4(-1.,0.,0.,wv);
        if (bbox == 1) {
            if ((x==0 && y==0) || (x==0 && z==0) || (y==0 && z==0) || (x==NM && y==NM) || (x==NM && z==NM) || (y==NM && z==NM)  || (y==0 && z==NM) || (x==0 && z==NM) || (x==0 && y==NM)  || (y==0 && x==NM) || (z==0 && y==NM)  || (z==0 && x==NM) )
                fragColor = vec4(-1.,0.,1.0,wv);
        }
        return;
    }

    setRules(index);
    if (readKey(KEY_D) && withermethod >= 0) { setRules(withermethod); } //wither

    if (iFrame < 1 || readKey(KEY_SPACE) || readKey(KEY_UP) || readKey(KEY_DOWN) || int(xres) != int(iResolution.x)) {
        pos3d-=N/2;

        if (hash1(fragCoord.x*0.03*fragCoord.y*0.021+iTime*0.04) < density) {
            if (clearmethod == 0) {
                if (length(vec3(pos3d))<radius) {
                    fragColor = vec4(liveval-1.,0.0,0.0,wv);
                    return;
                }
            } else if (clearmethod == 1) {
                int iradius=int(radius);
                if (abs(pos3d.x)<iradius && abs(pos3d.y)<iradius && abs(pos3d.z)<iradius) {
                    fragColor = vec4(liveval-1.,0.0,0.0,wv);
                    return;
                }
            } else {
                fragColor = vec4(liveval-1.,0.0,0.0,wv);
                return;
            }
        }
        fragColor = vec4(-1.,0.,0.,wv);
        return;
    }

    int st = cellval(pos3d,0,0,0);

    speed += speedplus;
    if (iFrame % (speed+1) > 0 || pause == 1) {
        fragColor = vec4(float(st),0.0,0.0,wv);
        return;
    }

    int count = 0;
    if (countmethod == 0) {
        // Mooore
        if (wrap == 1) {
            count = cellwrap(pos3d,-1,-1,-1) + cellwrap(pos3d,0,-1,-1) + cellwrap(pos3d,1,-1,-1) + cellwrap(pos3d,-1,0,-1) + cellwrap(pos3d,0,0,-1) + cellwrap(pos3d,1,0,-1) + cellwrap(pos3d,-1,1,-1) + cellwrap(pos3d,0,1,-1) + cellwrap(pos3d,1,1,-1) 
                  + cellwrap(pos3d,-1,-1,0) + cellwrap(pos3d,0,-1,0) + cellwrap(pos3d,1,-1,0) + cellwrap(pos3d,-1,0,0) + cellwrap(pos3d,1,0,0) + cellwrap(pos3d,-1,1,0) + cellwrap(pos3d,0,1,0) + cellwrap(pos3d,1,1,0)
                  + cellwrap(pos3d,-1,-1,1) + cellwrap(pos3d,0,-1,1) + cellwrap(pos3d,1,-1,1) + cellwrap(pos3d,-1,0,1) + cellwrap(pos3d,0,0,1) + cellwrap(pos3d,1,0,1) + cellwrap(pos3d,-1,1,1) + cellwrap(pos3d,0,1,1) + cellwrap(pos3d,1,1,1);
        } else {
            count = cell(pos3d,-1,-1,-1) + cell(pos3d,0,-1,-1) + cell(pos3d,1,-1,-1) + cell(pos3d,-1,0,-1) + cell(pos3d,0,0,-1) + cell(pos3d,1,0,-1) + cell(pos3d,-1,1,-1) + cell(pos3d,0,1,-1) + cell(pos3d,1,1,-1) 
                  + cell(pos3d,-1,-1,0) + cell(pos3d,0,-1,0) + cell(pos3d,1,-1,0) + cell(pos3d,-1,0,0) + cell(pos3d,1,0,0) + cell(pos3d,-1,1,0) + cell(pos3d,0,1,0) + cell(pos3d,1,1,0)
                  + cell(pos3d,-1,-1,1) + cell(pos3d,0,-1,1) + cell(pos3d,1,-1,1) + cell(pos3d,-1,0,1) + cell(pos3d,0,0,1) + cell(pos3d,1,0,1) + cell(pos3d,-1,1,1) + cell(pos3d,0,1,1) + cell(pos3d,1,1,1);
        }
    } else { 
        // Neumann
        if (wrap == 1)
            count = cellwrap(pos3d,0,-1,0) + cellwrap(pos3d,0,1,0) + cellwrap(pos3d,-1,0,0) + cellwrap(pos3d,1,0,0) + cellwrap(pos3d,0,0,-1) + cellwrap(pos3d,0,0,1);
        else
            count = cell(pos3d,0,-1,0) + cell(pos3d,0,1,0) + cell(pos3d,-1,0,0) + cell(pos3d,1,0,0) + cell(pos3d,0,0,-1) + cell(pos3d,0,0,1);
    }

    if(st <= 0) {
        if (((1<<count) & bornset) > 0)
            st = int(liveval)-1;
    }
    else {
        if (((1<<count) & liveset) == 0)
            st--;
    }

    fragColor = vec4(float(st),0.0,0.0,wv);
}

common_code:
#define SIZE 125.

#define HORIZON

const float LAST_PATT = 23.01;

int liveset, bornset, clearmethod, countmethod, withermethod;
float liveval;
float radius;
float density;
int speedplus, wrap;

void setRules(int index) {
    int v1, v2;
    countmethod=0, clearmethod=0, withermethod=-1;
    speedplus=0, wrap=1;

    if (index == 0) {  liveval=10.; v1=240, v2=448; radius=6.; density=0.4; withermethod=8; }                           // pyroclastic
    if (index == 1) {  countmethod=1; liveval=2.; v1=127, v2=10; clearmethod=1; radius=1.0; density=1.0; speedplus=4; } // crystal
    if (index == 2) {  liveval=8.;  v1=5382088, v2=9701408; radius=8.0; density=0.5; speedplus=-1; wrap=0; }            // amoeba II
    if (index == 3) {  liveval=2.;  v1=134209536, v2=942080; clearmethod=2; density=0.499; wrap=1; }                    // clouds
    if (index == 4) {  liveval=2.;  v1=593808, v2=2195520; radius=35.0; density=0.3; wrap=0; }                          // slowburning star
    if (index == 5) {  liveval=4.;  v1=134217600, v2=16; clearmethod=1; radius=12.0; density=0.5; wrap=1; }             // struct builder
    if (index == 6) {  liveval=5.;  v1=371520, v2=63793088; clearmethod=1; radius=6.0; density=0.6; }                   // expanding shells
    if (index == 7) {  liveval=2.;  v1=0, v2=2; clearmethod=1; radius=1.0; density=1.0; speedplus=20; wrap=0; }         // single point
    if (index == 8) {  liveval=10.; v1=580, v2=848; radius=13.; density=0.1; withermethod=0;}                           // builder
    if (index == 9) {  liveval=5.;  v1=16, v2=16; clearmethod=1; radius=15.0; density=0.4; }                            // 445
    if (index == 10) { countmethod=1; liveval=2.; v1=127, v2=10; clearmethod=1; radius=4.0; density=1.0; speedplus=4; } // crystal (same, radius change)
