const float PI = 3.1415926536;

const vec3 BACKGROUND_COLOR = vec3( 0.8, 0.8, 0.8 );

// Vertical field-of-view angle of camera. In radians.
const float FOVY = 50.0 * PI / 180.0;

// Use this for avoiding the "epsilon problem" or the shadow acne problem.
const float DEFAULT_TMIN = 10.0e-4;

// Use this for tmax for non-shadow ray intersection test.
const float DEFAULT_TMAX = 10.0e6;

// Equivalent to number of recursion levels (0 means ray-casting only).
// We are using iterations to replace recursions.
const int NUM_ITERATIONS = 2;

// Constants for the scene objects.
const int NUM_LIGHTS = 4;
const int NUM_MATERIALS = 10;
const int NUM_PLANES = 1;
const int NUM_ROWS = 5;
const int NUM_SPHERES = NUM_ROWS * NUM_ROWS;

const int n = 10;

//============================================================================
// Define new struct types.
//============================================================================
struct Ray_t {
    vec3 o;  // Ray Origin.
    vec3 d;  // Ray Direction. A unit vector.
};

struct Plane_t {
    // The plane equation is Ax + By + Cz + D = 0.
    float A, B, C, D;
    int materialID;
};

struct Sphere_t {
    vec3 center;
    float radius;
    int materialID;
    float radian;
};

struct Light_t {
    vec3 position;  // Point light 3D position.
    vec3 I_a;       // For Ambient.
    vec3 I_source;  // For Diffuse and Specular.
};

struct Material_t {
    vec3 k_a;   // Ambient coefficient.
    vec3 k_d;   // Diffuse coefficient.
    vec3 k_r;   // Reflected specular coefficient.
    vec3 k_rg;  // Global reflection coefficient.
    float n;    // The specular reflection exponent. Ranges from 0.0 to 128.0.
};

//----------------------------------------------------------------------------
// The lighting model used here is similar to that shown in
// Lecture Topic B08 (Basic Ray Tracing). Here it is computed as
//
//     I_local = SUM_OVER_ALL_LIGHTS {
//                   I_a * k_a +
//                   k_shadow * I_source * [ k_d * (N.L) + k_r * (R.V)^n ]
//               }
// and
//     I = I_local  +  k_rg * I_reflected
//----------------------------------------------------------------------------


//============================================================================
// Global scene data.
//============================================================================
Plane_t Plane[NUM_PLANES];
Sphere_t Sphere[NUM_SPHERES];
Light_t Light[NUM_LIGHTS];
Material_t Material[NUM_MATERIALS];

vec3 hsv2rgb(vec3 hsv)
{
    vec3 rgb = clamp(abs(mod(hsv.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0,0.0,1.0);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    return hsv.z * mix(vec3(1.0), rgb, hsv.y);
}

/////////////////////////////////////////////////////////////////////////////
// Initializes the scene.
/////////////////////////////////////////////////////////////////////////////
void InitScene()
{
    // Horizontal plane.
    Plane[0].A = 0.0;
    Plane[0].B = 1.0;
    Plane[0].C = 0.0;
    Plane[0].D = 0.0;
    Plane[0].materialID = 0;

    for(int i = 0;i < NUM_ROWS;i++)
    {
        for(int j = 0;j < NUM_ROWS;j++)
        {
            Sphere[i * NUM_ROWS + j].radius = 0.5;
            Sphere[i * NUM_ROWS + j].radian = float(i + j) * PI / 10.0;
            Sphere[i * NUM_ROWS + j].center = vec3( -i * 2 + 4, abs(sin(2.0 * iTime + Sphere[i * NUM_ROWS + j].radian)) + Sphere[i * NUM_ROWS + j].radius, -j * 2 + 4);
            Sphere[i * NUM_ROWS + j].materialID = i + 5 - j ;
        }
    }

    // Silver material.
    Material[0].k_d = vec3( 0.5, 0.5, 0.5 );
    Material[0].k_a = 0.2 * Material[0].k_d;
    Material[0].k_r = 2.0 * Material[0].k_d;
    Material[0].k_rg = 0.5 * Material[0].k_r;
    Material[0].n = 64.0;

    for(int i = 1; i < 10; i++)
    {
        float colorchange = float(i) / 5.0 + iTime / 2.0;
        Material[i].k_d = hsv2rgb(vec3(mod(colorchange, 1.0), 1.0, 1.0));;
        Material[i].k_a = 0.2 * Material[i].k_d;
        Material[i].k_r = vec3( 0.5, 0.5, 0.5 ) + 0.5 * Material[i].k_d;
        Material[i].k_rg = 0.5 * Material[i].k_r;
        Material[i].n = 128.0;
    }

    // Light 0.
    Light[0].position = vec3( 4.0, 8.0, 4.0 );
    Light[0].I_a = vec3( 0.1, 0.1, 0.1 );
    Light[0].I_source = vec3( 0.7, 0.3, 0.3 );

    // Light 1.
    Light[1].position = vec3( -4.0, 8.0, 4.0 );
    Light[1].I_a = vec3( 0.1, 0.1, 0.1 );
    Light[1].I_source = vec3( 0.3, 0.7, 0.3 );

    // Light 2.
    Light[2].position = vec3( 4.0, 8.0, -4.0 );
    Light[2].I_a = vec3( 0.1, 0.1, 0.1 );
    Light[2].I_source = vec3( 0.2, 0.2, 0.9 );

    // Light 3.
    Light[3].position = vec3( -4.0, 8.0, -4.0 );
    Light[3].I_a = vec3( 0.1, 0.1, 0.1 );
    Light[3].I_source = vec3( 0.6, 0.6, 0.1 );
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a plane and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is such an intersection, outputs the value of t, the position
// of the intersection (hitPos) and the normal vector at the intersection
// (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax,
                     out float t, out vec3 hitPos, out vec3 hitNormal )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;

    // We have a hit -- output results.
    t = t0;
    hitPos = ray.o + t0 * ray.d;
    hitNormal = N;
    for(int j = 0; j < NUM_SPHERES; j++)
    {
        vec3 radius = hitPos - vec3(Sphere[j].center.x ,0.0 ,Sphere[j].center.z);
        float r = length(radius)/5.0;
        float time = fract((iTime * 2.0 + Sphere[j].radian) / PI) * PI / 2.0;
        if(r < time && r > (time - PI/10.0))
        {
            hitNormal += sin((r - time) * 40.0) * normalize(radius) * (PI / 2.0 - time) / (PI / 2.0);
        }
    }

    hitNormal = normalize(hitNormal);
    return true;
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a plane and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;
    return true;
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a sphere and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is one or two such intersections, outputs the value of the
// smaller t, the position of the intersection (hitPos) and the normal
// vector at the intersection (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax,
                      out float t, out vec3 hitPos, out vec3 hitNormal )
{
    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    float a = dot(ray.d,ray.d);
    float b = 2.0 * dot(ray.d, ray.o - sph.center);
    float c = dot(ray.o - sph.center,ray.o - sph.center) - sph.radius * sph.radius;
    float d = b * b - 4.0 * a * c;
    if(d < 0.0) return false;
    float sqrtd = sqrt(d);
    float t1 = (- b - sqrtd)/(2.0 * a);
    float t2 = (- b + sqrtd)/(2.0 * a); 

    if (tmin <= t1 && t1 <= tmax) t = t1;
    else if (tmin <= t2 && t2 <= tmax) t = t2;
    else return false;

    hitPos = ray.o + t * ray.d;
    hitNormal = normalize(hitPos - sph.center);

    return true;


    //return false;  // Replace this with your code.

}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a sphere and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax )
{
    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    float a = dot(ray.d,ray.d);
    float b = 2.0 * dot(ray.d, ray.o - sph.center);
    float c = dot(ray.o - sph.center,ray.o - sph.center) - sph.radius * sph.radius;
    float d = b * b - 4.0 * a * c;
    if(d < 0.0) return false;
    float sqrtd = sqrt(d);
    float t1 = (- b - sqrtd)/(2.0 * a);
    float t2 = (- b + sqrtd)/(2.0 * a); 

    if (tmin <= t1 && t1 <= tmax) return true;
    else if (tmin <= t2 && t2 <= tmax) return true;
    else return false;

    //return false;  // Replace this with your code.

}



/////////////////////////////////////////////////////////////////////////////
// Computes (I_a * k_a) + k_shadow * I_source * [ k_d * (N.L) + k_r * (R.V)^n ].
// Input vectors L, N and V are pointing AWAY from surface point.
// Assume all vectors L, N and V are unit vectors.
/////////////////////////////////////////////////////////////////////////////
vec3 PhongLighting( in vec3 L, in vec3 N, in vec3 V, in bool inShadow,
                    in Material_t mat, in Light_t light )
{
    if ( inShadow ) {
        return light.I_a * mat.k_a;
    }
    else {
        vec3 R = reflect( -L, N );
        float N_dot_L = max( 0.0, dot( N, L ) );
        float R_dot_V = max( 0.0, dot( R, V ) );
        float R_dot_V_pow_n = ( R_dot_V == 0.0 )? 0.0 : pow( R_dot_V, mat.n );

        return light.I_a * mat.k_a +
               light.I_source * (mat.k_d * N_dot_L + mat.k_r * R_dot_V_pow_n);
    }
}



/////////////////////////////////////////////////////////////////////////////
// Casts a ray into the scene and returns color computed at the nearest
// intersection point. The color is the sum of light from all light sources,
// each computed using Phong Lighting Model, with consideration of
// whether the interesection point is being shadowed from the light.
// If there is no interesection, returns the background color, and outputs
// hasHit as false.
// If there is intersection, returns the computed color, and outputs
// hasHit as true, the 3D position of the intersection (hitPos), the
// normal vector at the intersection (hitNormal), and the k_rg value
// of the material of the intersected object.
/////////////////////////////////////////////////////////////////////////////
vec3 CastRay( in Ray_t ray,
              out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg )
{
    // Find whether and where the ray hits some object.
    // Take the nearest hit point.

    bool hasHitSomething = false;
    float nearest_t = DEFAULT_TMAX;   // The ray parameter t at the nearest hit point.
    vec3 nearest_hitPos;              // 3D position of the nearest hit point.
    vec3 nearest_hitNormal;           // Normal vector at the nearest hit point.
    int nearest_hitMatID;             // MaterialID of the object at the nearest hit point.

    float temp_t;
    vec3 temp_hitPos;
    vec3 temp_hitNormal;
    bool temp_hasHit;

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Try interesecting input ray with all the planes and spheres,
    //   and record the front-most (nearest) interesection.
    // * If there is interesection, need to record hasHitSomething,
    //   nearest_t, nearest_hitPos, nearest_hitNormal, nearest_hitMatID.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    for (int i = 0; i < NUM_PLANES; i++)
    {
        if (IntersectPlane(Plane[i], ray, DEFAULT_TMIN, nearest_t, temp_t, temp_hitPos, temp_hitNormal))
        {
            nearest_t = temp_t;
            nearest_hitPos = temp_hitPos;
            nearest_hitNormal = temp_hitNormal;
            nearest_hitMatID = Plane[i].materialID;
            hasHitSomething = true;
        }
    }
    for (int i = 0; i < NUM_SPHERES; i++)
    {
        if (IntersectSphere(Sphere[i], ray, DEFAULT_TMIN, nearest_t, temp_t, temp_hitPos, temp_hitNormal)) 
        {
            nearest_t = temp_t;
            nearest_hitPos = temp_hitPos;
            nearest_hitNormal = temp_hitNormal;
            nearest_hitMatID = Sphere[i].materialID;
            hasHitSomething = true;
        }
    }

    // One of the output results.
    hasHit = hasHitSomething;
    if ( !hasHitSomething ) return BACKGROUND_COLOR;

    vec3 I_local = vec3( 0.0 );  // Result color will be accumulated here.

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Accumulate lighting from each light source on the nearest hit point.
    //   They are all accumulated into I_local.
    // * For each light source, make a shadow ray, and check if the shadow ray
    //   intersects any of the objects (the planes and spheres) between the
    //   nearest hit point and the light source.
    // * Then, call PhongLighting() to compute lighting for this light source.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    for (int i = 0; i < NUM_LIGHTS; i++)
    {
        Ray_t lightray;
        lightray.o = nearest_hitPos;
        lightray.d = normalize(Light[i].position - nearest_hitPos);
        bool inShadow = false;
        for (int j = 0; j < NUM_PLANES; j++)
        {
            if (IntersectPlane(Plane[j], lightray, DEFAULT_TMIN, nearest_t))
                inShadow = true;

        }
        for (int j = 0; j < NUM_SPHERES; j++)
        {
            if (IntersectSphere(Sphere[j], lightray, DEFAULT_TMIN, nearest_t)) 
                inShadow = true;
        }
        I_local += PhongLighting(lightray.d, nearest_hitNormal, -ray.d, inShadow, Material[nearest_hitMatID], Light[i]);
    }

    // Populate output results.
    hitPos = nearest_hitPos;
    hitNormal = nearest_hitNormal;
    k_rg = Material[nearest_hitMatID].k_rg;

    return I_local;
}


/////////////////////////////////////////////////////////////////////////////
// Execution of fragment shader starts here.
// 1. Initializes the scene.
// 2. Compute a primary ray for the current pixel (fragment).
// 3. Trace ray into the scene with NUM_ITERATIONS recursion levels.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitScene();
    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // Position the camera.
    vec3 cam_pos = vec3( 2.0 * sin(iTime / 2.0) + 5.0, 7.0, 2.0 * cos(iTime / 2.0));
    vec3 cam_lookat = vec3( 0.0, 0.0, 0.0 );
    vec3 cam_up_vec = vec3( 0.0, 1.0, 0.0 );

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize( cam_pos - cam_lookat );
    vec3 cam_x_axis = normalize( cross(cam_up_vec, cam_z_axis));
    vec3 cam_y_axis = normalize( cross(cam_z_axis, cam_x_axis));

    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay;
    pRay.o = cam_pos;
    pRay.d = normalize( pixel_pos.x * cam_x_axis * 1.0  +  pixel_pos.y * cam_y_axis * 1.0 +  pixel_pos_z * cam_z_axis );


    // Start Ray Tracing.
    // Use iterations to emulate the recursion.

    vec3 I_result = vec3( 0.0 );
    vec3 compounded_k_rg = vec3( 1.0 );
    Ray_t nextRay = pRay;

    for ( int level = 0; level <= NUM_ITERATIONS; level++ )
    {
        bool hasHit;
        vec3 hitPos, hitNormal, k_rg;

        vec3 I_local = CastRay( nextRay, hasHit, hitPos, hitNormal, k_rg );

        I_result += compounded_k_rg * I_local;

        if ( !hasHit ) break;

        compounded_k_rg *= k_rg;

        nextRay = Ray_t( hitPos, normalize( reflect(nextRay.d, hitNormal) ) );
    }

    fragColor = vec4( I_result, 1.0 );
}

/*

    Cell-By-Cell Raymarching
    ------------------------

	Amalgamating cell-by-cell traversal and raymarching to render a 
    subdivided rectangular grid. There are a few examples involving 
    similar methods on Shadertoy, so this is not new. However, it's 
    a really useful method that isn't used very often, so I figured 
    I'd attempt to post a simple example in the hopes that those not 
    familiar with the process might benefit from it.

    If you've ever tried raymarching a repeat space grid containing 
    objects comparable to the size of the grid cell boundaries, you'll 
    notice a lot of artifacts. The two main solutions involve
    raymarching the grid neighbors, which is usually expensive and
    doesn't always work, or to perform a cell-by-cell traversal, which
    is great, but you lose a lot of raymarching benefits, like simple
    CSG, soft shadows, uncomplicated ambient occlusion formulas, etc.

    The solution is to combine the two aforementioned methods in a
    surprisingly easy to implement fashion. In addition to raymarching 
    the grid as usual, you determine the distance from the ray 
    position to the cell boundary and use that to restrict the ray 
    from advancing too far. The entire process requires the addition
    of just a few extra lines.

    I'm not sure who first demonstrated the technique on Shadertoy, 
    but Nimitz's "Sparse grid marching" is a really nice early example. 
    IQ's "Cubescape" shader was posted over ten years ago, and that 
    utilizes similar principles, but it involves advancing the ray 
    from cell to cell, then raymarching at each step. I'm not sure 
    which method is more efficient, but I'm employing a 2D version of 
    the former. The difference between this and a regular raymarched
    grid example is a small amount of setup and extra lines in the 
    distace, trace and shadow functions.

    This particular method can be applied to all kinds of rectangular
    grids, and with some adjustments, I'd imagine other grid types 
    would be possible. I have a simple 3D version that I'll post at
    some stage, and I intend to attempt some others later.	



	Similar examples:

    // Really stylish.
    Sparse grid marching - nimitz
    https://www.shadertoy.com/view/XlfGDs

	// A cell be cell traversal with raymarching inside each cell.
    // Amazing to think that this is over ten years old.
    Cubescape  - iq
	https://www.shadertoy.com/view/Msl3Rr  

    // A really clever 3D subdivided example. Traversing a
    // variable sized 3D box grid is on my list.
    Box Singularity - Tater
    https://www.shadertoy.com/view/7dVGDd

*/


// Max ray distance.
#define FAR 20.

// Scene color - Bluish purlple: 0, Tequila sunrise: 1.
#define COLOR 1


// Scene object ID to separate the mesh object from the terrain.
float objID;


// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// IQ's vec2 to float hash.
float hash21(vec2 p){  
    return fract(sin(mod(dot(p, vec2(27.609, 57.583)), 6.2831853))*43758.5453); 
}



// Cube face texturing -- Hacked together quickly, but it'll work.
vec3 texCube(sampler2D iCh, in vec3 p, in vec3 n){


    // Use the normal to determine the face. Z facing normals 
    // imply the XY plane, etc.
    n = abs(n);
    p.xy = n.x>.5? p.yz : n.y>.5? p.xz : p.xy; 

    // Reusing "p" for the color read.
    p = texture(iCh, p.xy).xyz;

    // Rough conversion from sRGB to linear.
    return p*p;

}

// Texture sample.
//
vec3 getTex(sampler2D iCh, vec2 p){

    // Strething things out so that the image fills up the window. You don't need to,
    // but this looks better. I think the original video is in the oldschool 4 to 3
    // format, whereas the canvas is along the order of 16 to 9, which we're used to.
    // If using repeat textures, you'd comment the first line out.
    //p *= vec2(iResolution.y/iResolution.x, 1);
    vec3 tx = texture(iCh, p/8.).xyz;
    return tx*tx; // Rough sRGB to linear conversion.
}

// Height map value, which is just the pixel's greyscale value.
float hm(in vec2 p){ return dot(getTex(iChannel0, p), vec3(.299, .587, .114)); }



// IQ's 3D signed box formula: I tried saving calculations by using the unsigned one, and
// couldn't figure out why the edges and a few other things weren't working. It was because
// functions that rely on signs require signed distance fields... Who would have guessed? :D
float sBoxS(vec3 p, vec3 b, float sf){

  p = abs(p) - b + sf;
  return min(max(p.x, max(p.y, p.z)), 0.) + length(max(p, 0.)) - sf;
}

// IQ's 2D signed box formula with some added rounding.
float sBoxS(vec2 p, vec2 b, float sf){

  p = abs(p) - b + sf;
  return min(max(p.x, p.y), 0.) + length(max(p, 0.)) - sf;
}


// Subdivided rectangle grid.
vec4 getGrid(vec2 p, inout vec2 sc){

    // Block offsets.
    vec2 ipOffs = vec2(0);
    // Row or column offset. Values like "1/3" would offset more
    // haphazardly, but I wanted to maintain a little symmetry.
    const float offDst = .5; 
    if(mod(floor(p.y/sc.y), 2.)<.5){
        p.x -= sc.x*offDst; // Row offset.
        ipOffs.x += offDst;
    }
    //if(mod(floor(p.x/sc.x), 2.)<.5){
        //p.y -= sc.y*offDst; // Column offset.
        //ipOffs.y += offDst;
    //}

    vec2 oP = p;

    // Block ID.
    vec2 ip;

    //#define EQUAL_SIDES

    // Subdivide.
    for(int i = 0; i<4; i++){

        // Current block ID.
        ip = floor(p/sc) + .5;
        float fi = float(i)*.0617; // Unique loop number.
        #ifdef EQUAL_SIDES        
        // Squares.

        // Random split.
        if(hash21(ip + .253 + fi)<.333){
           sc /= 2.;
           p = oP;
           ip = floor(p/sc) + .5; 
        }

        #else

        // Powers of two rectangles.

        // Random X-split.
        if(hash21(ip + .253 + fi)<.333){//3 && sc.x>1./8.
           sc.x /= 2.;
           p.x = oP.x;
           ip.x = floor(p.x/sc.x) + .5;
        }
        // Random Y-split.
        if(hash21(ip + .453 + fi)<.333){ // && sc.y>1./8.
           sc.y /= 2.;
           p.y = oP.y;
           ip.y = floor(p.y/sc.y) + .5;
        }

        #endif

    }

    // Local coordinates and cell ID.
    return vec4(p - ip*sc, (ip + ipOffs)*sc);

}

// Global cell boundary distance variables.
vec3 gDir; // Cell traversing direction.
vec3 gRd; // Ray direction.
float gCD; // Cell boundary distance.
// Box dimension and local XY coordinates.
vec3 gSc; 
vec2 gP;


// An extruded subdivided rectangular block grid. Use the grid cell's 
// center pixel to obtain a height value (read in from a height map), 
// then render a pylon at that height.

vec4 blocks(vec3 q3){


    // Local coordinates.
    vec2 p = q3.xy;


    vec3 sc = vec3(1); // Scale.
    // Local coordinates and cell ID.
    vec4 p4 = getGrid(p, sc.xy); 
    p = p4.xy;
    vec2 id = p4.zw;


    // The distance from the current ray position to the cell boundary
    // wall in the direction of the unit direction ray. This is different
    // to the minimum wall distance, so you need to trace out instead
    // of merely doing a box calculation. Anyway, the following are pretty 
    // standard cell by cell traversal calculations. The resultant cell
    // distance, "gCD", is used by the "trace" and "shadow" functions to 
    // restrict the ray from overshooting, which in turn restricts artifacts.
    vec3 rC = (gDir*sc - vec3(p, q3.z))/gRd;
    //vec2 rC = (gDir.xy*sc.xy - p)/gRd.xy; // For 2D, this will work too.

    // Minimum of all distances, plus not allowing negative distances, which
    // stops the ray from tracing backwards... I'm not entirely sure it's
    // necessary here, but it stops artifacts from appearing with other 
    // non-rectangular grids.
    //gCD = max(min(min(rC.x, rC.y), rC.z), 0.) + .0015;
    gCD = max(min(rC.x, rC.y), 0.) + .001; // Adding a touch to advance to the next cell.


    // The extruded block height. See the height map function, above.
    float h = hm(id);
    h = (h*.975 + .025)*2.5;


    // Change the prism rectangle scale just a touch to create some subtle
    // visual randomness. You could comment this out if you prefer more order.
    sc.xy -= .05*(hash21(id)*.9 + .1);

    // Lower box prism.
    float d = sBoxS(vec3(p, q3.z + h/2.), vec3(sc.xy, h)/2., .0);

    // Putting some random boxes on top.
    if(min(sc.x, sc.y)>.125 && hash21(id + .03)<.5){ 
       float d2 = sBoxS(vec3(p, q3.z + (h*1.3)/2.), vec3(sc.xy - .16, h*1.3)/2., .0);
       d = min(d, d2);
    }

    // Saving the box dimensions and local coordinates.
    gSc = vec3(sc.xy, h);
    gP = p;



    // Return the distance, position-base ID and box ID.
    return vec4(d, id, 0);
}


// Block ID -- It's a bit lazy putting it here, but it works. :)
vec4 gID;

// The extruded image.
float map(vec3 p){

    // Floor.
    float fl = -p.z;

    // The extruded blocks.
    vec4 d4 = blocks(p);
    gID = d4; // Individual block ID.


    // Overall object ID.
    objID = fl<d4.x? 1. : 0.;

    // Combining the floor with the extruded image
    return  min(fl, d4.x);

}


// Basic raymarcher.
float trace(in vec3 ro, in vec3 rd){

    // Overall ray distance and scene distance.
    float d, t = 0.; //hash31(ro + rd)*.15

    //vec2 dt = vec2(1e5, 0); // IQ's clever desparkling trick.

    // Set the global ray direction varibles -- Used to calculate
    // the cell boundary distance inside the "map" function.
    gDir = step(0., rd) - .5;
    gRd = rd; 

    int i;
    const int iMax = 128;
    for (i = min(iFrame, 0); i<iMax; i++){ 

        d = map(ro + rd*t);       
        //dt = d<dt.x? vec2(d, dt.x) : dt; // Shuffle things along.

        // Note the "t*b + a" addition. Basically, we're putting less emphasis on accuracy, 
        // as "t" increases. It's a cheap trick that works in most situations.
        if(abs(d)<.001*(1. + t*.05)|| t>FAR) break; 

        //t += i<32? d*.75 : d; 
        t += min(d, gCD); 
    }

    // If we've run through the entire loop and hit the far boundary, 
    // check to see that we haven't clipped an edge point along the way. 
    // Obvious... to IQ, but it never occurred to me. :)
    //if(i>=iMax - 1) t = dt.y;

    return min(t, FAR);
}


// Standard normal function. It's not as fast as the tetrahedral calculation, but more 
// symmetrical.
vec3 getNormal(in vec3 p, float t) {
	const vec2 e = vec2(.001, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	
                     map(p + e.yyx) - map(p - e.yyx)));
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing particular scenes with 
// limited iterations is impossible... However, I'd be very grateful if someone could 
// prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, vec3 n, float k){

    // More would be nicer. More is always nicer, but not always affordable. :)
    const int maxIterationsShad = 48; 

    ro += n*.0015; // Coincides with the hit condition in the "trace" function.
    vec3 rd = lp - ro; // Unnormalized direction ray.


    float shade = 1.;
    float t = 0.; 
    float end = max(length(rd), 0.0001);
    //float stepDist = end/float(maxIterationsShad);
    rd /= end;

    // Set the global ray direction varibles -- Used to calculate
    // the cell boundary distance inside the "map" function.
    gDir = sign(rd)*.5;
    gRd = rd;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. 
    // Obviously, the lowest number to give a decent shadow is the best one to choose. 
    for (int i = min(iFrame, 0); i<maxIterationsShad; i++){

        float d = map(ro + rd*t);


        shade = min(shade, k*d/t);
        //shade = min(shade, smoothstep(0., 1., k*h/dist)); // Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), 
        // dist += clamp(h, .01, stepDist), etc.
        t += clamp(min(d, gCD), .02, .25); 


        // Early exits from accumulative distance function calls tend to be a good thing.
        if (d<0. || t>end) break; 
    }

    // Shadow.
    return max(shade, 0.); 
}


// I keep a collection of occlusion routines... OK, that sounded really nerdy. :)
// Anyway, I like this one. I'm assuming it's based on IQ's original.
float calcAO(in vec3 p, in vec3 n){

	float sca = 2., occ = 0.;
    for(int i = 0; i<5; i++){

        float hr = float(i + 1)*.125/5.;        
        float d = map(p + n*hr);
        occ += (hr - d)*sca;
        sca *= .7;
    }

    return clamp(1. - occ, 0., 1.);    

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Screen coordinates.    
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

	// Camera Setup.
	vec3 ro = vec3(iTime/2., 0, -4); // Camera position, doubling as the ray origin.
	vec3 lk = ro + vec3(.02, .18, .25);//vec3(0, -.25, iTime);  // "Look At" position.

    // Light positioning.
    vec3 lp = ro + vec3(2, 2, 0);// Put it a bit in front of the camera.


    // Using the above to produce the unit ray-direction vector.
    float FOV = 1.333; // FOV - Field of view.
    vec3 fwd = normalize(lk - ro);
    vec3 rgt = normalize(vec3(fwd.z, 0., -fwd.x ));
    vec3 up = cross(fwd, rgt); 

    // rd - Ray direction.
    //vec3 rd = normalize(fwd + FOV*uv.x*rgt + FOV*uv.y*up);
    vec3 rd = normalize(uv.x*rgt + uv.y*up + fwd/FOV);

    // Rotation.
	rd.xy *= rot2(-.25);    



    // Raymarch to the scene.
    float t = trace(ro, rd);

    // Save the block ID and object ID.
    vec4 svGID = gID;

    // Scene object ID. Either the pylons or the floor.
    float svObjID = objID;

    // Saving the bloxk scale and local 2D base coordinates.
    vec3 svSc = gSc;
    vec2 svP = gP;


    // Initiate the scene color to black.
    vec3 col = vec3(0);

	// The ray has effectively hit the surface, so light it up.
	if(t < FAR){


    	// Surface position and surface normal.
	    vec3 sp = ro + rd*t;
	    //vec3 sn = getNormal(sp, edge, crv, ef, t);
        vec3 sn = getNormal(sp, t);

        // Light direction vector.
	    vec3 ld = lp - sp;

        // Distance from respective light to the surface point.
	    float lDist = max(length(ld), .001);

    	// Normalize the light direction vector.
	    ld /= lDist;


        // Obtaining the texel color. 
	    vec3 objCol; 

        float svObjEdge = 1.;

        // The extruded grid.
        if(svObjID<.5){

            /*
            // Random coloring using IQ's short versatile palette formula.
            float rnd = hash21(svGID.yz + .34);
            vec3 tx = .5 + .48*cos(6.2831853*rnd/10. + vec3(0, 1, 2) + .8);
            tx *= .5;
            */

            // Coloring the individual blocks with the saved ID.
            vec3 tx = getTex(iChannel1, svGID.yz);
            // Grunge texturing.
            vec3 tx2 = texCube(iChannel2, sp/4., sn);
            // Combining.
            objCol = tx.yzx*(tx2*2. + .2);
            //objCol = tx.yzx*.65;//



            // Last minute edge routine. I've returned the nearest rectangle ID
            // and dimensions from the "raycasting" routine, and the rest 
            // figures itself out.
            float ew = .0125*(1. + t*.1); // Edge width.
            float h = svSc.z;
            float rct = sBoxS(svP, svSc.xy/2., .0);
            float top = max(abs(sp.z + h), abs(rct));
            float side = abs(abs(svP.x) - svSc.x/2.);
            side = max(side, abs(abs(svP.y) - svSc.y/2.));
            float objEdge = min(top, side) - ew;

            // Same for the boxes on top.
            if(min(svSc.x, svSc.y)>.125 && hash21(svGID.yz + .03)<.5){
                rct = sBoxS(svP, (svSc.xy - .16)/2., .0);
                top = max(abs(sp.z + h), abs(rct));
                top = min(top, max(abs(sp.z + h*1.3), abs(rct)));
                side = abs(abs(svP.x) - (svSc.x - .16)/2.);
                side = max(side, abs(abs(svP.y) - (svSc.y - .16)/2.));
                objEdge = min(objEdge, min(top, side) - ew);
            }

            svObjEdge = objEdge;


        }
        else {

            // The dark floor in the background. Hidden behind the pylons, but
            // you still need it.
            vec3 tx = getTex(iChannel2, sp.xy);
            objCol = vec3(.3, .15, 1)/8.*(tx*2. + 1.);
            //objCol = vec3(0);
        }


        // Shadows and ambient self shadowing.
    	float sh = softShadow(sp, lp, sn, 16.);
    	float ao = calcAO(sp, sn); // Ambient occlusion.

	    // Light attenuation, based on the distances above.
	    float atten = 2./(1. + lDist*.125);

    	// Diffuse lighting.
	    float diff = max( dot(sn, ld), 0.);
        diff = pow(diff, 2. + 16.*objCol.x*objCol.x)*2.; // Ramping up the diffuse.

    	// Specular lighting.
	    float spec = pow(max(dot(reflect(ld, sn), rd), 0.), 16.); 

	    // Fresnel term. Good for giving a surface a bit of a reflective glow.
        float fre = pow(clamp(1. - abs(dot(sn, rd))*.5, 0., 1.), 2.);

		// Schlick approximation. I use it to tone down the specular term.
		float Schlick = pow( 1. - max(dot(rd, normalize(rd + ld)), 0.), 5.);
		float freS = mix(.15, 1., Schlick);  //F0 = .2 - Glass... or close enough.  

        /*
        // Cheap specular reflections. Requires loading the "Forest" cube map 
        // into "iChannel3".
        float speR = pow(max(dot(normalize(ld - rd), sn), 0.), 5.);
        vec3 rf = reflect(rd, sn); // Surface reflection.
        vec3 rTx = texture(iChannel3, rf.xzy*vec3(1, -1, -1)).xyz; rTx *= rTx;
        objCol = objCol*.5 + objCol*speR*rTx*4.;
        */

        float svEw = .0125*(1. + t*.1);
        float sf = 4./iResolution.y;
        objCol = mix(objCol, objCol*1.5, 1. - smoothstep(0.,sf, svObjEdge - svEw));
        objCol = mix(objCol, objCol/4., 1. - smoothstep(0., sf, svObjEdge));


        // Combining the above terms to procude the final color.
        col = objCol*(diff*sh + vec3(.2, .4, 1) + vec3(1, .97, .92)*spec*freS*sh*8.);


        // Shading.
        col *= ao*atten;


	}


    // Applying fog: This fog begins at 90% towards the horizon.
    col = mix(col, vec3(1, .5, 2), smoothstep(.25, .9, t/FAR));

    #if COLOR == 1
    // Some gradient coloring.
    col = mix(col.zyx, col.zxy, uv.y + .5);
    #endif

    // Greyscale.
    //col = vec3(1)*dot(col, vec3(.299, .587, .114));


    // Rough gamma correction.
    fragColor = vec4(sqrt(max(col, 0.)), 1);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool auto= true;
    bool tri= false;
    float div= 12.;
    float indice;
    //indice de 0 a 11
    float vel = 0.5;
        if(auto){
    float t;
    if(tri){
    t= abs(mod(iTime * vel*0.5  +0.5,1.)-0.5)*2.;
    }
    else{
    t = fract(iTime * vel);
    }
    indice = floor(mod(t*div,div));
    }
    else{indice=0.;}
    float px = 1./iResolution.x;
    float cantPx = px*(iResolution.x/div);
    vec2 uv = fragCoord.xy/(iResolution.xy);
    //vec2 iuv = floor(fragCoord.xy*div/(iResolution.xy))/div;

    vec3 col = vec3(uv.x);
    col= step(cantPx*(div-indice-1.),1.-col)*step(cantPx*indice,col);
    fragColor = vec4(col,1.0);
}

