/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a plane and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is such an intersection, outputs the value of t, the position
// of the intersection (hitPos) and the normal vector at the intersection
// (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax,
                     out float t, out vec3 hitPos, out vec3 hitNormal )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;

    // We have a hit -- output results.
    t = t0;
    hitPos = ray.o + t0 * ray.d;
    hitNormal = normalize( N );
    return true;
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a plane and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;
    return true;
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a sphere and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is one or two such intersections, outputs the value of the
// smaller t, the position of the intersection (hitPos) and the normal
// vector at the intersection (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax,
                      out float t, out vec3 hitPos, out vec3 hitNormal )
{
    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    vec3 oc = ray.o - sph.center;
    float a = dot(ray.d, ray.d);
    float b = 2.0f * dot(oc, ray.d);
    float c = dot(oc, oc) - sph.radius * sph.radius;
    float discriminant = b * b - 4.0f * a * c;

    if (discriminant < 0.0f)
        return false; // No intersection.

    // Find the nearest positive root of the quadratic equation.
    float sqrt_discriminant = sqrt(discriminant);
    float t0 = (-b - sqrt_discriminant) / (2.0f * a);
    float t1 = (-b + sqrt_discriminant) / (2.0f * a);

    // Choose the smaller positive root as the hit point.
    float nearest_t = min(t0, t1);
    if (nearest_t < tmin || nearest_t > tmax)
        return false; // The hit point is outside the acceptable range.

    // We have a hit - output the results.
    t = nearest_t;
    hitPos = ray.o + nearest_t * ray.d;
    hitNormal = normalize(hitPos - sph.center); // Normal points outward from the sphere center.
    return true;

}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a sphere and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax )
{
    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    vec3 oc = ray.o - sph.center;
    float a = dot(ray.d, ray.d);
    float b = 2.0f * dot(oc, ray.d);
    float c = dot(oc, oc) - sph.radius * sph.radius;
    float discriminant = b * b - 4.0f * a * c;

    if (discriminant < 0.0f)
        return false; // No intersection.

    // Find the nearest positive root of the quadratic equation.
    float sqrt_discriminant = sqrt(discriminant);
    float t0 = (-b - sqrt_discriminant) / (2.0f * a);
    float t1 = (-b + sqrt_discriminant) / (2.0f * a);

    // Choose the smaller positive root as the hit point.
    float nearest_t = min(t0, t1);
    if (nearest_t < tmin || nearest_t > tmax)
        return false; // The hit point is outside the acceptable range.

    // We have a hit.
    return true;

}



/////////////////////////////////////////////////////////////////////////////
// Computes (I_a * k_a) + k_shadow * I_source * [ k_d * (N.L) + k_r * (R.V)^n ].
// Input vectors L, N and V are pointing AWAY from surface point.
// Assume all vectors L, N and V are unit vectors.
/////////////////////////////////////////////////////////////////////////////
vec3 PhongLighting( in vec3 L, in vec3 N, in vec3 V, in bool inShadow,
                    in Material_t mat, in Light_t light )
{
    if ( inShadow ) {
        return light.I_a * mat.k_a;
    }
    else {
        vec3 R = reflect( -L, N );
        float N_dot_L = max( 0.0, dot( N, L ) );
        float R_dot_V = max( 0.0, dot( R, V ) );
        float R_dot_V_pow_n = ( R_dot_V == 0.0 )? 0.0 : pow( R_dot_V, mat.n );

        return light.I_a * mat.k_a +
               light.I_source * (mat.k_d * N_dot_L + mat.k_r * R_dot_V_pow_n);
    }
}



/////////////////////////////////////////////////////////////////////////////
// Casts a ray into the scene and returns color computed at the nearest
// intersection point. The color is the sum of light from all light sources,
// each computed using Phong Lighting Model, with consideration of
// whether the interesection point is being shadowed from the light.
// If there is no interesection, returns the background color, and outputs
// hasHit as false.
// If there is intersection, returns the computed color, and outputs
// hasHit as true, the 3D position of the intersection (hitPos), the
// normal vector at the intersection (hitNormal), and the k_rg value
// of the material of the intersected object.
/////////////////////////////////////////////////////////////////////////////
vec3 CastRay( in Ray_t ray,
              out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg )
{
    // Find whether and where the ray hits some object.
    // Take the nearest hit point.

    bool hasHitSomething = false;
    float nearest_t = DEFAULT_TMAX;   // The ray parameter t at the nearest hit point.
    vec3 nearest_hitPos;              // 3D position of the nearest hit point.
    vec3 nearest_hitNormal;           // Normal vector at the nearest hit point.
    int nearest_hitMatID;             // MaterialID of the object at the nearest hit point.

    float temp_t;
    vec3 temp_hitPos;
    vec3 temp_hitNormal;
    bool temp_hasHit;

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Try interesecting input ray with all the planes and spheres,
    //   and record the front-most (nearest) interesection.
    // * If there is interesection, need to record hasHitSomething,
    //   nearest_t, nearest_hitPos, nearest_hitNormal, nearest_hitMatID.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    // Intersect with planes.
    for (int i = 0; i < NUM_PLANES; i++)
    {
        if (IntersectPlane(Plane[i], ray, DEFAULT_TMIN, nearest_t, temp_t, temp_hitPos, temp_hitNormal))
        {
            hasHitSomething = true;
            if (nearest_t > temp_t) {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Plane[i].materialID;
            }
        }
    }

    // Intersect with spheres.
    for (int i = 0; i < NUM_SPHERES; i++)
    {
        if (IntersectSphere(Sphere[i], ray, DEFAULT_TMIN, nearest_t, temp_t, temp_hitPos, temp_hitNormal))
        {
            hasHitSomething = true;
            if (nearest_t > temp_t) {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Sphere[i].materialID;
            }
        }
    }


    // One of the output results.
    hasHit = hasHitSomething;
    if ( !hasHitSomething ) return BACKGROUND_COLOR;

    vec3 I_local = vec3( 0.0 );  // Result color will be accumulated here.

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Accumulate lighting from each light source on the nearest hit point.
    //   They are all accumulated into I_local.
    // * For each light source, make a shadow ray, and check if the shadow ray
    //   intersects any of the objects (the planes and spheres) between the
    //   nearest hit point and the light source.
    // * Then, call PhongLighting() to compute lighting for this light source.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////


    // Iterate over each light source to compute lighting.
    for (int i = 0; i < NUM_LIGHTS; i++)
    {
        vec3 L = normalize(Light[i].position - nearest_hitPos);
        vec3 V = ray.o - nearest_hitPos;
        bool inShadow = false;

        // Check if the point is in shadow by casting a shadow ray to each light source.
        for (int j = 0; j < NUM_PLANES; j++)
        {
            if (IntersectPlane(Plane[j], Ray_t(nearest_hitPos, L), DEFAULT_TMIN, length(V)))
            {
                inShadow = true;
                break;
            }
        }

        for (int j = 0; j < NUM_SPHERES; j++)
        {
            if (IntersectSphere(Sphere[j], Ray_t(nearest_hitPos, L), DEFAULT_TMIN, length(V)))
            {
                inShadow = true;
                break;
            }
        }

        // Compute the Phong lighting model and accumulate the result.
        I_local += PhongLighting(L, nearest_hitNormal, -ray.d, inShadow, Material[nearest_hitMatID], Light[i]);
    }

    // Populate output results.
    hitPos = nearest_hitPos;
    hitNormal = nearest_hitNormal;
    k_rg = Material[nearest_hitMatID].k_rg;

    return I_local;
}



/////////////////////////////////////////////////////////////////////////////
// Execution of fragment shader starts here.
// 1. Initializes the scene.
// 2. Compute a primary ray for the current pixel (fragment).
// 3. Trace ray into the scene with NUM_ITERATIONS recursion levels.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitScene();

    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0f * fragCoord.xy - iResolution.xy) / iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;

    // Position the camera.
    vec3 cam_pos = vec3( 3.0f * sin(iTime / 3.0f + 5.0f * m.x), 6.0f, 1.0f * cos(iTime / 3.0f + 5.0f * m.x) + 5.0f );
    vec3 cam_lookat = Sphere[0].center;
    vec3 cam_up_vec = vec3( 0.0f, 1.0f, 0.0f );

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize( cam_pos - cam_lookat );
    vec3 cam_x_axis = normalize( cross(cam_up_vec, cam_z_axis) );
    vec3 cam_y_axis = normalize( cross(cam_z_axis, cam_x_axis) );

    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay;
    pRay.o = cam_pos;
    pRay.d = normalize( pixel_pos.x * cam_x_axis  +  pixel_pos.y * cam_y_axis  +  pixel_pos_z * cam_z_axis );

    // Start Ray Tracing.
    // Use iterations to emulate the recursion.

    vec3 I_result = vec3( 0.0 );
    vec3 compounded_k_rg = vec3( 1.0 );
    Ray_t nextRay = pRay;

    for ( int level = 0; level <= NUM_ITERATIONS; level++ )
    {
        bool hasHit;
        vec3 hitPos, hitNormal, k_rg;

        vec3 I_local = CastRay( nextRay, hasHit, hitPos, hitNormal, k_rg );

        I_result += compounded_k_rg * I_local;

        if ( !hasHit ) break;

        compounded_k_rg *= k_rg;

        nextRay = Ray_t( hitPos, normalize( reflect(nextRay.d, hitNormal) ) );
    }

    fragColor = vec4( I_result, 1.0 );
}

#define PI 3.1415926


// From https://www.shadertoy.com/view/XlfGDN
vec3 hsv2rgb(vec3 c)
{
    //vec3 K = vec3(3,2,1)/3.;
    //return c.z * mix(K.xxx, clamp(abs(fract(c.xxx + K)*6. - vec3(3.)) - K.xxx, 0., 1.), c.y);
    // Which can be further optimized to:
    // Thanks to las @ http://www.pouet.net/user.php?who=4548
    return mix(vec3(1.),clamp((abs(fract(c.x+vec3(3,2,1)/3.)*6.-3.)-1.),0.,1.),c.y)*c.z;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    const float size = 0.2;
    // Speed is currently not used.
    //float speed = 0.02
    const float minSpeed = 0.1;
    const float maxSpeed = 0.2;
    // This is the integral of speed with respect to time
    // Integral of minSpeed + (maxSpeed-minSpeed) * (sin(iTime) + 1.0)/2.0
    float distanceOffset = minSpeed*iTime + (maxSpeed-minSpeed) * (cos(iTime) + iTime)/2.0;
    float len = length(uv);
    float len2 = pow(len, 0.1);
    float angle = mod(atan(uv.y, uv.x) / 1.0, PI) / PI;
    // Map to 0 at right, 0.5 at top, 1 at left, 1.5 at bottom
    angle = angle + -(sign(uv.y)-1.0)/2.0;

    const int spokes = 1;
    // Number of spokes are changed by changing the range of angle from 0-2 to 0-#spokes.
    float dFg = abs(mod(len2 + distanceOffset - angle*size*(float(spokes)/2.0), size));
    float dBg = abs(mod(len2 - iTime*0.01 - angle*size*(float(spokes)/2.0), size));

    // Normalize so d is between 0 and 1.
    dFg *= 1./size;
    dBg *= 1./size;
    // Make transition smooth so the mod does not make a harsh edge.
    float dFgSmooth = abs(dFg - 0.5) * 2.;
    float dBgSmooth = abs(dBg - 0.5) * 2.;

    // Used for transitioning between foreground and background rainbows smoothly
    float transition = smoothstep(0.5, 0.9, dFgSmooth);

    const float fgInterlaceAmount = 20.0;
    const float fgInterlaceOffset = 0.02;
    const float bgInterlaceAmount = 150.0;
    const float bgInterlaceOffset = 0.01;
    // 0 in centre of screen and 1 on the left/right edges
    float bgValue = pow(len/aspect, 0.8);
    const float blackSize = 0.05;
    float fgValue = (-pow(1e-3, len/aspect) + 1.0) * 1.2;

    // Glow effect done by making the value greater than 1 on the edges, and decreasing saturation at the edges
    vec3 rainbowFg = hsv2rgb(vec3(angle-len2 + fgInterlaceOffset*floor(mod(dFg*fgInterlaceAmount, 2.)), (1.0-bgValue)*0.7, fgValue));
    vec3 rainbowBg = hsv2rgb(vec3(dBgSmooth/10.+0.77 + bgInterlaceOffset*floor(mod(dBg*bgInterlaceAmount, 2.)), bgValue*0.8, bgValue*0.5));
    vec3 col = mix(rainbowFg, rainbowBg, vec3(1.0-transition));

    const float posterisation = 16.0;
    col = ceil(col * posterisation) / posterisation;

    //col = vec3(fgValue);



    // Output to screen
    fragColor = vec4(col,1.0);
}

#define maxIterations 4

#define PI 3.1415926

float circle(float radius, vec2 position, vec2 uv){

    float thickness = pow(radius, 2.0) * 0.025;
    //float val = 1.0 - smoothstep(thickness, thickness*2.0, abs(length(uv-position) - radius));
    float val = 1.0 - smoothstep(thickness, thickness*2.0, abs(dot(uv-position, uv-position) - pow(radius, 2.)));
    return val;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float speed = 0.2;
    float val = 0.0;
    float baseRadius = 0.5;

    float speedMultiplier = 1.618;
    float sizeMultiplier = 0.618;
    for (int iteration = 0; iteration < maxIterations; iteration++){
        float radius = baseRadius * pow(sizeMultiplier, float(iteration));
        // Loop through all circles in this iteration
        for (float c = 0.0; c < pow(4.0, float(iteration)); c++){ 
            vec2 position = vec2(0.0);
            // Move the circle
            for (float j=0.0; j < float(iteration); j++){
                float n = mod(floor(c/pow(4.0, float(iteration)-j-1.0)), 4.0);
                float angle = n*PI/2.0 + iTime*speed*pow(speedMultiplier, float(j));
                position += baseRadius*pow(sizeMultiplier, j) * vec2(cos(angle), sin(angle));
            }

            val += circle(radius, position, uv);
        }
    }
    val = 1.0 - val;

    // Output to screen
    fragColor = vec4(vec3(val), 1.0);
}

float grid(float size, float width, vec2 uv){
    return min(step(mod(uv.x, size), width) + step(mod(uv.y, size), width), 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
    vec2 st = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.y;
    float pixSize = 1.0 / iResolution.y;
    float pixSizeScaled = pixSize / canvasScale;

    bool isPaused = texelFetch(iChannel1, ivec2(KEY_SPACE, 2), 0).x == 1.0;

    float gridVal = grid(pixSizeScaled, pixSize*2.0,  uv);
    vec3 bgCol = isPaused ? bgColourPaused : vec3(0.0);
    vec3 bg = bgCol + vec3(gridVal * 0.1);

    vec3 cellCol = texture(iChannel0, st * canvasScale).rgb; 
    vec3 col =  mix(bg, cellCol, cellCol.g);

    fragColor = vec4(col,1.0);
}

