float isCellAlive(vec2 st){
    // Allows for cells to loop
    st = mod(st, canvasScale);
    return float(texture(iChannel0, st).g + texture(iChannel0, st).b + texture(iChannel0, st).r > 0.0);
}

int getNeighbours(vec2 st, vec2 pixSize){
    // Get adjacent and diagonal neighbours
    // Account for aspect ratio
    pixSize.x /= iResolution.x/iResolution.y;
    int neighbours = int(isCellAlive(vec2(st.x-pixSize.x, st.y-pixSize.y))
                   + isCellAlive(vec2(st.x, st.y-pixSize.y))
                   + isCellAlive(vec2(st.x+pixSize.x, st.y-pixSize.y))
                   + isCellAlive(vec2(st.x+pixSize.x, st.y))
                   + isCellAlive(vec2(st.x+pixSize.x, st.y+pixSize.y))
                   + isCellAlive(vec2(st.x, st.y+pixSize.y))
                   + isCellAlive(vec2(st.x-pixSize.x, st.y+pixSize.y))
                   + isCellAlive(vec2(st.x-pixSize.x, st.y))
                   );

    return neighbours;

}


int getNeighbours(vec2 st, vec2 pixSize, int radius){
    // Account for aspect ratio
    pixSize.x /= iResolution.x/iResolution.y;
    int neighbours = 0;
    for (int x=-radius; x <= radius; x++){
        for (int y=-radius; y <= radius; y++){
            neighbours += int(isCellAlive(vec2(st.x + pixSize.x*float(x), st.y + pixSize.y*float(y))));
        }
    }
    // Don't include yourself as a neighbour
    neighbours -= int(isCellAlive(st));
    return neighbours;
}


bool maze(vec2 st, vec2 pixSize){
    int neighbours = getNeighbours(st, pixSize, 2);
    return (bool(isCellAlive(st)) && (neighbours<13 && neighbours>0)) || (neighbours<5 && neighbours>0);
}

bool plant(vec2 st, vec2 pixSize){
    int neighbours = getNeighbours(st, pixSize);
    return (bool(isCellAlive(st)) && (neighbours>1)) || neighbours==3 ;
}

bool conway(vec2 st, vec2 pixSize){
    // Alive cell will stay alive if it has 2 or 3 live neighbours.
    // Dead cell will come to life if it has 3 neighbours.
    // All other cells will be dead.
    int neighbours = getNeighbours(st, pixSize);
    return (bool(isCellAlive(st)) && (neighbours==2 || neighbours==3)) || neighbours==3;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.y;
    vec2 pixSize = vec2(1.0) / iResolution.yy;
    vec2 canvasResolution = iResolution.xy * canvasScale;

    vec2 m = iMouse.xy/iResolution.y * canvasScale;
    m = m - mod(m, pixSize);

    bool isPaused = texelFetch(iChannel1, ivec2(KEY_SPACE, 2), 0).x == 1.0;
    bool clearPressed = texelFetch(iChannel1, ivec2(KEY_C, 0), 0).x == 1.0;
    bool shiftPressed = texelFetch(iChannel1, ivec2(KEY_SHIFT, 0), 0).x == 1.0;
    bool ctrlPressed = texelFetch(iChannel1, ivec2(KEY_CTRL, 0), 0).x == 1.0;


    vec3 col = vec3(0.0); 
    // Only render inside of the canvas resolution
    if (!(fragCoord.x > canvasResolution.x || fragCoord.y > canvasResolution.y) && !clearPressed){

        col.g = texture(iChannel0, st).g;

        // is true if we are switching to next generation this frame
        // If generationTime is below 0, it will run as fast as it can.
        bool doNextGeneration = mod(iTime - iTimeDelta, generationTime) >= mod(iTime, generationTime)
                                || generationTime < 0.0;
        doNextGeneration = doNextGeneration && !isPaused;

        if (doNextGeneration){

            bool isAlive = conway(st, pixSize);
            col.g = float(isAlive);
        }

        // Handle drawing
        bool isMouseDown = iMouse.z > 0.0;

        // Will be -1 if erasing
        float eraseMultiplier = (float(shiftPressed) - 0.5) * -2.0;
        float drawAmount = step(length(m-uv+pixSize/2.0), brushSize*pixSize.y) * float(isMouseDown);
        drawAmount *= eraseMultiplier;
        col.g = col.g + drawAmount;
        col = clamp(col , 0.0, 1.0);
    }


    fragColor = vec4(col,1.0);
}

