#define PI 3.14159268

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float angle = 1.;
    float offset_magnitude = 0.6;

    float period = 8.0;
    float radius = 0.1 + (0.4 * (sin(iTime*2.*PI / (period*2.5))+1.0)/2.);
    float max_width = radius/2.;
    float width = max_width * abs(sin(iTime*2.*PI / period));
    float separation_min = (radius - max_width)*sqrt(2.);
    float separation_max = (radius + max_width)*4.;
    float separation = (sin(iTime*2.*PI / period)/2.+0.5)*(separation_max-separation_min) + separation_min;
    //vec2 offset = vec2(cos(angle), sin(angle)) * offset_magnitude;
    float d = length(vec2(mod(uv.x, separation)-separation/2., 
                          mod(uv.y, separation)-separation/2.));
    d = abs(d - radius);
    d = step(width, d);

    vec3 col = vec3(d, d, d);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// A loose adaptation of noobdawn's "Vaporwave Outrun"
// https://www.shadertoy.com/view/sscSWn

const float warp = 0.0;
const float scan = 0.2;
const float aberration = 8.0;

vec4 crt(in vec2 fragCoord, in vec3 color, in vec2 uv) {

    vec2 dc = abs(0.5 - uv);
    dc *= dc;

    uv.x -= 0.5; 
    uv.x *= 1.0 + (dc.y * (0.3 * warp));
    uv.x += 0.5;

    uv.y -= 0.5; 
    uv.y *= 1.0 + (dc.x * (0.4 * warp));
    uv.y += 0.5;

    if (uv.y > 1.0 || uv.x < 0.0 || uv.x > 1.0 || uv.y < 0.0) return vec4(0.0, 0.0, 0.0, 1.0);
    else return vec4(mix(color, vec3(0.0), abs(sin(fragCoord.y) * 0.5 * scan)), 1.0);

}

// https://www.shadertoy.com/view/4dXXDl
vec4 chromoAberration(in vec2 uv) {

    vec2 texel = 1.0 / iResolution.xy;

    vec2 coords = (uv - 0.5) * 2.0;
    float coordDot = dot(coords, coords);

    vec2 uvOffset = aberration * coordDot * coords;
    vec2 uvR = uv - texel.xy * uvOffset;
    vec2 uvB = uv + texel.xy * uvOffset;

    vec4 newColor = vec4(0.0, 0.0, 0.0, 1.0);
    newColor.r = texture(iChannel0, uvR).r;
    newColor.g = texture(iChannel0, uv).g;
    newColor.b = texture(iChannel0, uvB).b;
    return newColor;

}
vec4 gradient(in vec2 uv,in vec4 image){

    vec2 center = (uv - 0.5);
    center = center * 2.0;
    float d = length(center);


    d = -d + 1.1;
    image = image * d;
    return image;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord / iResolution.xy;

    vec4 image = chromoAberration(uv);
    image = crt(fragCoord, image.rgb, uv);
    image = gradient(uv, image);

    fragColor = image;
    //fragColor = texture(iChannel0,uv);

}

