#define PI 3.14159265358979
#define ANIM_SPEED 0.5
#define GRID 32.0
#define RAY_STEP 0.005
#define NEAR_PLANE 1.0
#define FAR_PLANE 512.0 
#define TERRAIN_HEIGHT 0.33

struct Raycast {
    bool hit;
    vec3 position;
};

vec3 background(in vec3 direction, in float mouse) {
    return lerp(vec3(0.8, 0.45, 0.8), vec3(0.2, 0.1, 0.6), abs(direction.y)) * ((mouse + 0.5) * 0.75 + 0.25);
}

Raycast raycastTerrain(in vec3 origin, in vec3 direction) {

    // init raycast result
    Raycast raycast = Raycast(
        false,
        vec3(0.0, 0.0, 0.0)
    );

    // the height of the terrain at the previous ray step
    float prevHeight = 0.0;

    // cast ray
    for (float i = NEAR_PLANE; i <= FAR_PLANE; i++) {

         // get current position along ray
        raycast.position = origin + direction * RAY_STEP * i;

        // sample terrain height from texture
        float height = texture(iChannel0, raycast.position.xz / 16.0).x * TERRAIN_HEIGHT;

        // make valley along x axis flat
        height *= clamp(pow(abs(raycast.position.x - origin.x) * 1.66, 1.5), 0.0, 1.0);

        // if ray is clipping terrain
        if (raycast.position.y < height) {

            // move back along ray one step
            vec3 prevPos = raycast.position - direction * RAY_STEP; 

            // find y offset from the terrain of both points along ray
            float prevOffset = prevPos.y - prevHeight;
            float currOffset = height - raycast.position.y;

            // interpolate between offsets to find terrain surface intersection
            float t = prevOffset / (prevOffset + currOffset);
            raycast.position = prevPos + direction * RAY_STEP * t;

            // mark as hit
            raycast.hit = true;
            return raycast;

        }

        // cache current terrain height
        prevHeight = height;

    }

    return raycast;

}

vec3 terrainColor(in vec3 cameraPosition, in vec3 rayPosition, in vec3 backgroundColor, in float mouse) {

    // grid lines
    float fx = abs(fract(rayPosition.x * GRID + 0.5) - 0.5);
    float fz = abs(fract(rayPosition.z * GRID + 0.5) - 0.5);

    // grid line color
    vec3 gridLineColor = vec3(0.3, 0.2, 0.6);
    // current background color
    vec3 bg = pow(backgroundColor, vec3(3.0, 3.0, 2.0));

    // the distance along the current ray as a 0 -> 1 mask 
    float dist = length(rayPosition.xz - cameraPosition.xz) * 0.125;
    dist = clamp(dist, 0.0, 1.0);

    // the current color
    vec3 color = bg * dist + gridLineColor * (1.0 - dist);

    // basic post-processing
    float sweepingLight = texture(iChannel0, rayPosition.xz / 64.0 + vec2(0.0, -iTime * 0.01)).x * 0.5 + 0.5;
    float lineGlow = abs(rayPosition.z - cameraPosition.z) + 0.25;
    color /= min(sweepingLight, lineGlow);

    // apply grid
    dist = min(fx, fz);

    // reduce line spread
    dist = smoothstep(dist, 0., 0.02);

    // apply grid mask to background
    vec3 result = bg * dist;

    // add grid color with grid mask
    result += color * (1.0 - dist);

    return result;
}

const vec3 SUN_DIR = normalize(vec3(0.0, 0.1, 1.0));
const vec4 SUN_COLOR_TOP = vec4(1.0, 0.0, 0.5, 1.0);
const vec4 SUN_COLOR_BOTTOM = vec4(2.0, 2.0, 0.0, 1.0);

vec4 sun(in vec3 direction, in float animSpeed, in float mouse) {

    mouse = clamp(mouse, -0.5, 0.125);

    vec3 sunDir = SUN_DIR;
    sunDir.y += mouse - 0.1;

    float sunMask = pow(smoothstep(0.95, 0.9, dot(direction, sunDir)), 0.125);

    // generate horizontal cuts
    float cut = 3.0 * sin((direction.y + iTime * animSpeed * 0.125) * 75.0) * (1.0 - sunMask);
    cut += clamp((direction.y - mouse + 0.1) * 14.0 + 1.5, -7.0, 7.0);

    cut = clamp(cut, 0.0, 1.0);

    // interpolate between top and bottom colors and masking cut lines
    float a = abs(direction.y) + mouse;
    vec4 color = lerp(SUN_COLOR_TOP, SUN_COLOR_BOTTOM, a);
    color.a = lerp(color.a * cut, 0.0, sunMask);
    return color * (mouse + 1.0);

}

// generates ray direction at clipspace coordinate with camera settings
vec3 perspective(in vec2 clip, in vec3 target, in vec3 position, in float fov, in vec3 up) {
    vec3 w = normalize(target - position);
    vec3 u = normalize(cross(w, up));
    vec3 v = normalize(cross(u, w));
    return vec3(normalize(clip.x * u + clip.y * v + fov * w));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 clip = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    // mouse input is inverted to ensure that the sun is above the horizon on shader load
    float mouse = clamp((1.0 - (iMouse.y / iResolution.y)) - 0.5, -0.5, 0.5);
    float animSpeed = ANIM_SPEED * mouse;

    float deltaZ = iTime * ANIM_SPEED;
    vec3 cameraPosition = vec3(0.0, 0.1, deltaZ - 1.0);
    vec3 cameraTarget = vec3(0.0, 0.1, deltaZ);

    vec3 raydir = perspective(clip, cameraTarget, cameraPosition, 1.25, vec3(0.0, 1.0, 0.0));

    vec4 color = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 background = background(raydir, mouse);
    Raycast raycast = raycastTerrain(cameraPosition, raydir);
    vec3 terrain = terrainColor(cameraPosition, raycast.position, background, mouse);
    if (raycast.hit) color = vec4(terrain, 1.0);
    else {
        vec4 sun = sun(raydir, animSpeed, mouse);
        color = vec4(background * (1.0 - sun.w) + sun.xyz * sun.w, 1.0);
    }

    fragColor = color;
}

